-- ----------------------------
-- Table structure for books_list
-- ----------------------------
DROP TABLE IF EXISTS `books_list`;
CREATE TABLE `books_list`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '书名',
  `author` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '作者',
  `translator` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '译者',
  `original_title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '原作名',
  `publisher` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '出版社',
  `publication_info` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '出版信息',
  `content_summary` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '内容简介',
  `author_bio` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '作者简介',
  `page_count` int UNSIGNED NULL DEFAULT NULL COMMENT '页数',
  `price` decimal(8, 2) NULL DEFAULT NULL COMMENT '定价',
  `binding` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '装帧',
  `series` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '所属丛书',
  `ISBN` varchar(17) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT 'ISBN',
  `collection_count` int NULL DEFAULT NULL,
  `remaining_stock` int NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `ISBN`(`ISBN` ASC) USING BTREE,
  INDEX `idx_title`(`title` ASC) USING BTREE,
  INDEX `idx_author`(`author`(20) ASC) USING BTREE,
  INDEX `idx_ISBN`(`ISBN` ASC) USING BTREE,
  CONSTRAINT `check_remaining_stock` CHECK (`remaining_stock` <= `collection_count`)
) ENGINE = InnoDB AUTO_INCREMENT = 120 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of books_list
-- ----------------------------
INSERT INTO `books_list` VALUES (1, '解忧杂货店', '[日] 东野圭吾', '李盈春', 'ナミヤ雑貨店の奇蹟', '南海出版公司', '[日] 东野圭吾 / 李盈春 / 南海出版公司 / 2014-5 / 39.50元', '现代人内心流失的东西，这家杂货店能帮你找回—— 僻静的街道旁有一家杂货店，只要写下烦恼投进卷帘门的投信口，第二天就会在店后的牛奶箱里得到回答。 因男友身患绝症，年轻女孩静子在爱情与梦想间徘徊；克郎为了音乐梦想离家漂泊，却在现实中寸步难行；少年浩介面临家庭巨变，挣扎在亲情与未来的迷茫中…… 他们将困惑写成信投进杂货店，随即奇妙的事情竟不断发生。 生命中的一次偶然交会，将如何演绎出截然不同的人生？ 如今回顾写作过程，我发现自己始终在思考一个问题：站在人生的岔路口，人究竟应该怎么做？我希望读者能在掩卷时喃喃自语：我从未读过这样的小说。——东野圭吾', '东野圭吾 日本著名作家。 1985年，《放学后》获第31届江户川乱步奖，开始专职写作； 1999年，《秘密》获第52届日本推理作家协会奖； 2005年出版的《嫌疑人X的献身》史无前例地同时获得第134届直木奖、第6届本格推理小说大奖，以及年度三大推理小说排行榜第1名； 2008年，《流星之绊》获第43届新风奖； 2009年出版的《新参者》获两大推理小说排行榜年度第1名； 2012年，《解忧杂货店》获第7届中央公论文艺奖。 2014年，《祈りの幕が下りる時》（暂译《祈祷落幕时》）获第48届吉川英治文学奖。', 291, 39.50, '精装', '新经典文库·东野圭吾作品', '9787544270878 ', 20, 20);
INSERT INTO `books_list` VALUES (2, '巨人的陨落 : 世纪三部曲', '[英] 肯·福莱特', NULL, 'Fall of Giants', '江苏凤凰文艺出版社', '[英] 肯·福莱特 / 于大卫 / 江苏凤凰文艺出版社 / 2016-5-1 / 129.80元', '在第一次世界大战的硝烟中，每一个迈向死亡的生命都在热烈地生长——威尔士的矿工少年、刚失恋的美国法律系大学生、穷困潦倒的俄国兄弟、富有英俊的英格兰伯爵，以及痴情的德国特工… 从充满灰尘和危险的煤矿到闪闪发光的皇室宫殿，从代表着权力的走廊到爱恨纠缠的卧室，五个家族迥然不同又纠葛不断的命运逐渐揭晓，波澜壮阔地展现了一个我们自认为了解，但从未如此真切感受过的20世纪。', '肯·福莱特 Ken Follett (1949 -)，现象级畅销小说大师。1978年出版了处女作《风暴岛》，并于次年获得爱伦坡奖，声名鹊起，专职写作。2010年，荣登全球作家富豪榜上第5名。2013年，获得爱伦坡终身大师奖。他的小说出版前，都会请历史学家审读书稿，绝不容许出现任何史实错误。有出版人曾指出，肯·福莱特 “没有一个饱受折磨的灵魂”，他愉快地承认了，“大家都在写内心的痛苦，可我总是觉得很开心”。他说：“很多作家只写能取悦他们自己的东西，并模模糊糊地希望这也能取悦别人。但我每写一页都在清醒地思考：读者会怎么想？读者觉得这真的会发生吗？读者关心这些吗？读者想要知道接下来会发生什么吗？我敬佩那些用文字和新奇结构进行文学实验的作家，但我从不这么玩。” 作者官方网站：www.ken-follett.com', 1168, 129.80, '平装', '世纪三部曲', '9787539989891 ', 20, 20);
INSERT INTO `books_list` VALUES (3, '我的前半生', '亦舒', NULL, NULL, '新世界出版社', '亦舒 / 新世界出版社 / 2007-8 / 22.00元', '一个三十几岁的美丽女人子君，在家做全职家庭主妇。却被一个平凡女人夺走丈夫，一段婚姻的失败，让女主角不得不坚强，变得更美丽，有了事业，最终遇见一个更值得爱的男人……', '亦舒，言情文学作家。原名倪亦舒，兄长是香港作家倪匡。亦舒1946年生于上海，祖籍浙江镇海，五岁时定居香港。中学毕业后，曾在《明报》任职记者，及担任电影杂志采访和编辑等。1973年，亦舒赴英国修读酒店食物管理课程，三年后回港，任职富丽华酒店公关部，后进入政府新闻处担任新闻官，也曾当过电视台编剧。现为专业作家，并已移居加拿大。', 271, 22.00, '平装', '亦舒新经典', '9787802283930 ', 19, 19);
INSERT INTO `books_list` VALUES (4, '房思琪的初恋乐园', '林奕含', NULL, NULL, '磨铁 ·北京联合出版公司', '林奕含 / 北京联合出版公司 / 2018-1 / 45.00元', '令人心碎却无能为力的真实故事。 向死而生的文学绝唱 打动万千读者的年度华语小说。 李银河 戴锦华 骆以军 张悦然 冯唐 詹宏志 蒋方舟 史航 等多位学者作家社会名人郑重推荐。 痛苦的际遇是如此难以分享，好险这个世界还有文学。 我下楼拿作文给李老师改。他掏出来，我被逼到涂在墙上。老师说了九个字：“不行的话，嘴巴可以吧。”我说了五个字：“不行，我不会。”他就塞进来。那感觉像溺水。可以说话之后，我对老师说：“对不起。”有一种功课做不好的感觉。 小小的房思琪住在金碧辉煌的人生里，她的脸和她可以想象的将来一样漂亮。补习班语文名师李国华是同一栋高级住宅的邻居。崇拜文学的小房思琪同样崇拜饱读诗书的李老师。 有一天李老师说，你的程度这么好，不如每个礼拜交一篇作文给我吧，不收你周点费。思琪听话地下楼了。老师在家里等她，桌上没有纸笔。 思琪的初恋是李老师。因为李老师把...(展开全部)', '林奕含（1991——2017），台湾作家。出生于台南，曾居台北。梦想是一面写小说，一面像大江健三郎所说的：从书呆子变成读书人，再从读书人变成知识分子。', 272, 45.00, '平装', NULL, '9787559614636 ', 13, 13);
INSERT INTO `books_list` VALUES (5, '追风筝的人', '[美] 卡勒德·胡赛尼', NULL, 'The Kite Runner', '上海人民出版社', '[美] 卡勒德·胡赛尼 / 李继宏 / 上海人民出版社 / 2006-5 / 29.00元', '12岁的阿富汗富家少爷阿米尔与仆人哈桑情同手足。然而，在一场风筝比赛后，发生了一件悲惨不堪的事，阿米尔为自己的懦弱感到自责和痛苦，逼走了哈桑，不久，自己也跟随父亲逃往美国。 成年后的阿米尔始终无法原谅自己当年对哈桑的背叛。为了赎罪，阿米尔再度踏上暌违二十多年的故乡，希望能为不幸的好友尽最后一点心力，却发现一个惊天谎言，儿时的噩梦再度重演，阿米尔该如何抉择？ 故事如此残忍而又美丽，作者以温暖细腻的笔法勾勒人性的本质与救赎，读来令人荡气回肠。', '卡勒德·胡赛尼（Khaled Hosseini），1965年生于阿富汗喀布尔市，后随父亲迁往美国。胡赛尼毕业于加州大学圣地亚哥医学系，现居加州。“立志拂去蒙在阿富汗普通民众面孔的尘灰，将背后灵魂的悸动展示给世人。”著有小说《追风筝的人》(The Kite Runner，2003）、《灿烂千阳》(A Thousand Splendid Suns，2007)、《群山回唱》（And the Mountains Echoed,2013）。作品全球销量超过4000万册。2006年，因其作品巨大的国际影响力，胡赛尼获得联合国人道主义奖，并受邀担任联合国难民署亲善大使。', 362, 29.00, '平装', '卡勒德·胡赛尼作品', '9787208061644 ', 11, 11);
INSERT INTO `books_list` VALUES (6, '百年孤独', '[哥伦比亚] 加西亚·马尔克斯', NULL, 'Cien a?os de soledad', '南海出版公司', '[哥伦比亚] 加西亚·马尔克斯 / 范晔 / 南海出版公司 / 2011-6 / 39.50元', '《百年孤独》是魔幻现实主义文学的代表作，描写了布恩迪亚家族七代人的传奇故事，以及加勒比海沿岸小镇马孔多的百年兴衰，反映了拉丁美洲一个世纪以来风云变幻的历史。作品融入神话传说、民间故事、宗教典故等神秘因素，巧妙地糅合了现实与虚幻，展现出一个瑰丽的想象世界，成为20世纪最重要的经典文学巨著之一。1982年加西亚?马尔克斯获得诺贝尔文学奖，奠定世界级文学大师的地位，很大程度上乃是凭借《百年孤独》的巨大影响。', '加西亚?马尔克斯（Gabriel García Márquez）1927年出生于哥伦比亚马格达莱纳海滨小镇阿拉卡塔卡。童年与外祖父母一起生活。1936年随父母迁居苏克雷。1947年考入波哥大国立大学。1948年因内战辍学，进入报界。五十年代开始发表文学作品。六十年代初移居墨西哥。1967年出版《百年孤独》。1982年获诺贝尔文学奖。', 360, 39.50, '精装', '新经典文库:加西亚·马尔克斯作品', '9787544253994 ', 12, 12);
INSERT INTO `books_list` VALUES (7, '雪落香杉树', '', '熊裕', 'Snow Falling on Cedars', '全本书店|作家出版社', '[美] 戴维·伽特森 / 熊裕 / 全本书店|作家出版社 / 2017-6 / 52.00元', '编辑推荐: 1995福克纳奖得主 占据《纽约时报》畅销榜首数月 美国书商协会年度之选 被译成30余种文字，全球畅销500万册 当代美国文学史上罕见的超级畅销获奖经典 全美大中学文学课堂读本，被列入大学文学课程考试大纲 拥有哈罗德?布鲁姆和Cliff Notes等众多导读本 恢弘精妙，融爱情、战争、历史、悬疑和成长小说于一体 多次被改编成电影和舞台剧，同名电影由伊桑霍克和工藤夕贵主演 内容简介： 一位默默无闻的美国中学教师，用十年时间写下令自己一夜成名的故事，虚构了西雅图附近一座香杉积翠，草莓遍地的海岛，一处被海水封闭的小型人世，交织上演着爱情、战争、种族歧视和悬疑谋杀案…… 日裔少女初枝的生活中先后到来两个男人：邻居男孩伊什梅尔是她的初恋，他们气息相通；暗恋她并*终成为她丈夫的日本同学宫本天道，他们血脉相连。珍珠港事件爆发，日美矛盾空前激化，日裔居民...(展开全部)', '戴维·伽特森，1956年生，美国小说家、诗人。《雪落香杉树》是他的成名作，他花了十年时间在教书之余写成，出版后获福克纳奖和美国书商协会年奖，并畅销近五百万册。 译者 熊裕，资深文学编辑与译者，译有《借东西的小人》、《多莱尔的希腊神话书》等多部作品。', 370, 52.00, '精装', NULL, '9787506387873 ', 10, 10);
INSERT INTO `books_list` VALUES (8, '霍乱时期的爱情', '[哥伦比亚] 加西亚·马尔克斯', NULL, 'El amor en los tiempos del cólera', '南海出版公司', '[哥伦比亚] 加西亚·马尔克斯 / 杨玲 / 南海出版公司 / 2012-9-1 / 39.50元', '★马尔克斯唯一正式授权，首次完整翻译 ★《霍乱时期的爱情》是我最好的作品，是我发自内心的创作。——加西亚?马尔克斯 ★这部光芒闪耀、令人心碎的作品是人类有史以来最伟大的爱情小说。——《纽约时报》 《霍乱时期的爱情》是加西亚?马尔克斯获得诺贝尔文学奖之后完成的第一部小说。讲述了一段跨越半个多世纪的爱情史诗，穷尽了所有爱情的可能性：忠贞的、隐秘的、粗暴的、羞怯的、柏拉图式的、放荡的、转瞬即逝的、生死相依的……再现了时光的无情流逝，被誉为“人类有史以来最伟大的爱情小说”，是20世纪最重要的经典文学巨著之一。', '加西亚?马尔克斯（Gabriel García Márquez） 1927年出生于哥伦比亚马格达莱纳海滨小镇阿拉卡塔卡。童年与外祖父母一起生活。1936年随父母迁居苏克雷。1947年考入波哥大国立大学。1948年因内战辍学，进入报界。五十年代开始出版文学作品。六十年代初移居墨西哥。1967年《百年孤独》问世。1982年获诺贝尔文学奖。1985年出版《霍乱时期的爱情》。 加西亚?马尔克斯豆瓣小站：http://site.douban.com/marquez/', 401, 39.50, '精装', '新经典文库:加西亚·马尔克斯作品', '9787544258975 ', 10, 10);
INSERT INTO `books_list` VALUES (9, '新名字的故事 : 那不勒斯四部曲-02', '[意] 埃莱娜·费兰特', '陈英', NULL, '人民文学出版社', '[意] 埃莱娜·费兰特 / 陈英 / 人民文学出版社 / 2017-4 / 59.00元', '只有你身为女人 才会知道这些丑陋的秘密 两个女人 50年的友谊和战争 如何成为那个更强大的她，又不被她战胜 那不勒斯四部曲NO.2 《我的天才女友》后续， 意大利最神秘的作家费兰特 探索青年时代的激情、困惑、挣扎、背叛和失去 全球畅销近千万册 被翻译成40多种语言 希拉 里·克林顿、 乔纳森·弗兰岑、詹姆斯·伍德、詹姆斯·弗兰科都是她的粉丝 ------------------------- 《新名字的故事》是埃莱娜·费兰特的“那不勒斯四部曲”的第二部，描述了埃莱娜和莉拉的青年时代。在她们的人生以最快的速度急遽分化的那些年里，她们共同体验了爱、失去、困惑、挣扎、嫉妒和隐蔽的破坏。 莉拉在结婚当天就发现婚姻根本不是她想象的那样，她的初夜几乎是一场强奸。她带着一种强大的破坏欲介入了斯特凡诺的家族生意，似乎成为了她和埃莱娜小时候都想成为的那种女人。久未有身...(展开全部)', '埃莱娜·费兰特，目前意大利最受欢迎也最神秘的作家。埃莱娜·费兰特是一个笔名，其真实身份至今是谜。 埃莱娜·费兰特1992年发表第一部长篇小说《讨厌的爱》，很快引起关注，1995年就被意大利导演马里奥·马尔托内拍摄为同名影片；此后她相继出版小说《被抛弃的日子》(2002) 、《迷失的女儿》（2006）、《夜晚的沙滩》（2007）和散文、访谈集《不确定的碎片》（2003）。 2011年至2014年，埃莱娜·费兰特以每年一本的频率出版《我的天才女友》《新名字的故事》《离开的，留下的》和《失踪的孩子》这四部情节相关的小说，被称为“那不勒斯四部曲”。它们以史诗般的体例，描述了两个在那不勒斯穷困社区出生的女孩持续半个世纪的友谊。 “那不勒斯四部曲”也在世界范围内掀起了“费兰特热”，千万读者为书中对女性友谊极度真实、尖锐、毫不粉饰的描述所打动。虽然作者从未公开其性...(展开全部)', 492, 59.00, '平装', '那不勒斯四部曲', '9787020125265 ', 2, 2);
INSERT INTO `books_list` VALUES (10, '鱼王', '[俄] 维克托·阿斯塔菲耶夫', '夏仲翼 等', 'Царь-рыба', '理想国 | 广西师范大学出版社', '[俄] 维克托·阿斯塔菲耶夫 / 夏仲翼 等 / 理想国 | 广西师范大学出版社 / 2017-4 / 78.00元', '长篇小说《鱼王》是阿斯塔菲耶夫最具个性的一部代表作，俄罗斯当代文学的经典。全书由十三个内容相对独立的“叙事短篇小说”组成，全部围绕着人与自然的关系，深入细致地描绘了充满神秘诱惑的西伯利亚以及生活在那里的人们，他们关于生活的沉思。荒凉苦寒的自然环境，同时又是大自然尽显壮美广袤富饶之地，人类的足迹在其间虽如雪泥鸿爪，却又带着生命不息的尊严。 这些篇章在思想内容和艺术形式上显示了独特的风格，淡化情节，描写细腻鲜活，“集长篇小说、中篇小说、抒情散文、道德议论为一体” (王小波)，从不同的角度和方式显露出连贯的内容和意象，犹如不经意穿成的一串珍珠，每一颗都以其自身的美丽折射出耀眼的光芒。 《鱼王》写作时为十三篇，其中《没心没肺》一篇在1975年首次出版时未能收入，此后的汉语译本皆因袭此删节版。本次由俄语翻译家张冰将该篇翻译补入，首次呈现这部杰作 的全貌，并收...(展开全部)', '维克托·彼得洛维奇·阿斯塔菲耶夫(1924-2001)，20世纪下半叶俄罗斯文学代表人物之一。生于西伯利亚克拉斯诺亚尔斯克一个农民家庭，作品多取材于自身经历，被认为继承了屠格涅夫、普里什文和巴乌斯托夫斯基的自然文学传统 ， 并创造出将小说、道德思辨和抒情散文熔于一炉的独特风格。阿斯塔菲耶夫善于描写自然生活， 洞察人与大自然的关系， 开掘人的心灵， 返照时代的剪影。正如评论家马卡洛夫所说：“ 阿斯塔菲耶夫的作品是关于我们生活的沉思， 是关于人在大地上和社会上的使命以及人的道德标准的沉思， 是关于俄罗斯民族性格的沉思。” 代表作《鱼王》《树号》《牧童和牧女》《最后一次鞠躬》等。', 644, 78.00, '精装', '理想国·阿斯塔菲耶夫作品', '9787549586547 ', 18, 18);
INSERT INTO `books_list` VALUES (11, '月亮和六便士', '[英] 威廉·萨默塞特·毛姆', NULL, 'The Moon and Sixpence', '上海译文出版社', '[英] 毛姆 / 傅惟慈 / 上海译文出版社 / 2006-8 / 15.00元', '一个英国证券交易所的经纪人，本已有牢靠的职业和地位、美满的家庭，但却迷恋上绘画，像“被魔鬼附了体”，突然弃家出走，到巴黎去追求绘画的理想。他的行径没有人能够理解。他在异国不仅肉体受着贫穷和饥饿煎熬，而且为了寻找表现手法，精神亦在忍受痛苦折磨。经过一番离奇的遭遇后，主人公最后离开文明世界，远遁到与世隔绝的塔希提岛上。他终于找到灵魂的宁静和适合自己艺术气质的氛围。他同一个土著女子同居，创作出一幅又一幅使后世震惊的杰作。在他染上麻风病双目失明之前，曾在自己住房四壁画了一幅表现伊甸园的伟大作品。但在逝世之前，他却命令土著女子在他死后把这幅画作付之一炬。通过这样一个一心追求艺术、不通人性世故的怪才，毛姆探索了艺术的产生与本质、个性与天才的关系、艺术家与社会的矛盾等等引人深思的问题。同时这本书也引发了人们对摆脱世俗束缚逃离世俗社会寻找心灵家园这一话题的思考，而关...(展开全部)', '威廉·萨默赛特·毛姆（William Somerset Maugham）于1874年1月25日出生在巴黎。父亲是律师，当时在英国驻法使馆供职。小毛姆不满十岁，父母就先后去世，他被送回英国由伯父抚养。毛姆进坎特伯雷皇家公学之后，由于身材矮小，且严重口吃，经常受到大孩子的欺凌和折磨，有时还遭到冬烘学究的无端羞辱。孤寂凄清的童年生活，在他稚嫩的心灵上投下了痛苦的阴影，养成他孤僻、敏感、内向的性格。幼年的经历对他的世界观和文学创作产生了深刻的影响。 1892年初，他去德国海德堡大学学习了一年。在那儿，他接触到德国哲学史家昆诺·费希尔的哲学思想和以易卜生为代表的新戏剧潮流。同年返回英国，在伦敦一家会计师事务所当了六个星期的练习生，随后即进伦敦圣托马斯医学院学医。为期五年的习医生涯，不仅使他有机会了解到底层人民的生活状况，而且使他学会用解剖刀一样冷峻、犀利的目光...(展开全部)', 275, 15.00, '平装', '译文名著文库', '9787532739547 ', 6, 6);
INSERT INTO `books_list` VALUES (12, '世界的凛冬 : 世纪三部曲 2', '[英] 肯·福莱特', '陈杰', 'Winter of the World', '江苏凤凰文艺出版社', '[英] 肯·福莱特 / 陈杰 / 江苏凤凰文艺出版社 / 2017-3-1 / 132.00（全三册）', '我亲眼目睹，每一个迈向死亡的生命都在热烈地生长。 全球读者平均3个通宵读完的超级小说《巨人的陨落》的续篇！ 火遍全球的20世纪人类史诗“世纪三部曲”第二部。 美国《纽约时报》小说畅销榜第1名！ 加拿大书商协会畅销书排行榜第1名！ 德国小说类畅销书排行榜年度第1名！ 意大利小说类畅销排行榜年度第1名！ 西班牙《真理报》畅销书榜第1名！ 丹麦Bog & idé畅销书榜第1名！ 整个20世纪的吉光片羽，都被肯?福莱特写进了这部伟大的小说里。 一切都始于那个裂变中的大时代——希特勒上台，爱德华八世退位，原子弹在广岛和长崎爆炸……世界剧烈改变，我该怎么办？ 这正是他们的困惑——一群处于人生黄金时代的少男少女，来自德国、美国、英国、苏俄和威尔士的五大家族，他们父辈的命运因一战而彻底改变。如今，世界再次破碎，甚至更加暴烈和残酷。然而，这就是他们的时代！在时间的永恒...(展开全部)', '肯?福莱特（Ken Follett，1949－） 现象级畅销小说大师，爱伦坡终身大师奖得主。 20部小说被译成33国语言，累计总销量超1.5亿册。 在欧美出版界，肯?福莱特这个名字就是畅销的保证。 1978年，出版了处女作《风暴岛》，并于次年获得爱伦坡奖，声名鹊起，专职写作。2013年，获得爱伦坡终身大师奖。 他的小说出版前，都会请历史学家审读书稿，绝不容许出现任何史实错误。 他说：“很多作家只写能取悦他们自己的东西，并模模糊糊地希望这也能取悦别人。但我每写一页都在清醒地思考：读者会怎么想？读者觉得这真的会发生吗？读者关心这些吗？读者想要知道接下来会发生什么吗？我敬佩那些用文字和新奇结构进行文学实验的作家，但我从不这么玩。”', 1168, 132.00, '平装', '世纪三部曲', '9787539999074 ', 14, 14);
INSERT INTO `books_list` VALUES (13, '活着', '余华', NULL, NULL, '南海出版公司', '余华 / 南海出版公司 / 1998-5 / 12.00元', '地主少爷福贵嗜赌成性，终于赌光了家业一贫如洗，穷困之中的福贵因为母亲生病前去求医，没想到半路上被国民党部队抓了壮丁，后被解放军所俘虏，回到家乡他才知道母亲已经去世，妻子家珍含辛茹苦带大了一双儿女，但女儿不幸变成了聋哑人，儿子机灵活泼…… 然而，真正的悲剧从此才开始渐次上演，每读一页，都让我们止不住泪湿双眼，因为生命里难得的温情将被一次次死亡撕扯得粉碎，只剩得老了的福贵伴随着一头老牛在阳光下回忆。', '余华，浙江海盐人，1960年出生于浙江杭州，后来随父母迁居海盐县。中学毕业后，因父母是医生，余华曾当过牙医，五年后弃医从文，进入县文化馆和嘉兴文联，从此开始文学创作生涯。曾在北京鲁迅文学院与北师大中文系合办的研究生班深造，1984年开始发表小说。余华是中国先锋派小说的代表人物，与叶兆言、苏童等人齐名。作品有短篇小说集《十八岁出门远行》、《世事如烟》，长篇小说《在细雨中呼喊》、《战栗》及《兄弟》。', 195, 12.00, '平装', '余华作品', '9787544210966 ', 14, 14);
INSERT INTO `books_list` VALUES (14, '1984', '[英] 乔治·奥威尔', NULL, 'Nineteen Eighty-Four', '北京十月文艺出版社', '[英] 乔治·奥威尔 / 刘绍铭 / 北京十月文艺出版社 / 2010-4-1 / 28.00', '★村上春树以《1Q84》向本书致敬 ★著名学者刘绍铭经典译本内地首次出版 ★62种文字风靡110个国家，全球销量超过5000万册 ★《时代周刊》“最好的100本英语小说” ★兰登书屋“100本20世纪最佳英语小说” ★入选英美中学生必读书书目 1936年以来，我所写的每 一部严肃作品，都是直接或间接地反对极权主义，支持我所理解的民主社会主义。 ——乔治?奥威尔（《我为何写作》） 《1984》是一部杰出的政治寓言小说，也是一部幻想小说。作品刻画了人类在极权主义社会的生存状态，有若一个永不褪色的警示标签，警醒世人提防这种预想中的黑暗成为现实。历经几十年，其生命力益显强大，被誉为20世纪影响最为深远的文学经典之一。', '乔治?奥威尔（1903-1950）， 英国著名作家。主要作品有《动物农场》、《1984》等。 1903年出生于印度，1907年举家迁回英国，后因经济原因无力深造，被迫远走缅甸，参加帝国警察部队。终因厌倦殖民行径、痴迷写作而辞去公职，辗转回到欧洲，流亡伦敦、巴黎等地，一边深刻体验下层民众生活，一遍从事文学创作，有多部作品出版。1936年西班牙内战爆发，参加左翼组织，因重伤返回英国，但最终树立社会主义信念。1939年，二战爆发，积极参加反纳粹的活动。由于西班牙内战与二战的苦痛经历，对战争与和平、极权与民主、社会关怀与人类理想进行了深刻的思考。 1945年，著名的政治寓言小说《动物农场》出版。1949年，《1984...(展开全部)', 304, 28.00, '精装', '新经典文库·桂冠文丛', '9787530210291 ', 4, 4);
INSERT INTO `books_list` VALUES (15, '杀死一只知更鸟', '[美] 哈珀·李', '高红梅', 'To Kill a Mocking Bird', '译林出版社', '[美] 哈珀·李 / 高红梅 / 译林出版社 / 2012-9 / 32.00元', '成长总是个让人烦恼的命题。成长有时会很缓慢，如小溪般唱着叮咚的歌曲趟过，有时却如此突如其来，如暴雨般劈头盖脸……三个孩子因为小镇上的几桩冤案经历了猝不及防的成长——痛苦与迷惑，悲伤与愤怒，也有温情与感动。这是爱与真知的成长经典。 《杀死一只知更鸟》获1961年普利策奖。 美国图书馆借阅率最高的书，英国青少年最喜爱的小说之一。 美国中学推荐课外读物。 由小说改编的电影获第25届奥斯卡三项大奖。 美国电影协会评选的“100名银幕英雄与恶人”中，派克主演的芬奇律师名列英雄第一位。 作为史上最受喜爱的小说之一，《杀死一只知更鸟》已获得显赫声誉。它赢得过普利策奖，被翻译成四十多种语言，在世界范围内售出超过三千万册，并曾被拍成，备受欢迎的电影。 ——哈珀?柯林斯出版集团 斯塔尔六月对北卡罗来纳州律师协会发表演说时，自比为《杀死一只知更鸟》中勇敢的南方白人律师阿蒂...(展开全部)', '哈珀?李（1926— ） 生于美国阿拉巴马州， 曾被授予普利策小说奖及其他众多文学奖项。她与杜鲁门?卡坡蒂是幼年的邻居和一生的朋友。《杀死一只知更鸟》据说是以卡坡蒂为原型之一创作的，现已成为公认的美国文学经典。此后，她一直隐居在家乡亚拉巴马的小镇上 ，拒绝各种采访，过着平静的生活。有人问她为什么不在盛名之下接着写作，她回答：“有过这样一次，还有什么可写的？”', 346, 32.00, '精装', '译林纪念版', '9787544722766 ', 19, 19);
INSERT INTO `books_list` VALUES (16, '围城', '钱钟书', NULL, NULL, '人民文学出版社', '钱锺书 / 人民文学出版社 / 1991-2 / 19.00', '《围城》是钱钟书所著的长篇小说。第一版于1947年由上海晨光出版公司出版。1949年之后，由于政治等方面的原因，本书长期无法在中国大陆和台湾重印，仅在香港出现过盗印本。1980年由作者重新修订之后，在中国大陆地区由人民文学出版社刊印。此后作者又曾小幅修改过几次。《围城》 自从出版以来，就受到许多人的推崇。由于1949年后长期无法重印，这本书逐渐淡出人们的视野。1960年代，旅美汉学家夏志清在《中国现代小说史》(A History of Modern Chinese Fiction)中对本书作出很高的评价，这才重新引起人们对它的关注。人们对它的评价一般集中在两方面，幽默的语言和对生活深刻的观察。从1990年代开始，也有人提出对本书的不同看法，认为这是一部被“拔高”的小说，并不是一部出色的作品。很多人认为这是一部幽默作品。除了各具特色的人物语言之外，作者...(展开全部)', '钱钟书(1910－1998)，字哲良，默存，号槐聚，中国江苏无锡人，中国近代著名作家、 文学研究家。毕业于清华大学外文系，获文学学士，赴上海，到光华大学任教。后考取第三届(1935年)庚子赔款公费留学资格，名列榜首，留学英国牛津大学 埃克塞特学院。大学毕业后任教于多所高校。新中国成立后被评为一级教授。晚年就职于中国社会科学院，任副院长。其夫人杨绛也是著名作家，育有一女钱媛(1937年-1997年)。曾为《毛泽东选集》英文版翻译小组成员。1998年逝世，享年88岁。', 359, 19.00, '平装', NULL, '9787020024759 ', 3, 3);
INSERT INTO `books_list` VALUES (17, '斯通纳', '[美] 约翰·威廉斯', '杨向荣', 'Stoner', '世纪文景/上海人民出版社', '[美] 约翰·威廉斯 / 杨向荣 / 世纪文景/上海人民出版社 / 2016-1 / 39.00元', '《斯通纳》讲述了生命中最重要的部分：爱，认同，怜悯，志业，傲骨，信任与死亡。 一个勇者有过的失败不失意的人生：即使不能拥有完美的生活，所幸追求过完整的自我。 美国，密苏里州。来自偏远农场的农家子弟、19岁的威廉·斯通纳进入州立密苏里大学学习农学。自一堂选修文学课为起点， 他的一生就此悄然改变。未来的斯通纳成为了一名大学老师，结婚、生子、教学、退休、衰老、死亡。在他生命的尽头，或许他可以坦然面对这个问题：你的一生，还要期望别的什么吗？ 一部蕴含着真诚、激情与紧凑力量的小说，探究了历史洪流所忽视的人性之间的冲突、溃败与幸存，重新唤起思考每个个体独特存在的意义。作者约翰·威廉斯向世人展示了凡人中的勇者在如何生活。 ---------------------------------------- 威廉斯的写作显得澄澈、节制却准确。就像被打磨光滑的橡木上反射出...(展开全部)', '约翰·威廉斯（John Edward Williams, 1922—1994），美国作家，诗人，学者。辍过学，当过电台播音，从过军。退役后入大学就读并获博士学位。 于1945年-1985年间在母校丹佛大学任教，教授大学英语及创意写作。曾编辑出版学术文集《文艺复兴时期的英语诗歌》，也创作过两本诗集。 一生只写了四部小说：《惟有黑夜》（1948）、《屠夫十字镇》（1960）、《斯通纳》（1965）、《奥古斯都》（1973，本书当年获得美国国家图书奖）。 对约翰·威廉斯的写作，英国作家朱利安·巴恩斯评论：“当我称之为‘绝佳’，我的意思是它们（《屠夫十字镇》《斯通纳》）已经超越了各自类型范围内的评价标准。”', 336, 39.00, '精装', '约翰·威廉斯作品', '9787208130500 ', 18, 18);
INSERT INTO `books_list` VALUES (18, '囚鸟', '[美] 库尔特·冯内古特', NULL, 'Jailbird', '百花洲文艺出版社', '[美] 库尔特·冯内古特 / 董乐山 / 百花洲文艺出版社 / 2017-6 / 38.00元', '我们都是受困于时代的“囚鸟”——既渴望逃离，又踟蹰不前。 联邦最低限度安保措施成人改造所里，颓唐的小老头儿瓦尔特?斯代布克正在等待领他出狱的狱卒。 在他过去的人生中，他曾是斯拉夫移民的儿子，哈佛大学毕业生，前共产党党员，前联邦政府官员，“水门事件”的涉案者…… 不久他还将获得一个新的身份，神秘的……', '库尔特?冯内古特（Kurt Vonnegut，1922—2007），美国黑色幽默作家，美国黑色幽默文学的代表人物之一。代表作有《囚鸟》《五号屠宰场》《没有国家的人》。他的作品以喜剧形式表现悲剧内容，在灾难、荒诞、绝望面前发出笑声。这种“黑色幽默”风格始终是冯内古特小说创作的重要特质。2007年4月11日，于曼哈顿因病逝世。 译者简介 董乐山，1946年毕业于上海圣约翰大学英国文学系，长期从事新闻、翻译、教学和研究工作。中国作家协会会员、中国笔会中心会员、中国美国文学研究会常务理事、中国翻译工作者协会理事、中国三S研究会副会长。主要译作有《第三帝国的兴亡》《西行漫记》《美国梦》《囚鸟》《一九八四》等，并编有《美国社会知识辞典》，以及论述翻译的著作《译余废墨》。他创作的小说《傅正业教授的颠倒世界》曾译载于美国著名文艺刊物《巴黎评论》。', 296, 38.00, '平装', NULL, '9787550022348 ', 1, 1);
INSERT INTO `books_list` VALUES (19, '外婆的道歉信', '[瑞典] 弗雷德里克·巴克曼', '孟汇一', 'Min mormor h?lsar och s?ger f?rl?t', '天津人民出版社', '[瑞典] 弗雷德里克·巴克曼 / 孟汇一 / 天津人民出版社 / 2017-5 / 42.00元', '2016年瑞典年度作家 美国亚马逊作家排名No.1 版权售出40国 遍布欧美几乎每家书店 上市10个月 全球总销量突破150万册 出版后持续霸占《纽约时报》畅销榜50周至今 内容： 七岁的爱莎有个古怪又疯狂的外婆，会埋伏在雪堆里吓唬邻居，半夜从医院溜出来带着爱莎翻进动物园，在阳台上用彩弹枪射击推销员，基本上想干什么就干什么。这个四处惹麻烦的外婆却是爱莎唯一的朋友，也是她心中的超级英雄。不管什么情况下，外婆都会站在爱莎这一边，为了她去跟全世界拼命。 就算是超级英雄，也有失去超能力的一天。外婆不幸得了癌症去世，留给爱莎一项艰巨的任务——将外婆的道歉信送给她得罪过的九个邻居。收信人包括一只爱吃糖果的大狗，一个总在不停洗手的怪物，一个管东管西的烦人精和一个酗酒的心理医生。这一趟送信之旅让爱莎渐渐发现：外婆和邻居们的故事，比她听过的所有童话都更加精彩。 这是一...(展开全部)', '[瑞典]弗雷德里克·巴克曼 1981年出生于瑞典赫尔辛堡，以撰写博客和专栏起家。某天他把自己和老爸在宜家吵架的过程写在博客上，妙趣横生的对话让他瞬间爆红，吸引众多网友到巴克曼的博客讨论和吐槽自己的家人。结果他灵机一动，以此创作出《一个叫欧维的男人决定去死》，开启了自己的畅销书作家之路。 《外婆的道歉信》是巴克曼的第二本小说，在世界各国都取得了巨大成功，巴克曼因此成为2016年瑞典年度作家。他对日常生活极具洞察力，作品风趣活泼，笔下的人物生动真实，让读者笑中带泪，感悟人生。 如今他和妻子及两个孩子居住在瑞典斯德哥尔摩。', 352, 42.00, '平装', NULL, '9787201116693 ', 9, 9);
INSERT INTO `books_list` VALUES (20, '平凡的世界（全三部）', '路遥', NULL, NULL, '人民文学出版社', '路遥 / 人民文学出版社 / 2005-1 / 64.00元', '《平凡的世界》是一部现实主义小说，也是一部小说形式的家族史。作者浓缩了中国西北农村的历史变迁过程，在小说中全景式地表现了中国当代城乡的社会生活。在近十年的广阔背景下，通过复杂的矛盾纠葛，刻划社会各阶层众多普通人的形象。劳动与爱情，挫折与追求，痛苦与欢乐，日常生活与巨大社会冲突，纷繁地交织在一起，深刻地展示了普通人在大时代历史进程中所走过的艰难曲折的道路。', '路遥（1949年—1992年），陕西清涧人。1973年入延安大学中文系学习。1976年大学毕业后，先后在《陕西文艺》和《延河》杂志做编辑工作。1978年开始发表作品。著有长篇小说《平凡的世界》，中篇小说《惊心动魄的一幕》《人生》等。曾任陕西省作家协会副主席。', 1631, 64.00, '平装', '茅盾文学奖获奖作品全集', '9787020049295 ', 3, 3);
INSERT INTO `books_list` VALUES (21, '二手时间', '[白俄] S·A·阿列克谢耶维奇', '吕宁思', 'Время секонд хэнд', '中信出版社', '[白俄] S.A.阿列克谢耶维奇 / 吕宁思 / 中信出版社 / 2016-1 / 55.00元', '2015年度诺贝尔文学奖得主阿列克谢耶维奇最具分量的作品 发表后荣获德国书业和平奖（2013）、法国美第契散文奖（2013）、俄罗斯「大书奖」读者票选最佳文学作品（2014）、波兰卡普钦斯基报告文学奖（2015） 本书是白俄罗斯著名作家阿列克谢耶维奇最新作品，通过口述采访的形式，展现身处关键历史时刻的普通人的生活。本书讲述了苏联解体后，1991年到2012年二十年间的痛苦的社会转型中，俄罗斯普通人的生活，为梦想破碎付出的代价。在书中，从学者到清洁工，每个人都在重新寻找生活的意义。他们的真实讲述同时从宏观和微观上呈现出一个重大的时代，一个社会的变动，为这一段影响深远的历史赋予了人性的面孔。苏联解体已逾二十年，俄罗斯人重新发现了世界，世界也重新认识了俄罗斯。新一代已经成长起来，他们的梦想已不再关乎梦想，不再像90年代他们的父辈，关心信仰。二十年来，人们...(展开全部)', 'S.A.阿列克谢耶维奇（Святлана Аляксандра?на Алекс?ев?ч），记者，散文作家。出生于白俄罗斯，父亲是白俄罗斯人，母亲是乌克兰人。已出版的着作有：《战争的非女性面孔》《最后一个证人》《切尔诺贝利的回忆》等。曾获得德国莱比锡图书奖、法国国家电台“世界见证人”奖、美国国家书评人协会奖、德国书业和平奖、法国美第契文学奖、波兰卡普钦斯基国际报告文学奖等奖项。2015年，因为她的“复调书写，成为我们时代里苦难与勇气的纪念”而荣获诺贝尔文学奖。', 592, 55.00, '平装', NULL, '9787508658346 ', 5, 5);
INSERT INTO `books_list` VALUES (22, '活着为了讲述', '[哥伦比亚] 加西亚·马尔克斯', NULL, 'Vivir para contarla', '南海出版公司', '[哥伦比亚] 加西亚·马尔克斯 / 李静 / 南海出版公司 / 2016-4-1 / 55.00元', '一生中总会遇到一本书，奠定你人生的基调。 ★《百年孤独》作者马尔克斯唯一自传 生活不是我们活过的日子，而是我们记住的日子，我们为了讲述而在记忆中重现的日子。 ★就算走到绝境，失去耐心，也要永远保有幽默感，热爱生活，这是我们人生最大的财富。 给所有在梦想的路上走得磕磕绊绊的人 ★ “我年轻过，落魄过，幸福过，我对生活一往情深。”——加西亚?马尔克斯 诺奖得主、纯文学畅销奇迹创造者马尔克斯的成长故事：充满魔幻色彩的真实人生。 ★ 正是回忆中的这些细节、生命中的这些人将马尔克斯带到了《百年孤独》的门口； 能够解开《百年孤独》密码的，只有《活着为了讲述》；马尔克斯小说中的人物都能在本书中找到原型。 ★ 首版首次赠限量别册（含地图、人名索引、加博书单、作品年表）+ 雕刻时光咖啡券 ======================= “要么写作，要么死去” 他是举世...(展开全部)', '加西亚?马尔克斯 （Gabriel García Márquez，1927-2014） 1927年出生于哥伦比亚马格达莱纳海滨小镇阿拉卡塔卡。童年与外祖父母一起生活。 1936年随父母迁居苏克雷。1947年考入波哥大国立大学。1948年进入报界。五十年代开始出版文学作品。1967年《百年孤独》问世。1982年获诺贝尔文学奖。2002年出版自传《活着为了讲述》。2014年4月17日于墨西哥病逝。', 450, 55.00, '精装', '新经典文库:加西亚·马尔克斯作品', '9787544281584 ', 16, 16);
INSERT INTO `books_list` VALUES (23, '飘', '[美] 玛格丽特·米切尔', '李美华', 'Gone with the Wind', '译林出版社', '[美国] 玛格丽特·米切尔 / 李美华 / 译林出版社 / 2000-9 / 40.00元', '小说中的故事发生在1861年美国南北战争前夕。生活在南方的少女郝思嘉从小深受南方文化传统的熏陶，可在她的血液里却流淌着野性的叛逆因素。随着战火的蔓廷和生活环境的恶化，郝思嘉的叛逆个性越来越丰满，越鲜明，在一系列的的挫折中她改造了自我，改变了个人甚至整个家族的命运，成为时代时势造就的新女性的形象。 作品在描绘人物生活与爱情的同时，勾勒出南北双方在政治，经济，文化各个层次的异同，具有浓厚的史诗风格，堪称美国历史转折时期的真实写照，同时也成为历久不衰的爱情经典。', '米切尔（Margaret Mitchell, 1900-1949）美国女作家。出生于美国南部佐治亚州亚特兰大市。父亲是个律师，曾任亚特兰大历史协会主席。米切尔曾就读于华盛顿神学院、马萨诸塞州的史密斯学院。其后，她曾担任地方报纸《亚特兰大报》的记者。1925年与约翰·马尔什结婚，婚后辞去报职，潜心写作。 米切尔一生中只发表了《飘》这部长篇巨著。她从1926年开始着力创作《飘》，10 年之后，作品问世，一出版就引起了强烈的反响。 由于家庭的熏陶，米切尔对美国历史，特别是南北战争时期美国南方的历史产生了浓厚的兴趣。她在家乡听闻了大量有关内战和战后重建时期的种种轶事和传闻，接触并阅读了大量有关内战的书籍。她自幼在南部城市亚特兰大成长，耳濡目染了美国南方的风土人情，这里的自然环境和社会环境成了米切尔文思纵横驰骋的背景和创作的源泉。', 1235, 40.00, '精装', NULL, '9787806570920 ', 4, 4);
INSERT INTO `books_list` VALUES (24, '灯塔', '[法] 克里斯多夫·夏布特', '吕俊君', 'Tout Seul', '北京联合出版公司·后浪出版公司', '[法] 克里斯多夫·夏布特 / 吕俊君 / 北京联合出版公司·后浪出版公司 / 2016-2 / 128.00元', '若未知的世界让人心生恐惧， 我们是自我封闭，还是勇敢走出去？ 欧洲著名漫画诗人夏布特 最敏感忧伤的“慢镜头图像小说” 讲述人类心底无法承受的孤独和渴望拥有的自由 ................... ※编辑推荐※ 这是一部探索现代人内在情感的图像小说，引导读者直面人生中的种种精神困境。故事讲述了一个被父母之爱所“禁闭”、只能靠想象力生活的畸形怪人，渐渐在词典的语言碎片和无名水手的帮助下，找到了真正属于他自己的人生。作品以独特的“慢镜头”叙事方法勾勒出人性中的美丑、孤独、胆怯、恐惧……具有极深的哲学意义和艺术价值。 希望《灯塔》能为深处浮躁社会的我们，寻得一盏明净的灯光，照亮那被遗忘的自信、善心和勇气。也许，这里会成为你下一个独自出航的港口，夜色庇护，此处独明。 ★ 《灯塔》是2009年安古兰国际漫画节入选作品，目前已有德语、日语、西班牙语、波兰语等...(展开全部)', '克里斯多夫·夏布特（Christophe Chabouté）, 1967年生于阿尔萨斯地区，先后就读于安古兰美术学院和斯特拉斯堡美术学院。他因擅长改编文学名著，描绘复杂细腻的人性，被业界誉为“漫画家中的诗人”。作品曾获1999年安古兰国际漫画节“阿尔法艺术大奖”、2002年安古兰国际漫画节“评委特别奖”、2006年RTL广播电台“漫画大奖”、2008年科涅克侦探作品文化节“最佳漫画奖”等众多国际奖项。', 376, 128.00, '软精装', '后浪漫', '9787550268388 ', 13, 13);
INSERT INTO `books_list` VALUES (25, '我的天才女友 : 那不勒斯四部曲-01', '[意] 埃莱娜·费兰特', '陈英', 'L\'amica geniale', '人民文学出版社', '[意] 埃莱娜·费兰特 / 陈英 / 人民文学出版社 / 2017-1 / 42.00元', '只有你身为女人才会知道这些丑陋的秘密 两个女人，50年的友谊和战争 过去五年，几乎所有欧美读者都在谈论她、 全球畅销百万册 被翻译成40种语言 《金融时报》2015年度女性 2016《时代》周刊“世界最有影响力的100人” 希拉里·克林顿、 乔纳森·弗兰岑、詹姆斯·伍德、詹姆斯·弗兰科都是她的粉丝 ---------------------- 《我的天才女友》是埃莱娜·费兰特“那不勒斯四部曲”的第一部，讲述了两个女主人公莉拉和埃莱娜的少女时代。故事一开始，已经功成名就的埃莱娜接到莉拉儿子里诺的电话，说他母亲彻底消失了。埃莱娜想起莉拉对自己命运的预言，于是她写下她们一生的故事…… 莉拉和埃莱娜一起成长于那不勒斯一个破败的社区，从小形影不离，彼此信赖，但又都视对方为自己隐秘的镜子，暗暗角力。 莉拉聪明，漂亮。她可以毫不畏惧地和欺凌自己的男生对质，也可以去...(展开全部)', '埃莱娜·费兰特，目前意大利最受欢迎也最神秘的作家。埃莱娜·费兰特是一个笔名，其真实身份至今是谜。 埃莱娜·费兰特1992年发表第一部长篇小说《讨厌的爱》，很快引起关注，1995年就被意大利导演马里奥·马尔托内拍摄为同名影片；此后她相继出版小说《被抛弃的日子》(2002) 、《迷失的女儿》（2006）、《夜晚的沙滩》（2007）和散文、访谈集《不确定的碎片》（2003）。 2011年至2014年，埃莱娜·费兰特以每年一本的频率出版《我的天才女友》《新名字的故事》《离开的，留下的》和《失踪的孩子》这四部情节相关的小说，被称为“那不勒斯四部曲”。它们以史诗般的体例，描述了两个在那不勒斯穷困社区出生的女孩持续半个世纪的友谊。 “那不勒斯四部曲”也在世界范围内掀起了“费兰特热”，千万读者为书中对女性友谊极度真实、尖锐、毫不粉饰的描述所打动。虽然作者从未公开其性...(展开全部)', 330, 42.00, '平装', '那不勒斯四部曲', '9787020120130 ', 10, 10);
INSERT INTO `books_list` VALUES (26, '局外人', '[法] 阿尔贝·加缪', NULL, 'L\'Etranger', '上海译文出版社', '[法] 阿尔贝·加缪 / 柳鸣九 / 上海译文出版社 / 2010-9 / 22.00元', '《局外人》是加缪小说的成名作和代表作之一，堪称20世纪整个西方文坛最具有划时代意义最著名小说之一，“局外人”也由此成为整个西方文学-哲学中最经典的人物形象和最重要的关键词之一。', '阿尔贝·加缪（Albert Camus，1913—1960），法国声名卓著的小说家、散文家和剧作家，“存在主义”文学的大师。1957年因“热情而冷静地阐明了当代向人类良知提出的种种问题”而获诺贝尔文学奖，是有史以来最年轻的诺奖获奖作家之一。加缪在他的小说、戏剧、随笔和论著中深刻地揭示出人在异己的世界中的孤独、个人与自身的日益异化，以及罪恶和死亡的不可避免，但他在揭示出世界的荒诞的同时却并不绝望和颓丧，他主张要在荒诞中奋起反抗，在绝望中坚持真理和正义，他为世人指出了一条基督教和马克思主义以外的自由人道主义道路。他直面惨淡人生的勇气，他“知其不可而为之”的大无畏精神使他在第二次世界大战之后不仅在法国，而且在欧洲并最终在全世界成为他那一代人的代言人和下一代人的精神导师。', 128, 22.00, '精装', '译文经典（精装本）', '9787532751471 ', 14, 14);
INSERT INTO `books_list` VALUES (27, '步履不停', '[日] 是枝裕和', '郑有杰', '歩いても、歩いても', '北京联合出版公司', '[日] 是枝裕和 / 郑有杰 / 北京联合出版公司 / 2017-5 / 36.80元', '★电影大师是枝裕和亲自撰写，经典代表作原著小说 ★拿遍各大国际影展重要奖项，全球影迷热泪推荐！ ★绝无冷场的故事，比电影更幽微的人性暗涌；一茶一饭的淡香，触动每个家庭深潜的秘密 ★一些随口说出的约定还来不及实现，一些胸口小小的悸动被淡淡地忽略。人生就是不断地失去，时间终将带我们找到谅解的出口。 =================== 人生路上，步履不停，总有那么一点来不及。已经是夏末的季节了，院子里的紫薇花开得璀璨，黄斑蝶在山坡上翩然飞舞着。横山良多与妻儿一同返回位于湘南海边的老家。久违的横山一家人，每年只有在大哥纯平的忌日才会团聚。在沁凉的麦茶、红透的西瓜、母亲绝手的好料理炸玉米天妇罗，加上外卖的顶级寿司和鳗鱼饭之间，闲散地度过看似平凡的一天。然而他们细细分享品尝着的，却是欢笑背后隐隐的哀伤，小心翼翼的对话又忍不住的争执中，不经意触动的回忆以及深藏...(展开全部)', '是枝裕和，日本当代电影大师，被誉为“小津安二郎的接班人”。1962年生人，早稻田大学文艺学科毕业后，加入TV Man Union拍摄纪录片，关注的题材多具社会关怀及人文主义色彩。1995年执导由宫本辉同名小说改编的《幻之光》，获得威尼斯电影节新导演及最佳摄影奖。2004年以真实事件改编电影《无人知晓》，14岁的柳乐优弥凭此片荣获戛纳电影节最佳男主角奖。 电影代表作有《步履不停》《幻之光》《无人知晓》《如父如子》《海街日记》等，著有随笔集《有如走路的速度》等，小说《步履不停》《比海更深》《奇迹》《如父如子》等。', 236, 36.80, '平装', NULL, '9787550297685 ', 17, 17);
INSERT INTO `books_list` VALUES (28, '红楼梦', '', NULL, NULL, '人民文学出版社', '[清] 曹雪芹 著、高鹗 续 / 人民文学出版社 / 1996-12 / 59.70元', '《红楼梦》是一部百科全书式的长篇小说。以宝黛爱情悲剧为主线，以四大家族的荣辱兴衰为背景，描绘出18世纪中国封建社会的方方面面，以及封建专制下新兴资本主义民主思想的萌动。结构宏大、情节委婉、细节精致，人物形象栩栩如生，声口毕现，堪称中国古代小说中的经 典。 由红楼梦研究所校注、人民文学出版社出版的《红楼梦》以庚辰（1760）本《脂砚斋重评石头记》为底本，以甲戌（1754）本、已卯（1759）本、蒙古王府本、戚蓼生序本、舒元炜序本、郑振铎藏本、红楼梦稿本、列宁格勒藏本（俄藏本）、程甲本、程乙本等众多版本为参校本，是一个博采众长、非常适合大众阅读的本子；同时，对底本的重要修改，皆出校记，读者可因以了解《红楼梦》的不同版本状况。 红学所的校注本已印行二十五年，其间1994年曾做过一次修订，又十几年过去，2008年推出修订第三版，体现了新的校注成果和科研成果。...(展开全部)', '曹雪芹,（？-1763，一作1764）清小说家。名霑，字梦阮，号雪芹、芹圃、芹溪。为满洲正白旗“包衣”人。自曾祖起，三代任江宁织造，其祖曹寅尤为康熙帝所信用。雍正初年，在统计阶级内部政治斗争牵连下，雪芹家受到重大打击，其父免职，产业被抄，遂随家迁居北京。他早年经历了一段封建大官僚地主家庭的繁华生活，后因家道衰落，趋于艰困。晚期居北京西郊，贫病而卒，年未及五十。性情高傲，嗜酒健谈。具有深厚的文化修养和卓越的艺术才能。他生活在我国已有资本主义生产萌芽的封建末世，在其后期又有机会接触到下层人民，因而对当时社会阶级斗争和思想斗争有较具体的感受，看到了统治阶级的腐朽凶残和内部的分崩离析。曾以十年时间，从事《石头记》（即《红楼梦》）的创作。书中通过一个贵族官僚大家庭的盛衰历史的描写，塑造了许多典型人物形象，对当时社会的黑暗腐败，进行了深刻的解剖和批判，并热情地歌...(展开全部)', 1606, 59.70, '平装', '中国古典文学读本丛书', '9787020002207 ', 5, 5);
INSERT INTO `books_list` VALUES (29, '我们仨', '杨绛', NULL, NULL, '生活·读书·新知三联书店', '杨绛 / 生活·读书·新知三联书店 / 2003-7 / 18.80元', '《我们仨》是钱钟书夫人杨绛撰写的家庭生活回忆录。1998年，钱钟书逝世，而他和杨绛唯一的女儿钱瑗已于此前（1997年）先他们而去。在人生的伴侣离去四年后，杨绛在92岁高龄的时候用心记述了他们这个特殊家庭63年的风风雨雨、点点滴滴，结成回忆录《我们仨》。 这本书分为两部分。第一部分中，作者以其一贯的慧心、独特的笔法，用梦境的形式讲述了最后几年中一家三口相依为命的情感体验。第二部分，以平实感人的文字记录了自1935年伉俪二人赴英国留学并在牛津喜得爱女，直至1998年丈夫逝世，63年间这个家庭鲜为人知的坎坷历程。', '杨绛（1911- ），本名杨季康，祖籍江苏无锡，生于北京。作家、评论家、翻译家、学者。1932年毕业于苏州东吴大学。1935-1938年留学英法，回国后曾在上海震旦女子文理学院、清华大学任教。1949年后，在中国社会科学院文学研究所、外国文学研究所工作。主要作品有剧本《称心如意》《弄假成真》，长篇小说《洗澡》，散文《干校六记》，随笔集《将饮茶》，译作《堂吉诃德》《吉尔·布拉斯》《小癞子》《斐多》等。', 165, 18.80, '平装', NULL, '9787108018809 ', 10, 10);
INSERT INTO `books_list` VALUES (30, '沉默的大多数 : 王小波杂文随笔全编', '王小波', NULL, NULL, '中国青年出版社', '王小波 / 中国青年出版社 / 1997-10 / 27.00元', '这本杂文随笔集包括思想文化方面的文章，涉及知识分子的处境及思考，社会道德伦理，文化论争，国学与新儒家，民族主义等问题；包括从日常生活中发掘出来的各种真知灼见，涉及科学与邪道，女权主义等；包括对社会科学研究的评论，涉及性问题，生育问题，同性恋问题，社会研究的伦理问题和方法问题等；包括创作谈和文论，如写作的动机，作者的师承，作者对小说艺术的看法，作者对文体格调的看法，对影视的看法等；包括少量的书评，其中既有对文学经典的评论，也有对当代作家作品的一些看法；最后，还包括一些域外生活的杂感以及对某些社会现象的评点。', '', 555, 27.00, '平装', '王小波作品系列', '9787500627098 ', 17, 17);
INSERT INTO `books_list` VALUES (31, '看见', '柴静', NULL, NULL, '广西师范大学出版社', '柴静 / 广西师范大学出版社 / 2013-1-1 / 39.80元', '《看见》是知名记者和主持人柴静讲述央视十年历程的自传性作品，既是柴静个人的成长告白书，某种程度上亦可视作中国社会十年变迁的备忘录。十年前她被选择成为国家电视台新闻主播，却因毫无经验而遭遇挫败，非典时期成为现场记者后，现实生活犬牙交错的切肤之感，让她一点一滴脱离外在与自我的束缚，对生活与人性有了更为宽广与深厚的理解。十年之间，非典、汶川地震、两会报道、北京奥运……在每个重大事件现场，几乎都能发现柴静的身影，而如华南虎照、征地等刚性的调查报道她也多有制作。在书中，她记录下淹没在宏大叙事中的动人细节，为时代留下私人的注脚。一如既往，柴静看见并记录下新闻中给她留下强烈生命印象的个人，每个人都深嵌在世界之中，没有人可以只是一个旁观者，他人经受的，我必经受。书中记录下的人与事，是他们的生活，也是你和我的生活。 《看见》中，我没有刻意选择标志性事件，也没有描绘历史...(展开全部)', '柴静 山西临汾人，1976年出生。 曾在湖南文艺广播电台主持“夜色温柔”，在湖南卫视主持“新青年”。 2001年进入中央电视台，先后在“时空连线”“新闻调查”“24小时”“面对面”等栏目担任主持人与记者。 现为央视一套专题节目《看见》主持人。', 424, 39.80, '平装', NULL, '9787549529322 ', 13, 13);
INSERT INTO `books_list` VALUES (32, '送你一颗子弹', '刘瑜', NULL, NULL, '上海三联书店', '刘瑜 / 上海三联书店 / 2010-1 / 25.00元', '这本书里记录的是作者2005—2009年左右(尤其是2006—2007年)生活里的点点滴滴。在这本书里，被“审视”的东西杂七杂八，有街上的疯老头，有同宿舍的室友，有爱情、电影和书，大到制度，小到老鼠。由于我写这些东西的时候，出发点并不是写一本书，所以不同文章往往风格迥异，长短不一，质量不均，随着社会形势、荷尔蒙周期以及我逃避生活的力度而起伏。', 'Drunk Piano，生于1975年12月。毕业于中国人民大学，政治学博士，现执教于剑桥大学。小说、随笔、政治评论散见于《芙蓉》、《希望》、《南风窗》、《外滩画刊》。曾以DrunkPiano网名在某海外网站，连载其主要小说作品。', 339, 25.00, '平装', '刘瑜作品系列', '9787542631664 ', 14, 14);
INSERT INTO `books_list` VALUES (33, '目送', '龙应台', NULL, NULL, '生活·读书·新知三联书店', '龙应台 / 生活·读书·新知三联书店 / 2009-10 / 39.00元', '目送共由七十四篇散文组成，是为一本极具亲情、感人至深的文集。由父亲的逝世、母亲的苍老、儿子的离开、朋友的牵挂、兄弟的携手共行，写出失败和脆弱、失落和放手，写出缠绵不舍和绝然的虚无。正如作者所说：“我慢慢地、慢慢地了解到，所谓父女母子一场，只不过意味着，你和他的缘分就是今生今世不断地在目送他的背影渐行渐远。你站在小路的这一端，看着他逐渐消失在小路转弯的地方，而且，他用背影默默地告诉你，不用追。”', '龙应台 台湾著名文化人及公共知识份子，台湾地区著名作家，作品针砭时事，鞭辟入里。在欧洲、大陆、台湾三个文化圈中，龙应台的文章成为一个罕见的档案，作品《野火集》等具有很大的影响。 她秉著客观批判的精神，持续而正中要害的不断写出她的社会观察，龙应台无所畏惧，温柔纤细，深情动人：其《孩子你慢慢来》与《目送》均是以温柔笔触描写亲子间的亲密互动，同时身为父母与子女双重身份的她逐渐明了人世中亲情的牵绊，不仅是当下的感动，亦是渐行渐远的必经路程，龙应台一反批判犀利的笔调，描写诸多生活中有情细节，在在反映出其细腻情感，读起来温馨有味，情意盎然。', 281, 39.00, '平装', '龙应台作品精选', '9787108032911 ', 9, 9);
INSERT INTO `books_list` VALUES (34, '江城', '[美] 彼得·海斯勒', '李雪顺', 'River Town', '上海译文出版社', '[美] 彼得·海斯勒 / 李雪顺 / 上海译文出版社 / 2012-1 / 36.00元', '1996年8月底一个温热而清朗的夜晚，我从重庆出发，乘慢船，顺江而下来到涪陵。 涪陵没有铁路，历来是四川省的贫困地区，公路非常糟糕。去哪里你都得坐船，但多半你哪里也不会去。在随后的两年，这座城市就是我的家。 在这里，我有时是一个旁观者，有时又置身于当地的生活之中，这种亲疏结合的观察构成了我在四川停留两年的部分生活。 2001年，也就是这本书在美国出版的时候，一条通往重庆的高速公路通车了，一条铁路也正在修建之中，基本上再也没有人坐船去涪陵了。这座城市正在飞速发展着，在过去的二十年，那样一种转型变化的感觉——接二连三、冷酷无情、势不可挡——正是中国的本质特征。很难相信，这个国家曾经完全是另外一种模样，是19世纪西方人眼中“永远停滞的民族”。 2003年，三峡大坝一期完工后，不断上涨的江水将陆续淹没那些江畔之城，这多少令我有些伤感。而对于大多数中国人来说，...(展开全部)', '彼得·海斯勒(Peter Hessler)，中文名何伟，曾任《纽约客》驻北京记者，以及《国家地理》杂志等媒体的撰稿人。 他成长于美国密苏里州的哥伦比亚市，在普林斯顿主修英文和写作，并取得牛津大学英语文学硕士学位。海斯勒曾自助旅游欧洲三十国，毕业后更从布拉格出发，由水陆两路横越俄国、中国到泰国，跑完半个地球，也由此开启了他的旅游文学写作之路。 海斯勒散见于各大杂志的旅游文学作品，数度获得美国最佳旅游写作奖。他的中国纪实三部曲中，《江城》一经推出即获得“奇里雅玛环太平洋图书奖”，《甲骨文》则荣获《时代周刊》年度最佳亚洲图书等殊荣。海斯勒本人亦被《华尔街日报》赞为“关注现代中国的最具思想性的西方作家之一”。', 446, 36.00, '平装', '译文纪实', '9787532756728 ', 4, 4);
INSERT INTO `books_list` VALUES (35, '浮生六记', '沈复', NULL, NULL, '人民文学出版社', '（清）沈复 / 人民文学出版社 / 1999/1 / 5.70元', '这是一部自传体文学的作品，原书六卷，已逸其二，现仅存四卷（有所谓“足本”者，后二记系伪作。书中记叙了作者夫妇间平凡的家居生活，坎坷际遇，和各地浪游闻见。文辞朴素，情感真挚，前人曾有“幽芳凄三角，读之心醉”的评语。本书文字不长，但向为文学爱好者和研究者所重视，影响广泛。', '沈复（1763~？），字三白，号梅逸，江苏苏州人。年轻时秉承父业，以游幕经商为生，后偕妻离家别居，妻子客死扬州。46岁时有感于“苏东坡云‘事如春梦了无痕’，苟不记之笔墨，未免有辜彼苍之厚”，乃作《浮生六记》。', 80, 5.70, '平装', NULL, '9787020028313 ', 13, 13);
INSERT INTO `books_list` VALUES (36, '这些人，那些事', '吴念真', NULL, NULL, '译林出版社', '吴念真 / 译林出版社 / 2011-9 / 28.00元', '吴念真累积多年、珍藏心底的体会与感动。 他写的每个故事，都蕴藏了我们无法预知的生命能量与心灵启发。 跟他一起回望人生种种，您将学会包容、豁达与感恩…… 本书是吴念真导演经历过人生的风风雨雨和最大低潮后，所完成的生命记事。 他用文字写下心底最挂念的家人、日夜惦记的家乡、一辈子搏真情的朋友，以及台湾各个角落里最真实的感动。这些人和事，透过他真情挚意的笔，如此跃然的活在你我眼前，笑泪交织的同时，也无可取代的成为烙印在你我心底、这一个时代的美好缩影…… 特别收录 吴念真近年唯一小说创作《遗书》，写下对胞弟离开人间的真情告白 特别邀请 作家雷骧绘制插画，看两位大师以图文激荡出精采火花 生命里某些当时充满怨怼的曲折，在后来好像都成了一种能量和养分……这些人、那些事在经过时间的筛滤之后，几乎都只剩下笑与泪与感动和温暖。——《这些人，那些事》', '吴念真 全方位的创意人、电影人、广告人、剧场人。本名吴文钦。1952年出生于台北县。1973年开始从事小说创作，曾连续三年获得联合报小说奖。1981年起，陆续写了《恋恋风尘》《老莫的第二个春天》《悲情城市》等75部电影剧本，曾获五次金马奖最佳剧本奖、两次亚太影展最佳编剧奖。主持TVBS“台湾念真情”节目三年，舞台剧代表作有《人间条件》系列等。', 203, 28.00, '平装', '吴念真作品', '9787544717731 ', 13, 13);
INSERT INTO `books_list` VALUES (37, '时间的果', '黎戈', NULL, NULL, '北京联合出版公司', '黎戈 / 北京联合出版公司 / 2017-3 / 39.80', '《时间的果》是作家黎戈的最新随笔精选集。本书分为“叶舟”和“根岸”两个部分，“叶舟”收录了作者的文学艺术评论：帕蒂斯密斯的致青春、永井荷风的慢走慢爱、爱因斯坦的血肉爱情、奈保尔的无寄……这一部分作者分享了自己关于文学和艺术方面的独特体验与深切感悟。到了“根岸”，作者将笔头瞄准日常生活：一幅眼镜、一支铅笔、云的名字、一口锅……这些最平淡的日常，在黎戈的笔下总显出别样的玲珑趣意。“叶舟”是旁观他者，“根岸”多是对自我的体察和拔节。', '黎戈，70后作家，原名许天乐，南京人。嗜好阅读，勤于动笔，文字有雅致且考究的趣味，风格自我、恬淡。作品发表于《人民文学》《今天》《鲤》等刊物，著有书籍《一切因你而值得》《私语书》《因自由而美丽》《静默有时，倾诉有时》', 240, 39.80, '平装', NULL, '9787550294370 ', 5, 5);
INSERT INTO `books_list` VALUES (38, '民主的细节 : 美国当代政治观察随笔', '刘瑜', NULL, NULL, '上海三联书店', '刘瑜 / 上海三联书店 / 2009-6 / 25.00', '这本书是作者过去几年给一些期刊报纸写的专栏文章结集，其中主要是给《南方人物周刊》的文章。全书中以讲故事的形式，把“美国的民主”这样一个概念性的东西拆解成点点滴滴的事件、政策和人物去描述。', '刘瑜，哥伦比亚大学政治学博士，哈佛大学博士后，剑桥大学讲师。现任教于清华大学政治系，担任副教授。', 293, 25.00, '平装', '刘瑜作品系列', '9787542629586 ', 7, 7);
INSERT INTO `books_list` VALUES (39, '人间草木', '汪曾祺', NULL, NULL, '江苏文艺出版社', '汪曾祺 / 江苏文艺出版社 / 2005-01 / 20.00元', '《人间草木》(汪曾祺著)是汪曾祺优秀的散文集。当代文坛上,能同时在小说和散文两块田地里经营,且自成一家的并不多,汪曾祺先生算是其中的一个。汪曾祺先生是公认的文体家,不仅能写一手优秀的小说,还能写一手漂亮的散文。', '', 315, 20.00, '平装', '大家散文文存', '9787539921969 ', 19, 19);
INSERT INTO `books_list` VALUES (40, '吃鲷鱼让我打嗝', '[美] 杰西·艾森伯格', '吴文忠', 'Bream Gives me Hiccups', '人民文学出版社', '[美] 杰西·艾森伯格 / 吴文忠 / 人民文学出版社 / 2017-4 / 49.00元', '演员×剧作家×小说家、导演×制片×编剧 杰西?艾森伯格 首部短篇小说集、同名美剧拍摄中 《吃鲷鱼让我打嗝》是杰西?艾森伯格创作的首部短篇小说集。全书以“吃鲷鱼让我打嗝”系列开篇，后承“家人”、“历史”、“室友偷走了我的拉面” 系列、“约会”等八个部分。故事背景既有当代美国的各种实景，也有庞贝古城和“电话之父”贝尔的电话空间。既采用常规的短篇小说体，也大胆使用书信体、短信体、邮件体等多种形式。 作品中，人物个性和虚构场景融为一体，柔情和潜藏在滑稽幽默之后的悲情相互交织，极致诠释了艾森伯格式喜剧，探索了现代世界的疯狂与光怪陆离。 书中漫画由法国获奖插画师让?朱利安创作， “为巴黎祈福”是他的作品。 《今日美国》：杰西?艾森伯格在文坛的前途就跟他在影坛一样，不可限量。 《泰晤士报》：你还没听过杰西?艾森伯格这位有才华的年轻人？那快了！ 《周六夜现场》最年轻...(展开全部)', '杰西?艾森伯格（Jesse Eisenberg，1983— ） 美国演员、剧作家和幽默小说家。 十六岁开始创作剧本，出版《亚松森》《修正主义者》《战利品》和《我们所有人的一隅》四部作品，前三部皆自编自演登陆剧院。长期为文学杂志《麦克斯韦尼》《纽约客》供稿。作品展现不俗的文学格调，是颇受赞誉的文学界新秀。 2011年，因饰演《社交网络》中Facebook创始人马克?扎克伯格一角，获金球奖和奥斯卡金像奖最佳男演员双料提名。此外，主演《惊天魔盗团》《爱在罗马》《咖啡公社》等电影，并携手安妮?海瑟薇为《里约大冒险》配音。', 244, 49.00, '精装', NULL, '9787020120963 ', 11, 11);
INSERT INTO `books_list` VALUES (41, '撒哈拉的故事', '三毛', NULL, NULL, '皇冠出版社', '三毛 / 皇冠出版社 / 1976 / 160 TWD', '三毛作品中最膾炙人口的《撒哈拉的故事》﹐ 由１２篇精采動人的散文結合而成﹐ 其中＜沙漠中的飯店＞﹐ 是三毛適應荒涼單調的沙漠生活後﹐ 重新拾筆的第一篇文字﹐ 自此之後﹐三毛便寫出一系列以沙漠為背景的故事﹐ 風靡了全世界的中文讀者。', '台灣的著名作家──三毛 1943年3月26日出生於重慶，浙江省定海縣人。本名為陳懋平，1946年改名陳平。 筆名『三毛』更不知出自何處，只在她的《鬧學記》一篇序文中提及『三毛』二字暗藏一個《易經》的卦象，至於這卦象究竟隱藏何種玄機，序文中並沒有進一步的交代。 三毛的母親繆進蘭曾在〈我的女兒，大家的三毛〉這篇文章中，提及四個兄弟姊妹裡，次女三毛的性格最為特行卓立、不依常規，以及不能忍受虛假，因此父母要在她身邊看守著每一腳步是否踏穩。 另外，有讀者認為『流浪』才是三毛真正的名字，無論是她的遺作、她的遊歷和她心靈情感的轉折，始終透著一點點浪跡天涯的意味。 1973年7月，三毛與荷西在沙漠小鎮阿尤恩結婚。與荷西一道生活的歲月，三毛的文章充滿歡笑、喜樂，讀者閱讀他的小說，彷彿感受著她愉悅的婚姻生活，即使面對大風沙的侵襲，她仍顯得積極與樂觀。然而自荷西離開人世後...(展开全部)', 240, 160.00, '平裝', '三毛全集', '9789573305545 ', 19, 19);
INSERT INTO `books_list` VALUES (42, '我为你洒下月光', '简媜', NULL, NULL, '九州出版社', '简媜 / 九州出版社 / 2017-6 / 48.00元', '★当代华语文坛散文大家简媜写作三十年纪念之作 ★这样的书，一生只能写一本，为致敬，也是向过去的一场告别 ★如果《水问》是青春之书，《女儿红》是女性之书，《红婴仔》是初生之书，那《我为你洒下月光》就是爱情之书 ★数本友人遗赠的手札，带领我们回到那个车马邮件都很慢，一生只够爱一人的年代，见证手工时代刻骨墨缘 ★这是一场古典主义的浪漫，从诗经到离骚，从陶渊明到苏东坡，尽显古典风华与文字之美 ★这是一场文字的幻术。诗、小说、散文都用上，建构出真实与虚拟交错的世界，安放某些只对作者及她盼望却永远不在的唯一读者才有意义的情怀 ★若我走累了天涯， 看倦了风景，尝够了苦涩，你是否愿意变成柔软的草榻，让我把余生靠一靠？ ★爱情，是我在这世上唯一懂得的事情。简媜以至情至性之笔，书写至情至性之爱 ★感谢岁月的风霜够厚，赋予深情，足以让人体悟年轻时不懂的情与爱，在白发之年谱...(展开全部)', '简媜自述 一个对散文怀抱热情与奇想的人，乃兰阳平原生养、台大中文系培育之野生品种。惯于独来独往，既在人生火宅内，也在红尘岸边上。自认为写作性格混合猎人的冷静与猎犬的躁动，喜新厌旧，三十多年来用自己的方式走散文马拉松之路，仍然觉得是个学徒。愿化漫天烟尘为哲思凝露，在稿田里种植真与善，收割美与圣。自悟：身为作家只能葬在白纸黑字里，除此之外没有第二个江湖，故愿继续长途跋涉，独自一人，走到行兴自消之处，写到江郎才尽之时。若能如此，一生自在圆满。 已出版作品《好一座浮岛》《下午茶》《密密语》《私房书》《女儿红》《水问》《红婴仔》《只缘身在此山中》《微晕的树林》《梦游书》《天涯海角》《旧情复燃》《胭脂盆地》等。', 416, 48.00, '平装', NULL, '9787510851971 ', 1, 1);
INSERT INTO `books_list` VALUES (43, '亲爱的安德烈', '', NULL, NULL, '人民文学出版社', '龙应台、[德] 安德烈 / 人民文学出版社 / 2008-12 / 26.00', '安德烈十四岁的时候，龙应台离开欧洲，返回台湾，就任台北市首任文化局长。等她卸任回到儿子身边，安德烈已是一个一百八十四公分高的十八岁的小伙子，坐在桌子另一边，有一点“冷”地看着妈妈。 他们是两代人，年龄相差三十年；他们也是两国人，中间横着东西文化。失去小男孩安安没关系，但龙应台一定要认识成熟的大学生安德烈。 于是，母子俩用了三年时间互相通信。龙应台“认识了人生里第一个十八岁的人”，安德烈“也第一次认识了自己的母亲”。 收入此书的的三十多封书信，感动了无数被“亲子”之间隔阂与冲突深深困扰着的读者，为读者弭平代沟、跨越文化阻隔、两代人沟通交流带来了全新的思维和方法。', '龙应台，生于台湾高雄县。1986年出版《野火集》，抨击国民党政府黑暗统治，轰动台湾，当月再版二十四次，对台湾社会发生巨大影响。 1999年任首任台北市文化局局长。2003年来，先后任香港城市大学、香港大学访问教授。《南方人物周刊》中国“五十位最具影响力的公共知识分子”之一。 安德烈（Andreas Walther） 1985年12月生于台湾，八个月大移居瑞士及德国。2006年进入香港大学经济系，认为经济学得很“好玩”。', 288, 26.00, '平装', NULL, '9787020068616 ', 8, 8);
INSERT INTO `books_list` VALUES (44, '当我谈跑步时我谈些什么', '[日] 村上春树', NULL, '走ることについて語るときに僕の語ること', '南海出版公司', '[日] 村上春树 / 施小炜 / 南海出版公司 / 2009-1 / 25.00', '他以文字名满全球。 他的“蓝调”令万千人神迷忧伤。 文字是他的符号，“跑者蓝调”何尝不是？ 不再是浮华迷茫，不再是旖旎感伤，不再羚羊挂角无迹可寻——写了几十年“别人”的文字，他第一次只写自己：小说之外、故事之外、文字之外，均是不施雕琢娓娓道来，清淡如云，宁静如水……', '村上春树，日本著名作家。 循着岁月流逝、地点变迁，唯有四分之一个世纪里日日坚持跑步的所见所闻、所惑所思最是真真切切：1982年秋，开始职业作家生涯之际，也开始长跑。此后近30年，从夏威夷的考爱岛到马萨诸塞的剑桥，从日本村上市参加铁人三项赛，到踏上希腊马拉松长跑古道，他，永远奔跑。', 198, 25.00, '精装', '新经典文库·村上春树作品', '9787544242820 ', 16, 16);
INSERT INTO `books_list` VALUES (45, '皮囊', '蔡崇达', NULL, 'No More Than Skins', '天津人民出版社', '蔡崇达 / 天津人民出版社 / 2014-12-1 / 39.80元', '一部有着小说阅读质感的散文集，也是一本“认心又认人”的书。 作者蔡崇达，本着对故乡亲人的情感，用一种客观、细致、冷静的方式，讲述了一系列刻在骨肉间故事。一个福建渔业小镇上的风土人情和时代变迁，在这些温情而又残酷的故事中一一体现。用《皮囊》这个具有指向本质意味的书名，来 表达作者对父母、家乡的缅怀，对朋友命运的关切，同时也回答那些我们始终要回答的问题。 书中收录有《皮囊》《母亲的房子》《残疾》《重症病房里的圣诞节》《我的神明朋友》《张美丽》《阿小和阿小》《天才文展》《厚朴》《海是藏不住的》《愿每个城市都不被阉割》《我们始终要回答的问题》《回家》《火车伊要开往叨位》等14篇作品。 其中《皮囊》一文中的阿太，一位99岁的老太太，没文化，是个神婆。她却教给作者具有启示力量的生活态度：“肉体是拿来用的，不是拿来伺候的。” 《母亲的房子》里，母亲想要建一座房子，...(展开全部)', '蔡崇达 1982年生人，闽南人。 大隐隐于市的新生代媒体人，人称“天才达达”，韩寒口中的“写作大师”。 现任《中国新闻周刊》执行主编。“南方国际文学周”联合发起人。 曾任职于《新周刊》《三联生活周刊》；24岁担任《周末画报》新闻版主编；27岁任《GQ》中国版报道总监，为 全球17个国家版本的《GQ》最年轻的报道总监。', 264, 39.80, '精装', NULL, '9787201088945 ', 17, 17);
INSERT INTO `books_list` VALUES (46, '下雨天一个人在家', '[日] 江国香织', '张乐风 / 赵峻 / 李洁', NULL, '南海出版公司', '[日] 江国香织 / 张乐风、赵峻、李洁 / 南海出版公司 / 2017-4 / 45.00', '《下雨天一个人在家》是作家江国香织的随笔集，她以一则则简短美丽的文字，记录下带来幸福的食物、与友人喝酒谈天的时光、深夜徘徊于书店的心情、沉溺于沐浴的欢乐、无法成行的旅程、悲伤的过往…… 四季不停地轮转，唯有回忆是永恒的。我们一边将丢失的东西忘掉，一边阔步前行。随着江国香织的笔触回味过往，我们会更热爱当下的人生。', '江国香织，日本作家。1964年生于东京。1989年以《409拉德克里夫》获法国费米娜奖。1992年获紫式部文学奖，1999年获路旁之石文学奖，2002年获山本周五郎奖，2004年获直木奖，2012年获川端康成文学奖。主要作品有《沉落的黄昏》《那一年，我们爱得闪闪发亮》《好想痛痛快快哭一场》《寂寞东京塔》等。', 391, 45.00, '平装', '新经典文库：江国香织作品', '9787544287814 ', 17, 17);
INSERT INTO `books_list` VALUES (47, '孤独六讲', '蒋勋', NULL, NULL, '广西师范大学出版社', '蒋勋 / 广西师范大学出版社 / 2009-10-1 / 36.00元', '美学大师蒋勋以美学家特有的思维和情感切入孤独，从情欲、语言、革命、思维、伦理、暴力六个面向阐释孤独美学，融个人记忆、美学追问、文化反思、社会批判于一体。可以说，蒋勋在本书中，创造了孤独美学：美学的本质或许就是孤独。 自序 情欲孤独 语言孤独 革命孤独 暴力孤独 思维孤独 伦理孤独', '蒋勋，一九四七年生，福建长乐人，台北中国文化大学史学系、艺术研究所毕业。一九七二年负笈法国巴黎大学艺术研究所，一九七六年返台后，曾任《雄狮美术》月刊主编、东海大学美术系系主任，现任《联合文学》社长。著述颇丰，主要有《舞动白蛇传》《给年轻艺术家的信》《天地有大美》《美的沉思》《多情应笑我》《今宵酒醒何处》《写给大家的中国美术史》等。', 272, 36.00, '平装', '理想国·蒋勋作品', '9787563391271 ', 13, 13);
INSERT INTO `books_list` VALUES (48, '瓦尔登湖', '亨利·戴维·梭罗', NULL, 'walden', '上海译文出版社', '[美] 梭罗 / 徐迟 / 上海译文出版社 / 2006-8 / 11.00元', '这本书的思想是崇尚简朴生活，热爱大自然的风光，内容丰厚，意义深远，语言生动，意境深邃，就像是个智慧的老人，闪现哲理灵光，又有高山流水那样的境界。 书中记录了作者隐居瓦尔登湖畔，与大自然水乳交融、在田园生活中感知自然重塑自我的奇异历程。读本书，能引领人进入一个澄明、恬美、素雅的世界。', '亨利·戴维·梭罗（1817-1862），美国超验主义作家。他于1845年春天，在老家康科德城的瓦尔登湖边建起一座木屋，过起自耕自食的生活，并在那里写下了著名的《瓦尔登湖》一书。', 292, 11.00, '平装', '译文名著文库', '9787532739578 ', 11, 11);
INSERT INTO `books_list` VALUES (49, '文学回忆录（全2册） : 1989—1994', '木心 / 陈丹青', NULL, NULL, '广西师范大学出版社', '木心 口述、陈丹青 笔录 / 广西师范大学出版社 / 2013-1-10 / 98.00元', '文学是可爱的。生活是好玩的。艺术是要有所牺牲的。 八十年代末，木心客居纽约时期，亦自他恢复写作、持续出书以来，纽约地面的大陆和台湾同行在异国谋饭之中，居然促成木心开讲“世界文学史”，忽忽长达五年的一场“文学的远征”——从1989年1月15日开课，到1994年1月9日最后一课，每位听课人轮流提供自家客厅，在座者有画家、舞蹈家、史家、雕刻家等等。 听课学生陈丹青说，“我们当年这样地胡闹一场，回想起来，近于荒谬的境界：没有注册，没有教室，没有课本，没有考试与证书，更没有赞助与课题费，不过是在纽约市皇后区、曼哈顿区、布鲁克林区的不同寓所中，团团坐拢来，听木心神聊。” 菜单开出来，大家选。从古希腊神话、新旧约，到诗经、楚辞，从中世纪欧洲文学，到二十世纪文学世界，东方西方通讲，知识灵感并作。其中听的听，讲的讲，“金句”纷披，兀自燃烧。“讲完后，一部文学史，重要的...(展开全部)', '木心（1927—2011），原籍浙江，上海美术专科学校毕业。在“文革”囚禁期间，用白纸画了钢琴的琴键，无声弹奏莫扎特与巴赫。陈丹青说，“他挚爱文学到了罪孽的地步，一如他罪孽般与世隔绝”。著有《哥伦比亚的倒影》、《素履之往》、《即兴判断》、《琼美卡随想录》、《温莎墓园日记》、《我纷纷的情欲》、《西班牙三棵树》、《鱼丽之宴》、《巴珑》、《伪所罗门书》、《诗经演》、《爱默生家的恶客》、《云雀叫了一整天》等书。 木心说:“贝（聿铭）先生一生的各个阶段，都是对的；我一生的各个阶段，全是错的。”这不是反讽，而是实话，因为实话，尤甚于反讽——五十年代末，他躲在家偷学意识流写作；六十年代“文革”前夕，他与人彻夜谈论叶慈、艾略特、斯宾格勒、普鲁斯特、阿赫玛托娃；七十年代他被单独囚禁时，偷偷书写文学手稿，令人惊怵不已；八十年代末，他年逾花甲，生存焦虑远甚于流落异国的壮年...(展开全部)', 1102, 98.00, '精装', '理想国·木心作品', '9787549530816 ', 19, 19);
INSERT INTO `books_list` VALUES (50, '白鹿原', '陈忠实', NULL, NULL, '人民文学出版社', '陈忠实 / 人民文学出版社 / 1997-12 / 28.00元', '这是一部渭河平原五十年变迁的雄奇史诗，一轴中国农村班斓多彩、触目惊心的长幅画卷。主人公六娶六丧，神秘的序曲预示着不祥。一个家族两代子孙，为争夺白鹿原的统治代代争斗不已，上演了一幕幕惊心动魄的活剧：巧取风水地，恶施美人计，孝子为匪，亲翁杀媳，兄弟相煎，情人反目……大革命、日寇入侵、三年内战，白鹿原翻云覆雨，王旗变幻，家仇国恨交错缠结，冤冤相报代代不已……古老的土地在新生的阵痛中颤栗。厚重深邃的思想内容，复杂多变的人物性格，跌宕曲折的故事情节，绚丽多彩的风土人情，形成作品鲜明的艺术特色和令人震撼的真实感。', '', 677, 28.00, '平装', '茅盾文学奖获奖书系', '9787020026906 ', 20, 20);
INSERT INTO `books_list` VALUES (51, '繁花', '金宇澄', NULL, NULL, '上海文艺出版社', '金宇澄 / 上海文艺出版社 / 2013-3-1 / CNY 48.00', '这是一部地域小说，人物的行走，可找到“有形”地图的对应。这也是一部记忆小说，六十年代的少年旧梦，辐射广泛，处处人间烟火的斑斓记忆，九十年代的声色犬马，是一场接一场的流水席，叙事在两个时空里频繁交替，传奇迭生，延伸了关于上海的“不一致”和错综复杂的局面，小心翼翼的嘲讽，咄咄逼人的漫画，暗藏上海的时尚与流行；昨日的遗漏，或是明天的启示……即使繁花零落，死神到来，一曲终了，人犹未散。 《繁花》让人为之一震，小说的好处自有高人评说----程永新 优雅之野心，当今几乎不可能出现的文体----张辛欣 近年来最有看头的小说---吴亮', '金宇澄，生于上海，祖籍吴江黎里，著有中短篇集《迷夜》、随笔集《洗牌年代》，主编《城市地图》、《飘泊在红海洋——我的大串联》等。现任《上海文学》常务副主编。', 444, 0.00, '平装', NULL, '9787532148004 ', 2, 2);
INSERT INTO `books_list` VALUES (52, '北鸢', '葛亮', NULL, NULL, '人民文学出版社', '葛亮 / 人民文学出版社 / 2016-10-1 / 45.00元', '《北鸢》起笔于民国商贾世家子弟卢文笙的成长，收束于上世纪中叶。将波诡云谲的民国动荡史寄予两个家族的命运沉浮，书写中国最为丰盛起伏的断代。人生一线，恰似风筝。命运漂浮无着，人亦应有自己的主心骨。政客、军阀、寓公、文人、商人、伶人，书中上百位经典民国人物，进退于沧桑。群落交织，浑然磅礴。文笙在大时代的风云中辗转历练，且行且进，最终尘埃落定。每个人都有自己的来处，了解来处，才知道自己的去向。在故事的日常精微与家族兴颓里，藏着我们过去以及未来的影子。', '葛亮，原籍南京，现居香港，任教于高校。香港大学中文系博士。作品出版于两岸三地，著有小说《北鸢》《朱雀》《七声》《谜鸦》《浣熊》《戏年》，文化随笔《绘色》，学术论著《此心安处亦吾乡》等。部分作品译为英、法、俄、日、韩等国文字。 曾获首届香港书奖、香港艺术发展奖、台湾联合文学小说奖首奖、台湾梁实秋文学奖等奖项。作品被收入“当代小说家书系”“二十一世纪中国文学大系”，入选2008、2009、2015年“中国小说排行榜”和“2015年度诚品中文选书”。长篇小说《朱雀》获选“《亚洲周刊》全球华文十大小说”。2016年以新作《北鸢》再获此荣誉。', 490, 45.00, '平装', '葛亮作品', '9787020118076 ', 11, 11);
INSERT INTO `books_list` VALUES (53, '爱你就像爱生命', '李银河 / 王小波', NULL, NULL, '上海锦绣文章出版社', '王小波、李银河 / 上海锦绣文章出版社 / 2008-5 / 18.00元', '王小波书信均选自朝华出版社2004年4月第一版《爱你就像爱生命》，此书系王小波生前从未发表过的与李银河的“两地书”，也是迄今他们夫妇最完整和独立的一本书信集。', '王小波，1952年出生。一个特立独行的作家。他的作品被誉为“中国当代文坛最美的收获”。自1997年4月11日去世后，他的作品被人们广泛阅读、关注、讨论，并引发了“王小波热”的文化现象。 主要作品有《黄金时代》、《白银时代》、《青铜时代》、《我的精神家园》、《沉默的大多数》、《黑铁时代》、《地久天长》：纪念、评论集有 《浪漫骑士》、《不再沉默》、《王小波画传》。 李银河，1952年生于北京，王小波的遗孀。中国社会科学院社会学研究所研究员、博士生导师，美国匹兹堡大学社会博士、北京大学社会学博士后。 主要著作有《同性恋亚文化》、《中国人的性爱与婚姻》、《中国女性的性与爱》等。', 222, 18.00, '平装', '王小波作品全集', '9787806859988 ', 10, 10);
INSERT INTO `books_list` VALUES (54, '明朝那些事儿（1-9） : 限量版', '当年明月', NULL, NULL, '中国海关出版社', '当年明月 / 中国海关出版社 / 2009-4 / 358.20元', '《明朝那些事儿》讲述从1344年到1644年，明朝三百年间的历史。作品以史料为基础，以年代和具体人物为主线，运用小说的笔法，对明朝十七帝和其他王公权贵和小人物的命运进行全景展示，尤其对官场政治、战争、帝王心术着墨最多。作品也是一部明朝政治经济制度、人伦道德的演义。', '当年明月，青年历史学者，畅销书作家。专业职称为副研究员，明史学会会员。是心灵历史的开创者，担任国内多家电视台文史类节目主讲人。所著作品《明朝那些事儿》销量过五百万册，为三十年来最畅销之史学读本。', 0, 358.20, '精装16开', NULL, '9787801656087 ', 14, 14);
INSERT INTO `books_list` VALUES (55, '黄金时代 : 时代三部曲', '王小波', NULL, NULL, '花城出版社', '王小波 / 花城出版社 / 1999-3 / 19.00元', '《黄金时代》是《时代三部曲》之一。 这是以文革时期为背景的系列作品构成的长篇。发生“文化大革命”的二十世纪六七十年代，正是我们国家和民族的灾难年代。那时，知识分子群体无能为力而极“左”政治泛滥横行。作为倍受歧视的知识分子，往往丧失了自我意志和个人尊严。在这组系列作品里面，名叫“王二”的男主人公处于恐怖和荒谬的环境，遭到各种不公正待遇，但他却摆脱了传统文化人的悲愤心态，创造出一种反抗和超越的方式：既然不能证明自己无辜，便倾向于证明自己不无辜。于是他以性爱作为对抗外部世界的最后据点，将性爱表现得既放浪形骸又纯', '王小波：男，1952年生于北京。他是唯一一位两次荣获世界华语文学界的重要奖项——台湾联合报系文学奖中篇小说大奖（第13届和第16届）的祖国大陆作家。他经历、学历复杂，先后当过知青，民办教师，工人，工科大学生，后到美国兹堡大学取得文学硕士学位，再学计算机，在统计系当助教，回国后在中国人民大学和北京大学任教。', 375, 19.00, '平装', '王小波《时代三部曲》', '9787536025080 ', 20, 20);
INSERT INTO `books_list` VALUES (56, '活着本来单纯 : 丰子恺散文漫画精品集', '丰子恺', NULL, NULL, '江苏凤凰文艺出版社', '丰子恺 / 江苏凤凰文艺出版社 / 2016-10 / 45元', '丰子恺散文漫画精品集（全新精装收藏本）。女儿丰一吟授权版本，全面收录《渐》《大账簿》《梦痕》《给孩子们》等55篇经典佳作，一本书阅尽丰子恺散文精华。漫画原版复制，典雅可爱，采用特种纸印刷，色彩雅致，触感温润，可堪赏玩。有趣、 有心、有情的人生哲学。 在心情低谷时翻看，给人以温暖与悲悯；在事情顺境时闲读，又像一阵凉夜清风，不至昏头脑。 不要因为世界太复杂，而背叛了你的单纯。 ? 活着这回事，本来是如此单纯。 ? 所谓单纯，不是幼稚的天真，而是一种超越了世故，知道人世间种种矛盾、种种问题，种种细微的心理计较。但超过这一层，一种超乎其上的平视，达观看待这个世界，然后游戏其间。这是非常豁达的心胸，一种超越的态度和智慧。 ? 敬畏 * 宁静 * 智慧 * 美', '丰子恺，画家、散文家，卓有成就的文艺大师。师从弘一法师，学贯中西。朱自清，郁达夫，巴金、叶圣陶、林清玄，对其文章和漫画赞誉有加。日本汉学家吉川幸次郎则说，丰子恺是最中国的中国人。他的散文兼有平易纯朴之风、宽仁隽永之意和童真天然之趣，是现代文学中备受推崇的佳品，多次被选入学生教材，作语文教育典范。漫画幽默风趣，流传广泛 ，深受人们的喜爱，争相收藏。', 224, 45.00, '精装', NULL, '9787539996318 ', 20, 20);
INSERT INTO `books_list` VALUES (57, '台北人', '白先勇', NULL, NULL, '广西师范大学出版社', '白先勇 / 广西师范大学出版社 / 2010-10 / 38.00元', '作为20世纪中文小说100强的《台北人》，是一部深具复杂性的短篇小说集，由十四个一流的短篇小说构成，串联成一体，则效果遽然增加，不但小说之幅面变广，使我们看到社会之“众生相”，更重要的，由于主题命意之一再重复，与互相陪衬辅佐，使我们能更进一步深入了解作品之含义，并使我们得以一窥隐藏在作品内的作者之人生观与宇宙观。《台北人》之人物，可以说囊括了台北都市社会之各阶层：从年迈挺拔的儒将朴公（《梁父吟》）到退休了的女仆顺恩嫂（《思旧赋》），从上流社会的窦夫人（《游园惊梦》）到下流社会的“总司令”（《孤恋花》）。有知识分子，如《冬夜》之余嵚磊教授；有商人，如《花桥荣记》之老板娘；有帮佣工人，如《那血一般红的杜鹃花》之王雄；有军队里的人，如《岁除》之赖鸣升；有社交界名女，如尹雪艳；有低级舞女，如金大班。这些“大”人物、“中”人物与“小”人物，来自中国大陆不同的省...(展开全部)', '白先勇，小说家、散文家、评论家、剧作家。1937年生，广西桂林人。台湾大学外文系毕业，美国爱荷华大学“作家工作室”（Writer\'s Workshop）文学创作硕士。他的小说被译成英、法、德、意、日、韩等多种语言文字，在海内外拥有读者无数。著有短篇小说集《寂寞的十七岁》、《台北人》、《纽约客》，长篇小说《孽子》，散文集《蓦然回首》、《明星咖啡馆》、《第六只手指》、《树犹如此》，电影剧本《金大班的最后一夜》、《玉卿嫂》、《孤恋花》、《最后的贵族》等，重新整理明代大剧作家汤显祖的戏曲《牡丹亭》、高濂《玉簪记》，并撰有父亲白崇禧及家族传记。', 392, 38.00, '精装', '理想国·白先勇作品', '9787563397648 ', 16, 16);
INSERT INTO `books_list` VALUES (58, '一只特立独行的猪', '王小波', NULL, NULL, '北方文艺出版社', '王小波 / 北方文艺出版社 / 2006-4 / 18.80元', '这本书里除了文化杂文，还有给其他书写的序言与跋语。这些序言与跋语也表明了我的一些态度。除此之外，还有一些轻松的随笔。不管什么书，我都不希望它太严肃，这一本也不例外。 ——王小波', '王小波：学者、作家。1952年5月13日出生于北京，1968年去云南插队，1978年考入中国人民大学学习商业管理。1984年至1988年在美国匹兹堡大学学习，获硕士学位后回国，曾任教于北京大学和中国人民大学，后辞职专事写作。1997年4月11日病逝于北京。', 256, 18.80, '平装', '王小波作品选集', '9787531719199 ', 20, 20);
INSERT INTO `books_list` VALUES (59, '倾城之恋', '张爱玲', NULL, NULL, '花城出版社', '张爱玲 / 花城出版社 / 1997-3-1 / 11.00', '一对现实庸俗的男女，在战争的兵荒马乱之中被命运掷骰子般地掷到了一起，于“一刹那”体会到了“一对平凡的夫妻”之间的“一点真心”??张爱玲是作为中国现代文学史上的一位杰出作家，而不是作为一个怪人、异人而存在的。也许她将不仅仅属于现代文学史。遥想几十年、几百年后，她会像她欣赏的李清照一样，在整个中国文学史上占据一个稳定的位置也说不定，而我们知道，那时候今天为我们所熟知的许多现代作家肯定都将被忽略不计了。', '张爱玲是作为中国现代文学史上的一位杰出作家，而不是作为一个怪人、异人而存在的。也许她将不仅仅属于现代文学史。遥想几十年、几百年后，她会像她欣赏的李清照一样，在整个中国文学史上占据一个稳定的位置也说不定，而我们知道，那时候今天为我们所熟知的许多现代作家肯定都将被忽略不计了。', 284, 11.00, '平装', '张爱玲作品集', '9787536023918 ', 11, 11);
INSERT INTO `books_list` VALUES (60, '一句顶一万句', '刘震云', NULL, NULL, '长江文艺出版社', '刘震云 / 长江文艺出版社 / 2009-3 / 29.80', '《一句顶一万句》的故事很简单，小说的前半部写的是过去：孤独无助的吴摩西失去唯一能够“说得上话”的养女，为了寻找，走出延津；小说的后半部写的是现在：吴摩西养女的儿子牛爱国，同样为了摆脱孤独寻找“说得上话”的朋友，走向延津。一走一来，延宕百年。书中的人物大部分是中国最底层的老百姓，偏偏安排了一个意大利牧师老詹。', '刘震云，男，1958年5月出生，河南新乡延津人。1973年参加中国人民解放军。1978年复员，在家乡当中学教师，同年考入北京大学中文系。1982年毕业到《农民日报》工作。1988年至1991年曾到北京师范大学，鲁迅文学院读研究生。1982年开始创作，1987年后连续发表在《人民文学》《塔铺》、《新兵连》、《头人》、《单位》、《官场》、《一地鸡毛》、《官人》、《温故一九四二》等描写城市社会的“单位系列”和干部生活的“官场系列”，引起强烈反响在这些作品中，他迅速表现出成为大作家的潜在能力，确立了创作中的平民立场，将目光集中于历史、权力和民生问题，但又不失于简洁直接的白描手法，也因此被称为“新写实主义”作家。其中《塔铺》获1987-1988全国优秀短篇小说奖。', 362, 29.80, '精装', '刘震云作品', '9787535439765 ', 16, 16);
INSERT INTO `books_list` VALUES (61, '艺术的故事', '[英] 贡布里希', '范景中 / 杨成凯', 'The Story of Art', '广西美术出版社', '[英] 贡布里希 (Sir E.H.Gombrich) / 范景中、杨成凯 / 广西美术出版社 / 2008-04 / 280.00', '《艺术的故事》概括地叙述了从最早的洞窟绘画到当今的实验艺术的发展历程，以阐明艺术史是“各种传统不断迂回、不断改变的历史，每一件作品在这历史中都既回顾过去又导向未来。 继1997年三联版《艺术的故事》后，英国费顿出版社授权广西美术出版社独家出版，也是国内唯一合法授权的版本。', '恩斯特·贡布里希爵士（Sir. Ernst Gombrich）（1909——2001）生于维也纳，并在维也纳大学攻读美术史。1936年移居英国，进入伦敦大学瓦尔堡大学。曾任牛津大学斯莱德美术讲座教授，哈佛、康奈尔等多所大学的客座教授，并于1972年被英王授封勋爵。主要学术著作有《艺术的故事》《秩序感》《象征的图像》等。贡布里希善于以简明晓畅的语言来表达严肃的题目，以便初入门者能轻松学习。 范景中，1951年11月生于天津。1977年考入北京师范大学哲学系。1979年入浙江美术学院攻读艺术理论研究生，获硕士学位。先后任《美术译丛》和《新美术》主编、中国美术学院教授、博士生导师、图书馆馆长、出版社总编等职。 杨成凯，笔名林夕。1941年生，山东招远市人。1981年毕业于中国社会科学院语言系，获硕士学位。1981年起在中国社会科学院语言研究所从事研究工作，...(展开全部)', 688, 280.00, '平装', '贡布里希文集', '9787807463726 ', 5, 5);
INSERT INTO `books_list` VALUES (62, '心理学与生活', '[美] 理查德·格里格 / [美] 菲利普·津巴多', '王垒 / 王甦 等', NULL, '人民邮电出版社', '[美] 理查德·格里格、菲利普·津巴多 / 王垒、王甦 等 / 人民邮电出版社 / 2003-10 / 88.00元', '《心理学与生活》是美国斯坦福大学多年来使用的教材，也是在美国许多大学里推广使用的经典教材，被ETS推荐为GRE心理学专项考试的主要参考用书，还是被许多国家大学的“普通心理学”课程选用的教材。这本教科书写作流畅，通俗易懂，深入生活，把心理学理论与知识联系人们的日常生活与工作，使它同样也成为一般大众了解心理学与自己的极好读物。 作为一本包含着丰富的教育思想和独特教学方法的成熟教材，原书中所有元素——如由600余条词汇及解释组成的“专业术语表”，2000余条“参考文献”，以及近1000条的“人名和主题索引”等等，对于教学、研究和学习都十分宝贵，此中译本完整地翻译和保留了这些资料。', '理查德·格里格（Richard J. Gerrig）是美国纽约州立大学的心理学教授。获Lex Hixon社会科学领域杰出教师奖。在认知心理学研究领域有专长，是美国心理学会实验心理学分会的会员。从《心理学与生活》这部经典教科书第14版修订时开始，格里格成为该书的合著者。 菲利普·津巴多（Philip G. Zimbardo）是美国斯坦福大学的心理学教授，当代著名心理学家，美国心理学会主席。40多年来，由于他在心理学研究和教学领域的杰出贡献，美国心理学会特向津巴多频发了Hilgard普通心理学终生成就奖。由他开创的《心理学与生活》这部经典教科书哺育了一代又一代心理学工作者。津巴多主动让贤，推举格里格为《心理学与生活》第16版的第一作者。', 621, 88.00, '平装', '新曲线·心理学丛书', '9787115111302 ', 19, 19);
INSERT INTO `books_list` VALUES (63, '小王子', '[法] 圣埃克苏佩里 / [法] 安东尼·德·圣-埃克苏佩里', '马振聘', 'Le Petit Prince', '人民文学出版社', '[法] 圣埃克苏佩里 / 马振聘 / 人民文学出版社 / 2003-8 / 22.00元', '小王子是一个超凡脱俗的仙童，他住在一颗只比他大一丁点儿的小行星上。陪伴他的是一朵他非常喜爱的小玫瑰花。但玫瑰花的虚荣心伤害了小王子对她的感情。小王子告别小行星，开始了遨游太空的旅行。他先后访问了六个行星，各种见闻使他陷入忧伤，他感到大人们荒唐可笑、太不正常。只有在其中一个点灯人的星球上，小王子才找到一个可以作为朋友的人。但点灯人的天地又十分狭小，除了点灯人他自己，不能容下第二个人。在地理学家的指点下，孤单的小王子来到人类居住的地球。 小王子发现人类缺乏想象力，只知像鹦鹉那样重复别人讲过的话。小王子这时越来越思念自己星球上的那枝小玫瑰。后来，小王子遇到一只小狐狸，小王子用耐心征服了小狐狸，与它结成了亲密的朋友。小狐狸把自己心中的秘密——肉眼看不见事务的本质，只有用心灵才能洞察一切——作为礼物，送给小王子。用这个秘密，小王子在撒哈拉大沙漠与遇险的飞行员一...(展开全部)', '安托万·德·圣埃克苏佩里（Antoine de Saint-Exupery, 1900-1944）1900年6月29日出生在法国里昂。他曾经有志于报考海军学院，未能如愿，却有幸成了空军的一员。1923年退役后，先后从事过各种不同的职业。 1926年，圣埃克苏佩里进入拉泰科埃尔航空公司。在此期间，出版小说《南方邮件》（1929）、《夜航》（1931），从此他在文学上声誉鹊起。1939年，又一部作品《人的大地》问世。 第二次世界大战期间他重入法国空军。后辗转去纽约开始流亡生活。在这期间，写出《空军飞行员》、《给一个人质的信》、《小王子》（1943）等作品。1944年返回同盟国地中海空军部队。在当年7月31日的一次飞行任务中，他驾驶飞机飞上湛蓝的天空，就此再也没有回来。', 97, 22.00, '平装', NULL, '9787020042494 ', 19, 19);
INSERT INTO `books_list` VALUES (64, '刀锋', '[英] 威廉·萨默塞特·毛姆', NULL, 'The Razor\'s Edge', '上海译文出版社', '[英]毛姆 / 周煦良 / 上海译文出版社 / 2007-3 / 18.00元', '威廉·萨默塞特·毛姆（1874-1965），英国著名小说家、戏剧家。《刀锋》是他的主要作品之一。 小说写一个参加第一次世界大战的美国青年飞行员拉里·达雷尔。在军队里，拉里结识了一个爱尔兰好友：这人平时是那样一个生龙活虎般的置生死于度外的飞行员，但在一次遭遇战中，因趋救拉里而中弹牺牲。拉里因此对人生感到迷惘，弄不懂世界上为什么有恶和不幸，拉里开始了他令人匪夷所思的转变……', '威廉·萨默赛特·毛姆（William Somerset Maugham）（1874-1965），英国著名小说家、戏剧家。 毛姆作品列表 一．选集 《毛姆作品选》 弗雷德里克·T·贝森编（1931） 《威廉·萨姆塞特·毛姆作品选》 本书包括毛姆的全部作品和他发表在一些杂志上的文章，并附有这些刊物对此所作的介绍和注释。由雷蒙德·图尔·斯托特编（1950） 《威廉·萨姆塞特·毛姆作品选》 K．W．乔纳斯编，美国新泽西州新布伦思维克，卢特格斯大学出版。（1950） 二．全集 《毛姆戏剧集》6卷本（1931—1934） 《毛姆全集》20卷本（1934—1950） 《毛姆全集》袖珍版 14卷本（1936—1938） 《短篇小说全集》3卷本（1951年） 三．单行本 《兰姆贝思的丽莎》(Liza of Lambeth)（1897年）长篇小说 《一个圣徒发迹的奥秘》...(展开全部)', 318, 18.00, '平装', '译文名著文库', '9787532741922 ', 15, 15);
INSERT INTO `books_list` VALUES (65, '认识电影 : 插图第11版', '', NULL, NULL, '世界图书出版公司', '[美] 路易斯·贾内梯、[瑞典] 英格玛·伯格曼 [日] 黑泽明 等供图 / 焦雄屏 / 世界图书出版公司 / 2007-11 / 68.00元', '《认识电影》作为电影入门书中的经典之作，用深入浅出的笔触，告诉我们影视传播的手法，并逐项解析其复杂的语言系统与要素。问世几十年以来，此书始终是世界各地影视从业人员和主流专业院校的必备之选，更是电影爱好者不可或缺的手边书。 这本书从电影理论的典型视角出发，简明扼要地评论了电影艺术的方方面面。书中提及的影片包罗万象，组成了一道世界各地电影人的经验光谱，其中有我们熟悉的面孔如美国的斯蒂芬·斯皮尔伯格、英国的麦克·李、伊朗的阿巴斯以及中国的李安等。作者对欧美影片和导演了如指掌，尤其对美国影片和美国导演做了全面而详细的介绍。同时，作者也很关注第三世界国家电影，书中广泛涉及了伊斯兰电影、新亚洲电影以及非洲电影。作者以鞭辟入里的分析，新颖独到的见解和纷繁多样的视觉冲击，呈现给读者一个全新的电影世界。 全书围绕电影制作的核心要素组织架构，其中穿插以数百幅精美图片和精...(展开全部)', '路易斯·贾内梯（Louis Giannetti），美国著名电影理论家和影评人，俄亥俄州凯斯西储大学荣誉教授。贾内梯毕业于爱荷华大学及波士顿大学，20世纪60年代晚期开始讲授18世纪英语文学，后转入电影研究。他的作品雅俗共赏，内容涵盖政论、文学和戏剧等多方面，代表作有《美国电影大师》《闪回：电影简史》《戈达尔及其他：电影形式论文集》等。 英格玛·伯格曼（1918-2007），瑞典电影导演。代表作有《第七封印》《芬尼与亚历山大》《处女泉》等。 黑泽明（1910-1998），日本电影导演。代表作有《罗生门》《乱》《七武士》等。 焦雄屏，著名电影学者，华语世界最有影响力的电影人之一，现任台湾电影金马奖主席，“电影馆”系列丛书主编，著有《法国电影新浪潮》等四十余册文集。1975年毕业于台湾政治大学新闻系，1981年获得美国德州大学奥斯汀分校电影硕士学位,后就读...(展开全部)', 484, 68.00, '平装', NULL, '9787506287081 ', 19, 19);
INSERT INTO `books_list` VALUES (66, '山海经全译', '', NULL, NULL, '北京联合出版公司·后浪出版公司', '袁珂 译注 / 北京联合出版公司·后浪出版公司 / 2016-10 / 49.80元', '读懂《山海经》的权威实用版本 ★ 神话学大师袁珂先生在《山海经校注》的基础上，精炼了过于繁琐的学术性注释，增加了全文的白话翻译，将博大深奥的《山海经》变得浅显易读。 ★ 作为《山海经》译注的权威版本，全译本重视学术性和资料性，更强调了通俗性，对于读者来说更为实用。 《山海经》作为研究中国上古社会、领略古代神话传奇的珍贵史料，对于广大读者来说，一直存在阅读、理解上的难度。本书作为袁珂先生精心整理的一个“译注”本，没有改变经文本来面貌，并且有校、有注、有译，更加适合普通读者阅读。注释在《山海经校注》的基础上删繁就简，删去繁琐的学术性探讨文字；译文则逐字逐句全部翻译，力求能准确达意，以直译为主，小部分译文采用意译。精校的原文附以吸收了袁珂先生研究成果的注释和译文，除了重视学术性和资料性，更强调了通俗性。', '袁珂（1916—2001），当代中国神话学大师。1946年，任职台湾省编译馆，开始系统化地研究中国神话。1949年回到四川，继续从事文学暨神话学的研究；1978年调入四川省社会科学院任研究员，1984年担任中国神话学会主席。 袁珂先生著述颇丰。1950年，《中国古代神话》出版，这是我国第一部系统研究汉民族古代神话专著，由此奠定了袁珂先生的学术声望。之后，袁珂先生先后撰写了《中国神话传说》《中国神话传说词典》《古神话选释》《神话论文集》《袁珂神话论集》《巴蜀神话》（合著）等二十余部著作及八百余万字的论文。袁珂先生的多数著作被翻译成俄、日、英、法、意、韩、捷克、西班牙等多种语言。其作品还在中国、日本、美国、新加坡等国入选学校课本。', 328, 49.80, '平装', '袁珂作品', '9787550284913 ', 9, 9);
INSERT INTO `books_list` VALUES (67, '中国历代政治得失', '钱穆', NULL, NULL, '生活·读书·新知三联书店', '钱穆 / 生活·读书·新知三联书店 / 2001 / 12.00元', '《中国历代政治得失》为作者的专题演讲合集，分别就中国汉、唐、宋、明、清五代的政府组织、百官职权、考试监察、财经赋税、兵役义务等种种政治制度作了提要勾玄的概观与比照，叙述因革演变，指陈利害得失。既高屋建瓴地总括了中国历史与政治的精要大义，又点明了近现代国人对传统文化和精神的种种误解。言简意赅，语重心长，实不失为一部简明的“中国政治制度史”。', '钱穆（1895-1990年），字宾四，著名历史学家，江苏无锡人。1912年始为乡村小学教师，后历中学而大学，先后在燕京大学、北京大学、清华大学、西南联合大学等数校任教。1949年只身去香港，创办新亚书院，1967年起定居台湾。', 178, 12.00, '平装', '钱穆作品系列', '9787108015280 ', 8, 8);
INSERT INTO `books_list` VALUES (68, '恋情的终结', '格雷厄姆·格林', '柯平', 'The End of the Affair', '江苏凤凰文艺出版社', '[英] 格雷厄姆·格林 / 柯平 / 江苏凤凰文艺出版社 / 2017-4 / 58.00元', '二战期间的英国伦敦，作家莫里斯爱上公务员亨利的妻子萨拉。一次意外事件导致萨拉不辞而别， 莫里斯在恨和嫉妒中度过了两年。两年后 ，他们再次相遇，当初那段感情中炙热的 爱、恨、猜疑、嫉妒、信仰，再度折磨着莫里斯。这段恋情最终如莫里斯所言：变成了一桩有开始也有结束的风流韵事。莫里斯记述了爱情开始的时刻，以及最后的时刻。这本书里有狂热的爱、狂热的恨、狂热的猜疑、狂热的嫉妒、狂热的信仰，有爱情中所有狂热的情感。', '格雷厄姆·格林 Graham Greene (1904-1991)，21次诺贝尔文学奖提名的传奇大师。67年写作生涯，创作超过25部小说，被评为20世纪最伟大的英语作家。1950年，第一次获得诺贝尔奖提名。1976年，获美国推理作家协会大师奖。1981年，获耶路撒冷文学奖 。1986年，由英女王伊丽莎白二世授予功绩勋章。 格林一生游历于墨西哥、西非、南非、越南、古巴、中东等战乱之地，甚至任职于英国军情六处，从事间谍工作，并以此为背景创作小说，关注人灵魂深处的挣扎与救赎、内心的道德和精神斗争，被誉为20世纪人类意识和焦虑最卓越的记录者。至今，每年格林生日期间，在格林出生地——英国赫特福德郡，都会举办为期四天的格雷厄姆?格林国际艺术节，全球的格林粉丝齐聚这里参加纪念格林的活动。', 280, 58.00, '精装', '读客外国小说文库：格林文集', '9787539979410 ', 14, 14);
INSERT INTO `books_list` VALUES (69, '冷暴力', '', '顾淑馨', 'Le Harcèlement Moral: la violence perverse au quotidien', '后浪丨江西人民出版社', '[法] 玛丽-弗朗斯·伊里戈扬 / 顾淑馨 / 后浪丨江西人民出版社 / 2017-7 / 38.00元', '畅销法国20年的心理学经典 ................... ※编辑推荐※ ☆ 盘踞法国畅销榜50周，被译为24种语言； ☆ 首次全面探讨人际关系中的精神虐待行为，让无数受虐者如梦初醒的经典之作。法国心理学家与被害人学专家伊里戈扬的代表作； ☆ 伤人不见血的冷暴力 广泛 存 在于婚姻、亲子关系、职场和社会中，施虐者披着关系亲密的外衣，肆无忌惮地渗入受虐者的生活，使其成为自己精神虐待的猎物。本书意在指导受虐者认清自己的处境，跳出这一恶性循环； ☆ 伊里戈扬通过大量案例探讨了冷暴力的各种表现形式与危害，详细剖析了施虐者与受虐者的特点与关系。她饱含同情，笔锋犀利，从专业角度为受虐者立起一面镜子，让丑恶无可遁形，并提供了新的可能性。 ................... ※内容简介※ 本书首次提出了“精神虐待”这一概念，它广泛发生在婚姻、家庭和职场中...(展开全部)', '玛丽-弗朗斯·伊里戈扬，医学博士，法国精神分析学家、心理治疗师、被害人学专家，曾在巴黎第五大学任客座教授，专攻精神暴力行为，主要研究职场上的精神虐待。她曾数次作为嘉宾出现在法国的电视节目上，并积极推动法国针对职场和家庭内部精神暴力的立法进程。', 192, 38.00, '平装', NULL, '9787210090885 ', 4, 4);
INSERT INTO `books_list` VALUES (70, '东京本屋', '[日] 吉井忍', NULL, NULL, '上海人民出版社', '[日] 吉井忍 / 上海人民出版社 / 2016-8 / 69.00元', '总有一盏书店的灯光，等待温暖人心。 日本人对书店有两种称呼，“书店（shoten）”和“本屋（honya）”。前者比较书面，后者则偏口语，带着一种亲切感，我们通常会在“本屋”后面加上一个“桑”。我小时候，没事儿就老往离家不远的小书店跑。母亲听到门口穿鞋的动静，问我去哪儿，我总回答：“去本屋桑。” 可是长大后，“本屋桑”渐渐变少了。他们在哪儿呢，他们还好吗？', '吉井忍（Yoshii Shinobu），日籍华语作家，现旅居北京。毕业于日本国际基督教大学国际关系专业。曾在成都留学，法国南部务农，辗转台北、马尼拉、上海等地任经济新闻编辑。 现专职写作，著有《四季便当》、《东京本屋》，审校有“MUJI轻料理”丛书等。作品亦见于《知日》、《读库》、《 鲤》、《南方周末》、《城市画报》等报刊媒体。', 440, 69.00, '平装', NULL, '9787208139008 ', 19, 19);
INSERT INTO `books_list` VALUES (71, '人间失格', '', '许时嘉', '人間失格', '吉林出版集团有限责任公司', '太宰治 / 许时嘉 / 吉林出版集团有限责任公司 / 2009年9月 / 16.00元', '《人间失格》是日本著名小说家太宰治最具影响力的小说作品，同时也是糸色望（注：动漫《再见！绝望先生》的主角）老师日常生活必备的读物之一。另外在日本轻小说《文学少女》第一卷中被大量提及。《人间失格》（又名《丧失为人的资格》）发表于1948年，是一部自传体的小说，纤细的自传体中透露出极致的颓废，毁灭式的绝笔之作。太宰治巧妙地将自己的人生与思想，隐藏于主角叶藏的人生遭遇，藉由叶藏的独白，窥探太宰治的内心世界，一个“充满了可耻的一生”。在发表这部作品的同年，太宰治就自杀身亡。', '太宰治（1909－1948） 本名津岛修治，青森县人，父亲津岛原右卫门曾任众议院议员和贵族院议员，经营银行、铁路。太宰治是日本战后“无赖派”文学的代表作家，思想消沉、幻灭，不满现实的人生，几度自杀未果。一九三五年，他的短篇《逆行》入围第一届芥川奖，后因出版多部带有哀切的抒情作品而深受注目。一九三九年，他的《女生徒》获第四届北村透谷奖。一九四八年，他以《如是我闻》再度震惊文坛，并开始创作《人间失格》，书成之后，旋即投水自杀，结束了其灿烂多感而又凄美悲凉的一生。', 168, 16.00, '平装', '草月译谭', '9787546302393 ', 1, 1);
INSERT INTO `books_list` VALUES (72, '金色梦乡', '[日] 伊坂幸太郎', '李彦桦', 'ゴールデンスランバー', '译林出版社', '[日] 伊坂幸太郎 / 李彦桦 / 译林出版社 / 2010-9 / 35.00元', '平凡的送貨員──青柳雅春和多年不見的老友森田森吾偶遇，卻突然被告知首相即將被暗殺，而青柳則是兇手！還一頭霧水的青柳，早已成為警方不擇手段要緝捕的對象，更慘的是，馬上成為媒體炒作下十惡不赦的罪人，唯一的對策只有不斷的逃、逃、逃…… 一路上，小混混、無差別連續殺人魔、前女友等各路人馬相繼出現，情義相挺，他最後會栽在莫名其妙的命運作弄中，還是能順利逃出重圍呢？', '伊坂幸太郎，1971年出生于日本千叶县，东北大学法学部毕业。 2000年以《奥杜邦的祈祷》获得第五届新潮推理俱乐部奖，在文坛初露锋芒。 2003年凭借《重力小丑》入围直木奖。2004年《家鸭与野鸭的投币式置物柜》获得第二十五届吉川英治文学新人奖。本书中收录的《死神的精确度》亦获得第五十七届日本推理作家协会短篇奖。作者知识广博，文风独具一格，是日本文坛近年来备受瞩目的天才作者。 2008年作品《金色梦乡》荣获2008年日本书店大赏。本书原获直木奖提名但遭伊坂幸太郎婉拒，造成日本文坛一片哗然。', 432, 35.00, '精装', '乐读文库', '9787544713986 ', 8, 8);
INSERT INTO `books_list` VALUES (73, '我的职业是小说家', '[日] 村上春树', NULL, '職業としての小説家', '南海出版公司', '[日] 村上春树 / 施小炜 / 南海出版公司 / 2017-1 / 45.00元', '《我的职业是小说家》是村上春树首部自传性作品，历时六年完成。 一个人，写作三十五年，十三部长篇小说，超过五十种语言译本。虽然拥有享誉世界的知名度，但关于村上春树，许多事情始终包裹在神秘的面纱中：他是怎样下定决心走上职业小说家之路？对他来说，人生中幸福的事是什么？究竟如何看待芥川奖与诺贝尔文学奖…… 小说家看似风光，却是份孤独的职业。三十五年来，村上春树在孤独中编织着美妙动人的故事。他以十二章肺腑之言，真挚诚恳又不失幽默地讲述自己写作道路上的故事，和追逐梦想与幸福的人生往事。 不论是作为声名显赫的作家，还是认真生活的普通人，他的故事都为人们带来信心和勇气。《我的职业是小说家》就是村上春树热爱生活、追求梦想的真实写照。', '村上春树，日本作家。生于1949年。29岁开始写作，处女作《且听风吟》获日本群像新人奖。1987年出版的《挪威的森林》，日文版销量突破1000万册。2009年出版的《1Q84》被誉为“新千年日本文学的里程碑”。2013年4月，《没有色彩的多崎作和他的巡礼之年》面世，七天 突破100万册，创日本文学史上最快突破100万册的纪录。写作之余，热衷翻译英语文学、跑步、爵士乐等。 译者 施小炜，翻译家、学者，旅日多年。译有《老师的提包》《1Q84》《当我谈跑步时，我谈些什么》《天黑以后》《没有色彩的多崎作和他的巡礼之年》等。', 247, 45.00, '精装', '新经典文库·村上春树作品', '9787544285377 ', 16, 16);
INSERT INTO `books_list` VALUES (74, '挪威的森林', '[日] 村上春树', NULL, 'ノルウェイの森', '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2001-2 / 18.80元', '这是一部动人心弦的、平缓舒雅的、略带感伤的恋爱小说。小说主人公渡边以第一人称展开他同两个女孩间的爱情纠葛。渡边的第一个恋人直子原是他高中要好同学木月的女友，后来木月自杀了。一年后渡边同直子不期而遇并开始交往。此时的直子已变得娴静腼腆，美丽晶莹的眸子里不时掠过一丝难以捕捉的阴翳。两人只是日复一日地在落叶飘零的东京街头漫无目标地或前或后或并肩行走不止。直子20岁生日的晚上两人发生了性关系，不料第二天直子便不知去向。几个月后直子来信说她住进一家远在深山里的精神疗养院。渡边前去探望时发现直子开始带有成熟女性的丰腴与娇美。晚间两人虽同处一室，但渡边约束了自己，分手前表示永远等待直子。返校不久，由于一次偶然相遇，渡边开始与低年级的绿子交往。绿子同内向的直子截然相反，“简直就像迎着春天的晨光蹦跳到世界上来的一头小鹿”。这期间，渡边内心十分苦闷彷徨。一方面念念不忘直...(展开全部)', '村上春树（1949- ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 350, 18.80, '平装', '村上春树文集', '9787532725694 ', 18, 18);
INSERT INTO `books_list` VALUES (75, '白夜行', '[日] 东野圭吾', '刘姿君', '白夜行', '南海出版公司', '[日] 东野圭吾 / 刘姿君 / 南海出版公司 / 2008-9 / 29.80元', '“只希望能手牵手在太阳下散步”，这个象征故事内核的绝望念想，有如一个美丽的幌子，随着无数凌乱、压抑、悲凉的故事片段像纪录片一样一一还原：没有痴痴相思，没有海枯石烂，只剩下一个冰冷绝望的诡计，最后一丝温情也被完全抛弃，万千读者在一曲救赎罪恶的凄苦爱情中悲切动容……', '东野圭吾，日本著名作家。1958年生于大阪。1985年，以第31届江户川乱步奖获奖作品《放学后》出道，开始扬名立万。20年作品逾60部，几乎囊括日本所有大奖：1999年，《秘密》获第52届日本推理作家协会奖，入围第120届直木奖；2000年，《白夜行》入围第122届直木奖；2001年，《暗恋》入围第125届直木奖；2004年，《幻夜》入围第131届直木奖；2006年的《嫌疑人X的献身》，获得第134届直木奖、第6届本格推理小说大奖。 《白夜行》出版之后引起巨大轰动，使东野圭吾成为天王级作家。小说被改编成同名电视连续剧，与图书一同为作家带来了如潮好评，使他成为日本、韩国与台湾等地最受欢迎的作家之一。', 467, 29.80, '平装', '新经典文库·东野圭吾作品', '9787544242516 ', 19, 19);
INSERT INTO `books_list` VALUES (76, '百鬼夜行 阳', '', '王华懋', NULL, '世纪文景/上海人民出版社', '[日] 京极夏彦 / 王华懋 / 世纪文景/上海人民出版社 / 2017-3-3 / 49.00元', '★简体中文版首次收录十幅京极夏彦亲笔百鬼图。 ★十夜怪谈，真相层层揭开。被妖怪吞噬了心灵的凡人，渐渐模糊了幻象与现实的边界。 ★青行灯、大首、屏风窥、鬼童、青鹭火、雨女……“勿于昏夜谈鬼事，谈鬼事，则怪至。” ★著名插画师五宝手绘封面图，诠释京极夏彦的妖怪之美。 京极夏彦的“百鬼夜行”系列短篇集新作。与《百鬼夜行——阴》的内容构成一致，全书收录了十篇阴郁压抑，又独具魅力的妖怪故事，既有恐怖的“大首”，又有凄美的“青鹭火”；既有邪恶的“鬼童”，又有诡异的“蛇带”。讲述了被妖怪吞噬了心灵的凡人，如何渐渐模糊了幻象与现实的边界。全书以独特的叙述节奏营造出一种虚虚实实、亦真亦幻的氛围。', '京极夏彦—— 别人难以模仿、难以企及的作品，对他来说只是兴趣。 1963年3月26日出生于北海道小樽。 1994年：在工作之余写下处女作《姑获鸟之夏》，为推理文坛带来极大的冲击。 1996年：出版百鬼夜行系列之二《魍魉之匣》，就拿下第四十九届日本推理作家协会奖，之后陆续推出《狂骨之梦》《铁鼠之槛》等十余部系列作品。 1997年：时代小说《嗤笑伊右卫门》获第二十五届泉镜花文学奖。 2003年：时代小说《偷窥者小平次》获第十六届山本周五郎奖；怪奇时代小说《后巷说百物语》获第一百三十届直木奖。 2011年：怪奇时代小说《西巷说百物语》获第二十四届柴田炼三郎奖。 百鬼夜行系列小说人物设定鲜明，布局精彩，架构繁复，举重若轻的书写极具压倒性魅力，书籍甫出版便风靡大众，读者群遍及各年龄层与行业。该系列从1994年延续至今，已成为里程碑式的经典。', 448, 49.00, '精装', '京极夏彦作品', '9787208139572 ', 2, 2);
INSERT INTO `books_list` VALUES (77, '不可思议的朋友', '', '李秀芬', NULL, '北京联合出版公司', '[日] 田岛征彦 / 李秀芬 / 北京联合出版公司 / 2017-7 / 45.00元', '第十三届文津奖入围作品 豆瓣2017年度高分图书第三名 第20届日本绘本奖大奖作品 改编自真实故事的自闭症题材绘本 ..................... ※编辑推荐※ ★第20届日本绘本奖大奖作品，一部改编自真实故事的自闭症题材绘本 《不可思议的朋友》讲述的是一段 历经20年的非同寻常的友情。“我”和自闭症少年“小安”相 识在小学二年级，刚认识小安的时候，我对他充满了戒备和不解，可随着交往的深入和自身的改变，我和小安渐渐成为了好朋友。我们一路同行，直到长大成人，经历了许许多多。在我心里，小安就是我的——不可思议的朋友。 这是一本主题深刻的自闭症题材绘本，但讲述的是一个平实、动人的故事。故事取材于日本淡路岛上发生的真实故事，故事的原型是治井一马、小田阳介两位少年，在书中他们作为“小安”和“太田佑介”登场。他们从幼儿园时代就相识，直到29岁的今天，...(展开全部)', '田岛征彦，1940年生于日本大阪府堺市，京都市立美术大学染色设计系毕业。 绘本作品有：《祗园祭》（获第六届世界绘本原画展金牌）、《地狱里的宗兵卫》（获第一届日本绘本奖）、《小太郎》（与吉桥通夫合作）、《初雪》（与竹内智惠子合作，获1989年德国莱比锡国际图书设计展银奖） 、《鲶鱼上天了》（获第11届世界绘本原画展金牌）、《中冈在哪里》（关屋敏隆绘）、《着火的芳子》（与高腾匡子合作）、《小蚯蚓宽太郎》、《双胞胎兄弟》（与田岛征三合作）、《七度狐》等。另外还有自传作品集《愤染记》及多部随笔集。', 20, 45.00, '精装', NULL, '9787550297760 ', 12, 12);
INSERT INTO `books_list` VALUES (78, '强风吹拂', '', '林佩瑾 / 李建铨 / 杨正敏', '风が强く吹いている', '广西师范大学出版社', '三浦紫苑 / 林佩瑾、李建铨、杨正敏 / 广西师范大学出版社 / 2015-1-1 / 39.00元', '长跑的目标不是更快，而是更强。 “明明这么痛苦，这么难过，为什么就是不能放弃跑步？ 因为全身细胞都在蠢蠢欲动，想要感受强风迎面吹拂的滋味。” 宽政大学宿舍“竹青庄”的十名舍友凑成杂牌长跑队，在队长清濑灰二的魔鬼训练下，从零开始向日本历史最悠久的长跑接力赛“箱根驿传”挺进 。这十名大学生包括两名田径队逃兵、一对神经大条又聒噪的双胞胎、俊帅漫画宅男、尼古丁中毒的万年留级生、逻辑超强的毒舌精英、不爱跑步的黑人留学生、老实好青年、百发百中猜谜王。这些“选手”刚开始时连自己是田径队都不知道，且一半成员没有长跑经验。他们能否创造“箱根驿传”史上最大奇迹？不到最后一棒，没人知道答案。 “箱根驿传”二三事 1、 日本历史最悠久的长跑接力比赛，始于1920年。每年1月2日、3日举办，由关东学生田径联盟主办，读卖新闻协办。是日本新年最重要的、全民关注的体育赛事。 2、 ...(展开全部)', '三浦紫苑 1976年生于东京。 2000年以长篇小说《女大生求职奋战记》踏入文坛。 三浦擅长塑造个性鲜明的人物角色，故事洋溢着年轻人的青春面貌，深获年轻读者喜爱。作品多获影视、广播剧、漫画改编，广受好评。 2006年以《多田便利屋》荣获第135届“直木赏”大奖肯定。 2 007年以《强风吹拂》拿下“本屋大赏”第三名。 2010年以《哪啊哪啊~神去村》入选“本屋大赏”前十大。 2012年以《编舟记》夺得“本屋大赏”第一名。', 434, 39.00, '平装', NULL, '9787549559671 ', 12, 12);
INSERT INTO `books_list` VALUES (79, '怒', '[日] 吉田修一', '岳远坤', '怒り', '世纪文景/上海人民出版社', '[日] 吉田修一 / 岳远坤 / 世纪文景/上海人民出版社 / 2016-7-31 / 42.00元', '2011的夏天，东京郊外一对普通夫妇惨遭杀害。凶手山神一也作案后逗留屋内长达六个小时，并用被害人的血在墙上留了一个“怒”字，方才离去。案件的凶残性让人发指，其动机则让警方困惑。一年后，经过微整容的山神依旧在逃。而此时，三地分别有三个来路不明的男子，日渐融入当地的生活。房总渔港的田代、东京市区的直人、冲绳附近离岛的田中，三个人各与通缉犯山神有着某种程度的暗合。那些接纳陌生人并付出情感的人们迟迟注意到第二次公布的通缉信息，信任与爱被放到了天平上。 谁是凶手？你又如何能相信身边的他不是凶手？', '吉田修一 ，1968年生于长崎。1997年以《最后的儿子》获文学界新人奖。2002年以《同栖生活》获第15届山本周五郎奖，并以《公园生活》获第127届芥川奖，彰显其跨界大众小说和纯文学的才气。2007年在《朝日新闻》连载《恶人》，获第61届每日出版文化奖，第34届大佛次郎奖。2010年，《横道世之介》获第23届柴田炼三郎奖。2012年起在《读卖新闻》连载《怒》。 吉田修一专注于描写日本都市年轻人的生活状态。其作品有着丰富的面向，既有温情如《横道世之介》，也有散淡背后隐藏惊悚的《同栖生活》，或是荒诞戏谑的小人物列传《平成猿蟹合战图》。而其笔力最犀利的，当属《恶人》和《怒》，相隔七年的这两部小说，都是借罪案揭露人性的幽微，让人读后心有回响。', 396, 42.00, '平装', '吉田修一作品', '9787208137004 ', 7, 7);
INSERT INTO `books_list` VALUES (80, '1Q84 BOOK 1 : 4月～6月', '[日] 村上春树', NULL, NULL, '南海出版公司', '[日] 村上春树 / 施小炜 / 南海出版公司 / 2010-5 / 36.00元', '1Q84简体中文版官方网站：http://www.douban.com/minisite/1q84/ “不管喜欢还是不喜欢，目前我已经置身于这‘1Q84年’。我熟悉的那个1984年已经无影无踪，今年是1Q84年。空气变了，风景变了。我必须尽快适应这个带着问号的世界。像被放进陌生森林中的动物一样，为了生存下去，得尽快了解并顺应这里的规则。” 《1Q84》写一对十岁时相遇后便各奔东西的三十岁男女，相互寻觅对方的故事，并将这个简单故事变成复杂的长篇。我想将这个时代所有世态立体地写出，成为我独有的“综合小说”。超越纯文学这一类型，采取多种尝试。在当今时代的空气中嵌入人类的生命。——村上春树 ★村上春树蛰伏7年震撼新作！ ★1979-2009，村上春树创造30年巅峰杰作！ ★ 现在是1Q84年，空气变了，风景变了，规则也变了。 为了保护自己，你必须尽快顺应这1...(展开全部)', '村上春树 著名作家。 1979年，《且听风吟》出版以来，寂寞忧郁的文字、清淡闲适的情节，尤其是独特新颖的都市感觉的写作文风，令万千读者如痴如醉。随着《挪威的森林》、《舞！舞！舞！》、《海边卡夫卡》等作品的陆续出版，逐渐赢得世界性的崇高声誉，最终形成“村上春树文学山系”。 2009年，集毕生文学之大成的巅峰杰作《1Q84》隆重出版，以席卷之势荣登日本所有畅销榜首，在整个日本、东亚乃至全球都引起巨大反响。正如媒体评论：“如此重大而复杂的题材，几可视为日本文学在新千年的伟大开篇。”', 392, 36.00, '精装', '新经典文库·村上春树作品', '9787544247269 ', 15, 15);
INSERT INTO `books_list` VALUES (81, '智惠子抄 : 我不能接受，你将要远去', '[日] 高村光太郎', '安素', '智恵子抄', '中信出版集团/楚尘文化', '[日] 高村光太郎 / 安素 / 中信出版集团/楚尘文化 / 2017-6-15 / 45.00', '日本著名雕刻家、画家与诗人高村光太郎四十年来写给妻子漫长的情书。光太郎与智惠子的恋爱不为家长认同，却对彼此倾注了全部情感。然而，生活的艰辛、艺术创作中的挫折、娘家的破产，使智惠子患了精神疾病。在与病魔搏斗数年后，智惠子撒手人寰。 《智惠子抄》记录了两人从相恋的不安、婚后贫穷又幸福的生活，到智惠子患病仿若生活在另一个世界，以及决然离去后诗人的一系列生活光影。《智惠子抄》出版后广为流传，在影视、戏剧等各个领域都有它的改编作品。 那份真诚的爱和痛切，打动了一代又一代的读者。成为镌刻在教科书上的经典爱情。 本书由《智惠子抄》《<智惠子抄>补遗》《<智惠子抄>以后》三部分组成，收录了所有与智恵子相关的诗歌、散文、日记，囊括了高村光太郎一生的爱和思念。', '高村光太郎（たかむら こうたろう，1883—1956），号碎雨，日本近代著名雕刻家、画家和诗人。《东京新闻》曾评价：“作为诗人和艺术家，他是无可挑剔的。” 而在日本新诗史上，高村光太郎也是一个绕不过去的名字，其诗歌代表作《智惠子抄》更是世界文学史上罕见的“爱的编年史”。 1911年，28岁的高村光太郎结识女画家长沼智惠子，三年后他发表了自己的第一部诗集《路程》。 1938年，在与病魔苦斗多年之后，52岁的智惠子死于肺结核。三年后，高村光太郎出版了纪念诗集《智惠子抄》，这部诗集是他们三十年来爱情的见证。 晚年，他独居于岩手县的山间小屋中，自耕自食，终日与记忆中的智惠子为伴。1956年，73岁的他也因肺结核病逝。', 200, 45.00, '精装', NULL, '9787508669892 ', 14, 14);
INSERT INTO `books_list` VALUES (82, '海边的卡夫卡', '[日] 村上春树', NULL, NULL, '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2003-4 / 25.00元', '小说的主人公是一位自称名叫田村卡夫卡——作者始终未交代其真名——的少年。他在十五岁生日前夜独自离家出走，乘坐夜行长途巴士远赴四国。出走的原因是为了逃避父亲所作的比俄底浦斯王还要可怕的预言：尔将弑父，将与尔母、尔姐交合。卡夫卡四岁时，母亲突然失踪，带走了比卡夫卡年长四岁、其实是田村家养女的姐姐，不知何故却将亲生儿子抛弃。他从未见过母亲的照片，甚至连名字也不知道。仿佛是运命在冥冥之中引导，他偶然来到某私立图书馆，遂栖身于此。馆长佐伯女士是位四十多岁气质高雅的美妇，有着波澜曲折的神秘身世。卡夫卡疑心她是自己的生母，佐伯却对此不置可否。卡夫卡恋上了佐伯，并与之发生肉体关系。小说还另设一条副线，副线的主角是老人中田，他在二战期间读小学时，经历过一次神秘的昏迷事件，从此丧失了记忆，将学过的知识完全忘记，甚至不会认字计数，却获得了与猫对话的神秘能力。中田在神智失控...(展开全部)', '村上春树（1949- ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 514, 25.00, '平装', '村上春树文集', '9787532734191 ', 3, 3);
INSERT INTO `books_list` VALUES (83, '来自新世界 （上下）', '[日] 贵志祐介', '丁丁虫', '新世界より', '上海译文出版社', '[日] 贵志祐介 / 丁丁虫 / 上海译文出版社 / 2014-4 / 88.00', '“离开小町回头去看，有一件事我看得清清楚楚——我们的小町，很扭曲。 为了维持小町的安定和秩序，不断杀害孩子们的小町，还能称之为人类的社会吗？人类从涂满鲜血的历史中走过，才抵达如今的状态。然而即使是和过去最黑暗的时代相比，今天的小町也是并不值得自豪的替代品。 我们已经无法在神栖六十六町活下去了。小町不许我们活下去。只要被打上了失格的烙印，便无法再回到当初了。这不是对待人类的方式，而是和甄选不良品一样的做法，你不觉得吗？当烧瓷窖开启的时候，走型的、有裂纹的瓷器，等待它们的就是被敲碎的命运。”', '贵志祐介， 1959年生于大阪，毕业于京都大学。30岁时因为同事的意外死亡而改变人生轨迹，从公司辞职，专职从事写作。擅长推理、恐怖和科幻小说，处女作《第十三种人格》，代表作《黑屋吊影》、《青之焰》、《恶之教典》等。获奖记录：日本恐怖小说大奖（1997），日本推理作家协会奖（2005），日本SF大奖（2008），山田风太郎奖（2010）。', 1075, 88.00, '精装', NULL, '9787532763863 ', 16, 16);
INSERT INTO `books_list` VALUES (84, '不思议图书馆', '[日] 寺山修司', '杨玲', NULL, '浦睿文化/湖南人民出版社', '[日] 寺山修司 / 杨玲 / 浦睿文化/湖南人民出版社 / 2017-5-1 / 48.00', '怪人胡迪尼的逃脱艺术、怀孕机器人的悲剧、世界上最古老的迷宫是给克里奥帕特拉的礼物、巨人的毛发对黑死病有效说、发明了自杀机器并当场演示的男人、世上有蓄胡子的女人吗？……所有冷僻、猎奇话题都在寺山修司的《不思议图书馆》中。 寺山修司以强烈的好奇心、热情洋溢的解说，讲述了街头魔术师、机器人、迷宫、爱犬读物、蓄须女、恋物癖、错视画、猎奇杀人法、美食读物、爱伦?坡、龙的画集、少女杂志、开膛手杰克等22种猎奇话题，引经据典、趣味十足。 翻开这本书，就像进入一间充满了奇珍异宝的房间。 ———————————— 寺山修司语录 ?街头魔术被推上了舞台，白日梦变成了“作品”被封印在舞台的虚构性中……在当今这个时代，整个社会的柔型结构早已强有力到你无论做什么都会被“杂耍化”的境地。 ?对于多数现在作家而言，机器人是现实人类的比喻，并且通常象征那些被抹杀的人类特征。 ? 嘲...(展开全部)', '寺山修司 日本诗人、导演。1935年出生于日本青森县，12岁开始发表诗作，17岁组织全国学生俳句会议，19岁获得“短歌研究”新人奖。 1960年前后，开始涉足影视编剧和电影导演工作，代表作有《抛掉书本上街去》《上海异人娼馆》《死者田园祭》，因颠覆而前卫的视觉风格而被誉为“银幕诗人”。 1967年，创办实验剧团“天井栈敷”，巧妙运用肉体、影像、音乐和诗的语言，革新了小剧场的表演美学。 著有《幻想图书馆》《不思议图书馆》《寺山修司少女诗集》等多本图书。 1983年5月，因肝硬化逝世。 杨玲（译者） 日语教师。日本国立广岛大学文学硕士，专攻中日汉诗。曾旅居日本九年，回国后任新东方日语教师培训师。译有寺山修司的《不思议图书馆》、高阶秀尔的《日本人眼中的美》等。', 279, 48.00, '精装', '浦睿文化 寺山修司作品', '9787556116621 ', 9, 9);
INSERT INTO `books_list` VALUES (85, '世界尽头与冷酷仙境', '[日] 村上春树', NULL, '世界の終りとハードボイルド?ワンダーランド', '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2002-12 / 23.00元', '《世界尽头与冷酷仙境》是村上春树最重要的小说之一，与《挪威的森林》、《舞舞舞》合称为村上春树三大杰作。小说共40章，单数20章“冷酷仙境”，双数20章为“世界尽头”，这种交叉平行地展开故事情节的手法是村上春树小说的特征，而这部作品是这种特征最典型的体现。“冷酷仙境”写两大黑社会组织在争夺一个老科学家发明的控制人脑的装置，老人躲到了地底。主人公“我”是老人的实验对象，他受到黑社会的恐吓，在老人的孙女帮助下，经过了惊心动魄的地底之旅，好容易找到老人，却被告知由于老人的计算错误，他24小时后离开人世，转往另一世界即“世界尽头”。“我”回到地面上, 与女友过了最后一夜告别，然后驱车到海边静候死的到来。“世界尽头”是另一番景象，这里与世隔绝，居民相安无事，但人们没有心，没有感情，没有目标。“我”一直想逃离这里，但在即将成功时选择了留下，因为“我”发现“世界尽头...(展开全部)', '村上春树（1949- ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 450, 23.00, '平装', '村上春树文集', '9787532730001 ', 16, 16);
INSERT INTO `books_list` VALUES (86, '情书', '[日] 岩井俊二', '穆晓芳', 'ラヴレター', '天津人民出版社', '[日] 岩井俊二 / 穆晓芳 / 天津人民出版社 / 2004-7 / 18.00元', '日本神户，渡边博子在未婚夫藤井树的三周年祭日上又一次陷入到悲痛和思念之中。博子在藤井树的中学同学录里找到了他在小樽市读书时的地址。由于抑制不住对爱人的怀念，博子按着这个地址给远在天国的藤井树寄去了一封充满问候和思念的书信。 不可思议的是，不久博子竟然收到了署名为“藤井树”的回信。经过进一步了解，这个藤井树是一个年轻的女子，而且她还曾经是男性藤井树的同班同学，原来是博子从同学录中误抄了她的地址。为了多了解一些男友在中学时代的情况，博子继续与女性藤井树保持书信来往。而藤井树在不断的回忆中，竟逐渐发现中学时代那个和自己同名同姓的少男曾经对自己产生过一段真挚的感情…… 《情书》由一个同名同姓的误会开始，通过两个女子书信的交流，以含情脉脉的笔触舒缓地展现了两段可贵的爱情。女主角博子对藤井树的眷恋，两个藤井树之间朦胧的情感，都没有由于藤井树的意外死亡而枯萎，而通...(展开全部)', '岩井俊二，作家，导演。1963年1月24日生于日本宫城县仙台市，毕业于横滨国立大学，具有多方面的艺术修养。 在1990年代佳作迭出的“日本新电影运动”中，简单纯粹、深情唯美的《情书》于1995年公映后，引起空前轰动，迅速风靡整个亚洲，并在世界电影的版图中取得无可争议的重要一席。岩井俊二成为日本电影界的首席代表。 此后，岩井俊二创作并执导的更具深度的《燕尾蝶》以及《关于莉莉周的一切》等作品问世后，进一步奠定了他在世界文坛和影坛的地信。他的每一部作品，都成为无数人视听和精神的盛宴，令人久久流连，回味无穷。至今，《情书》中讲述的深沉眷恋和一去不复返的少年往事，已成为亿万人心中珍藏的记忆。而美丽与忧伤的记忆，总是深深唤起我们对真爱与生命的珍视。', 256, 18.00, '平装', '新经典文库·岩井俊二作品', '9787201048161 ', 14, 14);
INSERT INTO `books_list` VALUES (87, '悲伤与理智', '[美] 约瑟夫·布罗茨基', NULL, 'On Grief and Reason', '上海译文出版社', '[美] 约瑟夫·布罗茨基 / 刘文飞 / 上海译文出版社 / 2015-4 / CNY 58.00', '在这部题材丰富、视界浩淼的散文集中，约瑟夫?布罗茨基开篇便用深沉内省的目光审视了自己在苏俄的早年经历以及随后去往美国的流亡生涯。接着，作者用惊人的博学探讨了诗歌的张弛变幻、历史的本质、流亡诗人的双重困境等一系列颇具广度与深度的话题，思维的触手延揽古今，上及古罗马贤帝马可?奥勒留， 下至现当代诗人托马斯?哈代与罗伯特?弗罗斯特，将对存在本质的哲学探讨与对诗歌美学的炽烈情愫糅合锻造为继《小于一》之后的又一部世所罕见的奇作。 收入文集的二十一篇散文大致分为回忆录、旅行记、演说讲稿、公开信和悼文等几种体裁。这些散文形式多样，长短不一，但它们诉诸的却是一个共同的主题，即“诗和诗人”。这卷文集可以说是通向布罗茨基的诗歌观和美学观，乃至他的伦理观和世界观的一把钥匙。文集中最后一篇作品《悼斯蒂芬·斯彭德》完成后不到半年，布罗茨基自己也离开了人世，《悲伤与理智》因此也...(展开全部)', '1987年诺贝尔文学奖得主约瑟夫?布罗茨基（1940—1996）是一位跨越了英语与俄语世界的文学奇才。生于1940年的列宁格勒，布罗茨基的前半生在母国苏联度过，他的大部分诗歌成就也是用俄语完成的；1972年，永别故土、定居美国的布罗茨基从零开始学习英语，进而一举成为英语世界最为卓 越的散文大师之一。诚如他在一次采访中所给出的自我认知：“我是一名犹太人；一名俄语诗人；一名英语散文家。” 1986年，布罗茨基荣获美国国家书评奖，1987年荣获诺贝尔文学奖，1991年获选“美国桂冠诗人”。其代表作品有诗集《诗选》、《词类》、《致乌拉尼亚》，散文集《小于一》、《悲伤与理智》等。', 592, 0.00, '精装', '约瑟夫·布罗茨基作品', '9787532768349 ', 19, 19);
INSERT INTO `books_list` VALUES (88, '孩子你慢慢来', '龙应台', NULL, NULL, '生活·读书·新知三联书店', '龙应台 / 生活·读书·新知三联书店 / 2009-12-1 / 28.00元', '作为华人世界最有影响的一支笔，龙应台的文章有万丈豪气，然而《孩子你慢慢来》却令人惊叹，她的文字也可以有款款深情。这本书里的龙应台是一个母亲，作为母亲的龙应台和作为一个独立的人的龙应台有着丰富、激烈的内心冲突，而正是通过对这一冲突的诉说，表现出她内心深处的母爱。但它不是传统母爱的歌颂，是对生命的实景写生，只有真正懂得爱的作家才写得出这样的生活散文。 十五年前龙应台以一位母亲的亲身经验写下《孩子你慢慢来》，她在书中说：“谁能告诉我做女性和做个人之间怎么平衡？我爱极了做母亲，只要把孩子的头放在我胸口，就能使我觉得幸福。可是我也是个需要极大的内在空间的个人……女性主义者，如果你不曾体验过生养的喜悦和痛苦，你究竟能告诉我些什么呢？” 十五年过去，龙应台不仅成为华文界最有影响力的一枝笔，也以她自己的智慧走出女性在个人事业和母亲角色的冲突，而这本书也给无数读者带来...(展开全部)', '龙应台 近年来常驻三个地址：香港沙湾径二十五号滨于海，台北仰德大道白云山庄藏于山，金华街月涵堂隐于市。写作教书兼成立基金会推动全球意识之余，最流连爱做之事，就是怀着相机走山走水走大街小巷，上一个人的摄影课。', 138, 28.00, '平装', '龙应台作品精选', '9787108033635 ', 12, 12);
INSERT INTO `books_list` VALUES (89, '我口袋里的星辰如沙砾', '韩松落', NULL, NULL, '新经典文化/北京十月文艺出版社', '韩松落 / 新经典文化/北京十月文艺出版社 / 2017-6 / 39.00元', '全书由50余篇散文组成，按时间脉络分为五个部分，描述了一个小城青年的成长历程。从昆仑山下的绿洲，到祁连山下的小城；从青翠的少年，到沧桑的中年；从最初的狂喜，到经历病痛、死亡、离别、内心的磨砺，直到最后在平静的生活中找到欣悦……这些不能承受的生命之重、生命之轻，他每每轻轻带过，却在人们心中掀起惊涛骇浪和无尽想象。 正如韩松落所说，写得越多，隐藏得也越多。其实是，隐藏得越多，袒露得也越多。本书是一部心灵突围史，在书中，我们也能看到自己的影子，和作者同悲同喜，那些困顿、悲伤、欣喜、茫然、突围、拯救，是我们每一个人的生命必经之途。', '韩松落，作家、影评人。1970年代生，祖籍湖南，新疆出生，生长于西北。著有《为了报仇看电影》系列、《窃美记》、《怒河春醒》、《我们的她们》、《老灵魂》等。华语电影传媒大奖、华语优质电影大奖评委，中国电影家协会理论评论委员会理事。《GQ》中文版2012年“年度专栏作家”。 “韩松落”是笔名，“松落”两字，出自蒲松龄《聊斋志异》自序：“松落落秋萤之火，魑魅争光；逐逐野马之尘，魍魉见笑。”', 292, 39.00, '平装', NULL, '9787530216866 ', 5, 5);
INSERT INTO `books_list` VALUES (90, '一个人的村庄', '刘亮程', NULL, NULL, '春风文艺出版社', '刘亮程 / 春风文艺出版社 / 2006-1-1 / 28.00元', '《一个人的村庄》讲述了刘亮程是真正的作家，也是真正的农民，是真正的农民作家。作为农民，写作真正是他业余的事情；而作为作家，他却无时不在创作，即使在他扛着一把铁锨在田间地头闲逛的时候。在文章里，刘亮程是一个农民，但是作为农民的他，是否意识到自己是个作家呢——或者说，在他的内心深处，是否也以作家自许呢？我不知道。我揣测，在他的村庄里，在与他一样日出而作日没而息的村民们的眼里，这个无事扛着铁锨闲逛，到处乱挖，常常不走正道却偏要走无人走过的草丛中的人一定是个难以捉摸、有些古怪的人吧。在他们眼里，这个说不出却总觉着有点不一样的人是不是有点神秘呢？当然，他们也许不知道这个人在跟他们一样的劳作之外，还喜欢偷偷观察着村里的人，以及驴，兔，飞鸟，蚂蚁，蚊子，以及风中的野草和落叶，甚至村东头以及村西头的阳光……', '刘亮程，作家，1962年出生在新疆古尔班通古特沙漠边缘的一个小村庄。著有诗集《晒晒黄沙梁的太阳》，散文集《风中的院门》、《一个人的村庄》、《库车》等。所获荣誉被誉为“20世纪中国最后一位散文家”和“乡村哲学家”。', 280, 28.00, '平装', NULL, '9787531329329 ', 10, 10);
INSERT INTO `books_list` VALUES (91, '此生多珍重 : 丰子恺散文漫画精选集', '', NULL, NULL, '天地出版社', '丰子恺 / 天地出版社 / 2017-7-6 / 49.80', '本书就是关于文艺大师丰子恺的经典散文的漫画本，将他的散文和漫画一并呈现给读者，会使读者大呼过瘾，而且本书是经其女儿丰一吟授权的版本，全面收录了《弘一大师》《送阿宝告别孩提时代》等40篇经典佳作，一本书可以阅尽丰子恺的散文精华。书中的漫画都是原版复制，典雅可爱，且采用特种纸印刷，色彩雅致，触感温润，可堪赏玩，体现了一种有趣、 有心、有情的人生哲学。在心情低谷时翻看，能给人以温暖；在处于顺境时闲读，又像一阵清风，能使我们头脑保持清醒。', '丰子恺，画家、散文家，卓有成就的文艺大师。师从弘一法师，学贯中西。朱自清、郁达夫、巴金、叶圣陶、林清玄，对其文章和漫画赞誉有加。日本汉学家吉川幸次郎则说，丰子恺是最中国的中国人。他的散文兼有平易纯朴之风、宽仁隽永之意和童真天然之趣，是现代文学中备受推崇的佳品，多次被选入学生教材，作语文教育典范。漫画幽默风趣，流传广泛，深受人们的喜爱，争相收藏。', 256, 49.80, '精装', NULL, '9787545528220 ', 12, 12);
INSERT INTO `books_list` VALUES (92, '生活，是很好玩的', '汪曾祺', NULL, NULL, '江西人民出版社', '汪曾祺 / 江西人民出版社 / 2016-11 / 52元', '内容简介： 本书是备受推崇的“生活家”汪曾祺的散文精选集。 生活家，就是热爱生活、多才多艺、有趣之人。 汪曾祺就是是一位生活家。 对生活心存热爱，从不消沉沮丧，无机心，少俗虑；活得有情趣，对世间万物皆有情，体察得细致。 写字、画画、做饭，明明是最平常普通的日常，他却深得其中的乐趣。一个平凡的景，经过汪曾祺的视角，便美得天真烂漫。 他的字到何处，何处便有画境。 “生活，是很好玩的”是他的一句名言，代表了他的人生态度。他以一颗赤忱之心，把一切都写活了。 本书分为有味、草木和春秋三辑，从美食、植物、旧事等三个部分来展现汪曾祺先生的生活情趣和人生态度。 编辑推荐 汪曾祺一生颠沛坎坷，却写出了今人所没有的慢与闲。 平常的一草一木，一茶一饭，因他而变得生动有趣。 他说：“我们有过各种创伤，但我们今天应该快活。” 他想让人觉得，生活是美好的，人是有诗意的。 他的文...(展开全部)', '汪曾祺，江苏高邮人，中国当代作家、散文家、戏剧家，京派作家的代表人物，师从于沈从文的文学大师，被誉为“抒情的人道主义者，中国最后一个纯粹的文人，中国最后一个士大夫”。', 376, 52.00, '精装', NULL, '9787210088004 ', 9, 9);
INSERT INTO `books_list` VALUES (93, '冬牧场', '李娟', NULL, NULL, '新星出版社', '李娟 / 新星出版社 / 2012-6 / 29.80元', '文坛清新之风，阿勒泰的精灵 李娟首部长篇纪实散文力作 四个月、零距离、全程记述哈萨克民族冬牧场的点点滴滴！ 春天接羔，夏天催膘，秋天配种，冬天孕育。羊的一生是牧人的一年，牧人的一生呢？这绵延千里的家园，这些大地最隐秘微小的褶皱，这每一处最狭小脆弱的栖身之地……青春啊，财富啊，爱情啊，希望啊，全都默默无声。 ——李娟 2010年冬天，李娟跟随一家熟识的哈萨克牧民深入阿勒泰南部的冬季牧场、沙漠，度过了一段艰辛迥异的荒野生活。李娟是第一位描写哈萨克民族冬牧生活的汉族作家，她以饱含深情又不失节制的文字，呈现出阿尔泰最后一批“荒野主人”冬季转场时的独特生存景观。', '散文作家，诗人。 1979年生于新疆。高中毕业后一度跟随家庭进入阿尔泰深山牧场，经营一家杂货店和裁缝铺，与逐水草而居的哈萨克牧民共同生活。1999年开始写作。出版有散文集《九篇雪》、《我的阿勒泰》、《阿勒泰的角落》、《走夜路请放声歌唱》，在读者中产生巨大回响，被誉为文坛清新之风，来自阿勒泰的精灵吟唱。', 320, 29.80, '平装', NULL, '9787513305662 ', 7, 7);
INSERT INTO `books_list` VALUES (94, '我的阿勒泰', '李娟', NULL, NULL, '云南人民出版社', '李娟 / 云南人民出版社 / 2010-7-1 / 22.00元', '本书是作者十年来散文创作的合集。分为阿勒泰文字、阿勒泰角落和九篇雪三辑。这是一部描写疆北阿勒泰地区生活和风情的原生态散文集。充满生机活泼、新鲜动人的元素。记录作者在疆北阿勒泰地区生活的点滴，包括人与事的记忆。作者在十年前以天才的触觉和笔调初现文坛并引起震惊。作品风格清新、明快，质地纯粹，原生态地再现了疆北风物，带着非常活泼的生机。', '李娟，一直在疆北阿勒泰地区陪伴母亲，随牧民在穷荒辽阔之地辗转迁徙，家庭以裁缝和小杂货店为生。后曾在阿勒泰和乌鲁木齐做过各种临时性的文字工作，现返回阿勒泰。在孤寂中迸发天才的力量，以浑然天成的笔触抒写生之爱恋。十年前，李娟在到处收集来的纸片上用密密麻麻的字写下她的生活、感想和领悟，投稿到新疆的文艺单位。一些资深的编辑认为一个二十岁左右的女孩不可能写出如此清新有才华的作品。但新疆著名作家刘亮程将她挖掘出来，她的第一部作品以《九篇雪》的名字结集出版，在文化界开始产生反响，人们开始知道有个新疆女孩叫李娟。此后，她的散文在《南方周末》和上海《文汇报》陆续刊登，完全是天才的笔触，陈村在他的菜园里曾着重讨论并推荐，知道并喜爱她的作品的人越来越多。所写的内容也让人耳目一新，很难想象一个没有受过很像样的教育、阅读范围主要是金庸、琼瑶、一直生活在漠北极荒之地的女孩能够写...(展开全部)', 260, 22.00, '平装', NULL, '9787222065413 ', 10, 10);
INSERT INTO `books_list` VALUES (95, '我与地坛 : 史铁生代表作', '史铁生', NULL, NULL, '春风文艺出版社', '史铁生 / 春风文艺出版社 / 2002-5 / 25.00元', '收有“午餐半小时”、“我的遥远的清平湾”、“命若琴弦”、“第一人称”、“两个故事”等15篇史铁生的代表作。', '史铁生，1951年生于北京。1967年初中毕业。1969年去陕北农村插队，三年后双腿瘫痪转回北京。回京后在一家街道工厂做临时工，七年后因病情加重回家疗养。1979年始有小说、散文、剧本等作品发表。主要作品有：短篇小说《我的遥远的清平湾》、《命若琴弦》、《老屋小记》；散文《我与地坛》、《好运设计》、《病隙碎笔》；中篇小说《关于詹牧师的报告文学》、《中篇1或短篇4》；长篇小说《务虚笔记》、《我的丁一之旅》等。', 399, 25.00, '平装', '新经典文库', '9787531324362 ', 6, 6);
INSERT INTO `books_list` VALUES (96, '没有色彩的多崎作和他的巡礼之年', '', '施小炜', '色彩を持たない多崎つくると、彼の巡礼の年', '南海出版公司', '[日] 村上春树 / 施小炜 / 南海出版公司 / 2013-10-1 / 39.50元', '“并不是一切都消失在了时间的长河里。那时，我们坚定地相信某种东西，拥有能坚定地相信某种东西的自我。这样的信念绝不会毫无意义地烟消云散”——十六年的彷徨迷惑，换来一场决然的巡礼之年，当最后一块拼图集齐，重回完满的正五边形，剩下最稀薄的人，重建大地。 人若真的受伤，通常会无法直视伤口，想隐 藏它忘却它，把心 门关起来。这是一个成长的故事。要成长，伤痛就得大一点，伤口就得深一点。——村上春树 ------------------- --------------------------------------------------------------------------------- 在名古屋上高中时，多崎作有四个非常要好的朋友，两男两女，他们的姓氏都带有色彩——“赤”、“青”、“白”、“黑”，唯独多崎作的姓名中没有色彩，也只有他离开名古屋到东京读...(展开全部)', '村上春树 日本著名作家。生于1949年。29岁开始写作，处女作《且听风吟》获日本群像新人奖。1987年出版的《挪威的森林》，日文版销量突破1000万册。2009年出版的《1Q84》被誉为“新千年日本文学的里程碑”。2013年4月，《没有色彩的多崎作和他的巡礼之年》面世，七天突破100万册，创日本文学史上的最快突破100万册的纪录。 写作之余，热衷翻译英语文学、跑步、爵士乐等。', 288, 39.50, '精装', '新经典文库·村上春树作品', '9787544268417 ', 20, 20);
INSERT INTO `books_list` VALUES (97, '1Q84 BOOK 2 : 7月～9月', '[日] 村上春树', NULL, NULL, '南海出版公司', '[日] 村上春树 / 施小炜 / 南海出版公司 / 2010-6 / 36.00元', '1Q84简体中文版官方网站：http://www.douban.com/minisite/1q84/ 今年夏天，属于1Q84年。 在1984年，他们连相遇的机会都没有，只能一面思念着对方，一面孤独地远去。 在1Q84年，他们却决定拯救彼此…… “不管喜欢还是不喜欢，目前我已经置身于这‘1Q84年’。我熟悉的那个1984年已经无影无踪，今年是1Q84年。空气变了，风景变了。我必须尽快适应这个带着问号的世界。像被放进陌生森林中的动物一样，为了生存下去，得尽快了解并顺应这里的规则。” 《1Q84》写一对十岁时相遇后便各奔东西的三十岁男女，相互寻觅对方的故事，并将这个简单故事变成复杂的长篇。我想将这个时代所有世态立体地写出，成为我独有的“综合小说”。超越纯文学这一类型，采取多种尝试。在当今时代的空气中嵌入人类的生命。——村上春树', '村上春树 著名作家。 1979年，《且听风吟》出版以来，寂寞忧郁的文字、清淡闲适的情节，尤其是独特新颖的都市感觉的写作文风，令万千读者如痴如醉。随着《挪威的森林》、《舞！舞！舞！》、《海边卡夫卡》等作品的陆续出版，逐渐赢得世界性的崇高声誉，最终形成“村上春树文学山系”。 2009年，集毕生文学之大成的巅峰杰作《1Q84》隆重出版，以席卷之势荣登日本所有畅销榜首，在整个日本、东亚乃至全球都引起巨大反响。正如媒体评论：“如此重大而复杂的题材，几可视为日本文学在新千年的伟大开篇。”', 352, 36.00, '精装', '新经典文库·村上春树作品', '9787544247252 ', 4, 4);
INSERT INTO `books_list` VALUES (98, '且听风吟', '[日] 村上春树', NULL, '風の歌を聴け', '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2001-8 / 11.80元', '《且听风吟》是村上春树的成名作，他是以这部中篇开始文学创作的。 小说的情节并不很复杂。“我”在酒吧喝酒，去卫生间时见一少女醉倒在地，遂就其护送回家，翌日少女发现自己一丝不挂，斥责“我”侮辱了她，“我”有口难辩。但几天后，两人逐渐亲密……不料“我”寒假回来，少女已无处可寻，只好一个人坐在原来两人坐过的地方怅怅地望着大海。', '村上春树（1949- ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 141, 11.80, '平装', '村上春树文集', '9787532726172 ', 19, 19);
INSERT INTO `books_list` VALUES (99, '大萝卜和难挑的鳄梨 : 村上Radio', '[日] 村上春树', NULL, 'おおきなかぶ、むずかしいアボカド　村上ラヂオ2', '南海出版公司', '(日)村上春树 / 施小炜 / 南海出版公司 / 2014-10 / 39.00', '《大萝卜和难挑的鳄梨》收录村上春树的52篇风趣随笔，配以画家大桥步的52幅美丽插画，村上以幽默的语言写下蔬菜的心情、关于大萝卜的怪谈、挑选好吃鳄梨的超能力、和海豹接吻的感觉……囊括了形形色色的有趣话题，被誉为“日本最好喝的乌龙茶”。 《大萝卜和难挑的鳄梨》中有最有趣、最真实的村上春树，平淡得恰到好处的笔触，描绘出耐人寻味的生命细节，让人发现在日常生活中忽视的有趣的东西，找回遗忘许久的自己。 ------------------------------------------------------------------------------------------------ ★《达文西》杂志年度散文第1名，著名画家大桥步精心绘制插图 ★52篇风趣随笔+ 52幅精美插画，被誉为“日本最好喝的乌龙茶” ★这是最真实、最有趣的村上春树，让人找回遗忘许...(展开全部)', '村上春树 日本著名作家。生于1949年。29岁开始写作，处女作《且听风吟》获日本群像新人奖。1987年出版的《挪威的森林》，日文版销量突破1000万册。2009年出版的《1Q84》被誉为“新千年日本文学的里程碑”。2013年4月，《没有色彩的多崎作和他的巡礼之年》面世，七天突破100万册，创日本文学史上的最快突破100万册的纪录。 写作之余，热衷翻译英语文学、跑步、爵士乐等。 大桥步 插画家。1940年生于日本三重县。 施小炜 翻译家、学者，旅日多年。译有《老师的提包》、《1Q84》、《当我谈跑步时，我谈些什么》、《天黑以后》、《没有色彩的多崎作和他的巡礼之年》等。', 213, 39.00, '平装', '新经典文库·村上春树作品', '9787544274050 ', 1, 1);
INSERT INTO `books_list` VALUES (100, '国境以南 太阳以西', '[日] 村上春树', NULL, NULL, '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2001-8 / 13.50元', '37岁的男主人公，在东京市区拥有两家兴旺的酒吧，还有娇美的妻子，可爱的女儿，他是一位真正的成功人士。但是，他的内心还是感到饥饿干渴，事业和家庭都填补不了，而让他那缺憾的部分充盈起来的，是他小学时的女友岛本。岛本不愿吐露自己的经历、身份、只希望他就这样接受眼前的自己，只把她当成小学时那个爱古典乐的女孩。然而，就在他接受了这不可能接受的条件时，两人却在箱根别墅度过了销魂的一夜。翌晨，她一去杳然、再无踪迹可寻了。', '村上春树（1949－ ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 190, 13.50, '平装', '村上春树文集', '9787532726745 ', 7, 7);
INSERT INTO `books_list` VALUES (101, '远方的鼓声', '[日] 村上春树', NULL, '遠い太鼓', '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2011-8-1 / 32.00元', '《远方的鼓声》是村上春树的游记，时间为1986-1989年，游历地区为欧洲，主要为希腊、意大利两个国家。“一天早上睁眼醒来，蓦然侧耳倾听，远处传来鼓声。鼓声从很远很远的地方、从很远很远的时间传来，微乎其微。听着听着，我无论如何都要踏上漫长的旅途”——作者听得的微乎其微的“远方的鼓声”，最终成了您手头上这部可触可观的《远方的鼓声》。村上的游记具有个人特色，他几乎不写人所熟知的名胜古迹，而是与普通居民共同生活，描写他们的日常工作、饮食起居等，以及他们的所思所想，富有深度感，对读者了解这些国家的真实状况有很大帮助，文笔也幽默有趣，可读性很强。', '', 381, 32.00, '精装', '村上春树随笔系列', '9787532754533 ', 11, 11);
INSERT INTO `books_list` VALUES (102, '舞！舞！舞！', '[日] 村上春树', NULL, NULL, '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2002-6 / 25.00元', '六具白骨摆列在一间亦真亦幻的死亡之屋里。“我”的老朋友“鼠”已经死于寻羊冒险。“我”好不容易找到了投缘的老同学五反田，可这位被演艺界包装得精神分裂的电影明星，却接连勒死了两名高级应召女郎，自己也驾着高级跑车“玛莎拉蒂”葬身大海。孤独的女孩“雪”和她孤傲的母亲“雨”，虽然还能在这疯狂世界上勉强生存，可“雨”的守护神笛克，却也躲不过一场莫名的车祸。度过了一段死亡陪伴的惊魂日子，“我”终于在宾馆女服务员由美吉那里找到了安全感，也有了在安静城市过安静生活的具体计划。可是，那第六具白骨到底意味着谁呢？“我”依然脱不出死亡之屋。 “高度发达的资本主义社会，”村上春树把这句话重复了不知多少遍，他是想寻找所有这些死亡和罪恶的源头吧？', '村上春树（1949- ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 502, 25.00, '平装', '村上春树文集', '9787532728893 ', 16, 16);
INSERT INTO `books_list` VALUES (103, '没有女人的男人们', '[日] 村上春树', '竺家荣 / 姜建强 / 岳远坤 / 陆求实 / 林少华 / 毛丹青', '女のいない男たち', '上海译文出版社', '[日] 村上春树 / 竺家荣、姜建强、岳远坤、陆求实、林少华、毛丹青 / 上海译文出版社 / 2015-2 / 35.00元', '1、《驾驶我的车》：最早发表于《文艺春秋》（2013年12月号）。舞台剧演员家福（60岁左右）明明知道同为演员的妻子和同剧男演员定期幽会，却假装不知，在世人面前扮演着恩爱夫妻。妻子去世后，他雇佣了一位女性司机渡利。相处中，他开始逐渐和渡利交流自己的内心世界，追问是否去世的妻子非和“那个男人”保持关系不可。 2、《昨天》（披头士的一首歌曲的名字）：最早发表于《文艺春秋》（2014年1月号）。木樽和我是同年级，他出身于东京都大田区田园调布，却能说得一口字正腔圆的关西腔，我们俩在打工时相识，从此开始了一场奇妙的“文化交流”。木樽有个小学起就青梅竹马的女友，却只因自己没有考上大学而始终无法将这段深沉真挚的感情更进一步。16年后，两人天各一方，各自孤单，走不到一起，又彼此无法忘怀。 3、《独立器官》：最早发表于《文艺春秋》（2014年3月号）。我的朋友美容整形...(展开全部)', '村上春树，日本现代小说家，生于京都伏见区。毕业于早稻田大学第一文学部演剧科，亦擅长美国文学的翻译，29岁开始写作，第一部作品《且听风吟》即获得日本群像新人奖，1987年第五部长篇小说《挪威的森林》上市至2010年在日本畅销一千万册，国内简体版到2004年销售总量786万，引起“村上现象” 其作品风格深受欧美作家的影响，基调轻盈，少有日本战后阴郁沉重的文字气息，被称作第一个纯正的“二战后时期作家”，并被誉为日本80年代的文学旗手。', 258, 35.00, '平装', NULL, '9787532768776 ', 6, 6);
INSERT INTO `books_list` VALUES (104, '无比芜杂的心绪 : 村上春树杂文集', '[日] 村上春树', NULL, NULL, '南海出版公司', '村上春树 / 施小炜 / 南海出版公司 / 2013-4 / 39.50元', '★村上春树首次明确对世界表达看法 ★出道三十五年最重要随笔集 ★亲自遴选未曾收录的作品、未曾发表的文章 ★收入耶路撒冷著名演讲《高墙与鸡蛋》 ★世上的人终其一生，都在寻求某个宝贵的东西，然而能找到的人不多。即使幸运地找到了，那东西也大多受到致命的损伤。但是，我们必须继续寻求。 村上春树自选三十五年来的精彩随笔结集而成，都是未以单行本发表过的文字，并在每篇前附短文记述写作时的心绪。 《无比芜杂的心绪》是村上创作生涯最具分量的随笔集，被誉为“完整了解村上春树文学与内心的必读之书”。', '村上春树 日本作家。生于1949年。29岁开始写作，处女作《且听风吟》获日本群像新人奖，1987年问世的《挪威的森林》日文版销量已突破1000万册，2009年出版的《1Q84》创上市12天销售100万册的空前纪录，被誉为日本文学新千年的伟大开篇。写作之余，热衷翻译英语文学、跑步、爵士乐等。 施小炜 翻译家、学者。译有《1Q84》、《天黑以后》、《当我谈跑步时，我谈些什么》、《老师的提包》等', 344, 39.50, '精装', '新经典文库·村上春树作品', '9787544264839 ', 20, 20);
INSERT INTO `books_list` VALUES (105, '1Q84 BOOK 3 : 10月～12月', '[日] 村上春树', NULL, NULL, '南海出版公司', '[日] 村上春树 / 施小炜 / 南海出版公司 / 2011-1 / 39.50元', '1Q84简体中文版官方网站：http://www.douban.com/minisite/1q84/ “不管喜欢还是不喜欢，目前我已经置身于这‘1Q84年’。我熟悉的那个1984年已经无影无踪，今年是1Q84年。空气变了，风景变了。我必须尽快适应这个带着问号的世界。像被 放进陌生 森林中的动物一样，为了生存下去，得尽快了解并顺应这里的规则。” 《1Q84》写一对十岁时相遇后便各奔东西的三十岁男女，相互寻觅对方的故事，并将这个简单故事变成复杂的长篇。我想将这个时代所有世态立体地写出，成为我独有的“综合小说”。超越纯文学这一类型，采取多种尝试。在当今时代的空气中嵌入人类的生命。——村上春树', '村上春树 著名作家。 1979年，《且听风吟》出版以来，寂寞忧郁的文字、清淡闲适的情节，尤其是独特新颖的都市感觉的写作文风，令万千读者如痴如醉。随着《挪威的森林》、《舞！舞！舞！》、《海边卡夫卡》等作品的陆续出版，逐渐赢得世界性的崇高声誉，最终形成“村上春树文学山系”。 2009年，集毕生文学之大成的巅峰杰作《1Q84》隆重出版，以席卷之势荣登日本所有畅销榜首，在整个日本、东亚乃至全球都引起巨大反响。正如媒体评论：“如此重大而复杂的题材，几可视为日本文学在新千年的伟大开篇。”', 416, 39.50, '精装', '新经典文库·村上春树作品', '9787544249867 ', 20, 20);
INSERT INTO `books_list` VALUES (106, '奇鸟行状录', '[日] 村上春树', NULL, NULL, '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2002-11 / 30.00元', '《奇鸟行状录》是村上春树篇幅最长的小说三部曲。 失业者冈田亨的妻子久美子在其兄长、众议员，黑暗势力代表人物绵谷升的胁迫下失踪了。冈田身边来了许多怪人：女巫师、“意识娼妇”、失手使男友车祸死亡的少女、旧军人等。同时也发生了许多怪事。冈田到一口深井里冥思苦想后，出来在奇怪的母子“肉豆蔻”、“肉桂”的帮助下向绵谷升挑战，在虚幻中将其击伤，久美子又在现实中将其杀死。作品色彩诡异，规模宏大，虚实交叉，被称为当代的“一千零一夜”。', '村上春树（1949- ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 682, 30.00, '平装', '村上春树文集', '9787532729531 ', 2, 2);
INSERT INTO `books_list` VALUES (107, '爱吃沙拉的狮子 : 村上Radio', '', '施小炜', 'サラダ好きのライオン', '南海出版公司', '[日] 村上春树、[日]大桥步 图 / 施小炜 / 南海出版公司 / 2015-6-1 / 39.00元', '村上春树的日常生活是什么样子？每天在孤独之中默默埋头写作？NO~！！ 他爱爵士乐、威士忌，但同样爱旅行，爱猫咪，爱恶作剧，爱奇思妙想，过着平凡的生活，坐坐公共汽车，漫无目的地散散步，在商店买买萝卜和大葱。 村上春树在《爱吃沙拉的狮子》中写下这些温馨的细节，52则随笔流露出难能可贵的趣味和性情，配以大桥步52幅可爱插画，温暖又自由自在，读起来像在屋檐下抱着猫看天，治愈琐碎生活的烦恼忧愁。 如果说《大萝卜和难挑的鳄梨》是最美味的乌龙茶，《爱吃沙拉的狮子》就是最美味的寿司。寿司配乌龙茶，炎炎夏日给你带来最清爽纯粹的欢乐。', '52篇连载随笔+52幅有趣的插画=村上春树最有趣的随笔集。 男人做西式煎蛋卷时，什么风景最搭？你们家的猫，挑不挑音乐？村上春树算命灵吗？椰子树为什么要长得那么高？饶有趣味的阅读体验，最令人肩膀放松的随笔结集，村上春树珍藏的趣事感悟一一精彩呈现。 比《大萝卜和难挑的鳄梨》更有趣，让你笑口常开，感动常在。 ★《大萝卜和难挑的鳄梨》姊妹篇 ★这是最真实的村上春树:温馨的细节，带你体味平淡生活的惊喜 ★村上春树最有趣的随笔集+著名画家大桥步精致插图彩色印刷 ★我的随笔就像寿司店里的寿司。“那家店里的葫芦条寿司、酱菜卷很好吃啊。”就是这种感觉吧。我家的寿司非常美味，材料都很新鲜，我对此很有自信。——村上春树', 215, 39.00, '精装', '新经典文库·村上春树作品', '9787544277549 ', 11, 11);
INSERT INTO `books_list` VALUES (108, '东京奇谭集', '[日] 村上春树', NULL, '東京奇譚集', '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2006-7 / 13.00元', '《东京奇谭集》收有五部短篇小说，分别为《偶然的旅人》、《哈纳莱伊湾》、《在可能找见的地方，无论哪里》、《天天移动的肾脏石块》和《品川猴》。五篇奇谭中最奇的是最后一篇《品川猴》。一个叫安藤瑞纪的年轻女子得了一种“忘名症”，每星期有一两次想不起自己的名字，几经周折，查明“忘名症”起因于一只猴……虽说是“奇谭”，但村上春树在小说中讨论的仍然是形而上的人生问题。 谭通谈，奇谭即奇谈、奇闻之意。众所周知，村上小说的篇名大多声东击西，避实就虚，而这部短篇集却表里如一，果然是发生在东京的奇谭。小说通过偶然性突出了人生命运的神秘感。 在这部短篇集里，村上春树一如既往，依然在不动声色地拆除着现实与非现实或此岸世界与彼岸世界之间的篱笆，依然像鹰一样在潜意识王国上空盘旋着，寻找更深更暗的底层，依然力图从庸常的世俗生活中剥离出灵魂信息和人性机微。', '村上春树（1949－ ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 148, 13.00, '平装', '村上春树文集', '9787532740536 ', 5, 5);
INSERT INTO `books_list` VALUES (109, '遇到百分之百的女孩', '[日] 村上春树', NULL, NULL, '上海译文出版社', '[日] 村上春树 / 林少华 / 上海译文出版社 / 2002-12 / 13.50元', '《遇到百分之百的女孩》为作者1981-1983年间，为一本杂志写的18篇连载小说。除一篇外，均是一二千字的超短篇小说。各篇内容不一，有写遇到“百分之百的女孩”后的遐想，有写半夜值班的惊魂故事，有写穷人搬家的苦恼等。这些作品有的惊险，有的滑稽，有的深沉，有的故事完整，有的注重感想，像一篇速写。其总的风格是有趣好玩又富有回味，是一本可读性很强的休闲读物。', '村上春树（1949－ ），日本小说家。曾在早稻田大学文学部戏剧科就读。1979年，他的第一部小说《听风之歌》问世后，即被搬上了银幕。随后，他的优秀作品《1973年的弹子球》、《寻羊冒险记》、《挪威的森林》等相继发表。他的创作不受传统拘束，构思新奇，行文潇洒自在，而又不流于庸俗浅薄。尤其是在刻画人的孤独无奈方面更有特色，他没有把这种情绪写成负的东西，而是通过内心的心智性操作使之升华为一种优雅的格调，一种乐在其中的境界，以此来为读者，尤其是生活在城市里的人们提供了一种生活模式或生命的体验。', 158, 13.50, '平装', '村上春树文集', '9787532730131 ', 14, 14);
INSERT INTO `books_list` VALUES (110, '一只狼在放哨 : 阿巴斯诗集', '[伊朗] 阿巴斯·基阿鲁斯达米', NULL, NULL, '中信出版集团', '[伊朗] 阿巴斯·基阿鲁斯达米 / 黄灿然 / 中信出版集团 / 2017-7 / 39.00', '阿巴斯不仅是一位备受瞩目电影导演，他的电影被称为“诗意电影”，因为阿巴斯的另外一个身份是一位风格独特的诗人。事实上，他的诗歌写作比其他的艺术行为在时间上要早得多，并受到了广泛的关注。这本诗集收录了阿巴斯的500余首诗歌，其诗作传统来自于古老的波斯诗歌传统，特点上呈现出哲 学和冥思的特征。他的诗歌可以说是短小精悍的典型，阿巴斯从不写冗长的诗歌，每一首诗歌也没有一个确定的标题。但是始终充满了拥有独特观察之眼的诗人对于世界特有的专注凝视和细致观察。他的那些简短、迅疾、跳跃但目光细致的诗句，让我们重新审视身边的事物和景象，领悟日常世界的诗意本质。', '阿巴斯·基阿鲁斯达米（Abbas Kiarostami，1940-2016），出生于德黑兰，导演、剧作家、制作人、剪辑师。 1989年，凭借《何处是我朋友家》获洛加诺国际电影节金豹奖。1997年，阿巴斯凭借《樱桃的滋味》赢得戛纳电影节金棕榈奖。1999年，《随风而逝》获 威尼斯电影节评委会大奖。2013年，《如沐爱河》获亚洲电影节最佳导演奖。在中国，阿巴斯·基阿鲁斯达米是深受影迷喜爱的导演，而在伊朗，阿巴斯·基阿鲁斯达米不仅仅是一位电影人，也是一位诗人，被誉为“他那一代，或者那个世纪中最激进的伊朗诗人”。', 224, 39.00, '平装', NULL, '9787508675893 ', 12, 12);
INSERT INTO `books_list` VALUES (111, '鲍勃·迪伦诗歌集 (1961-2012)', '[美] 鲍勃·迪伦', '西川 / 陈黎 / 奚密 / 李皖 / 冷霜 / 陈震 / 曹疏影 / 马世芳 / 胡续冬 / 包慧怡 / 罗池 / 胡桑 / 周公度 / 厄土 / 张芬龄', NULL, '广西师范大学出版社', '[美] 鲍勃·迪伦 / 西川、陈黎、奚密、李皖、冷霜、陈震、曹疏影、马世芳、胡续冬、包慧怡、罗池、胡桑、周公度、厄土、张芬龄 / 广西师范大学出版社 / 2017-6 / 198.00元', '▼2016年诺贝尔文学奖作品，囊括迪伦逾半世纪创作经典 迪伦与布莱克、兰波、惠特曼和莎士比亚比肩而立。 他不是为了歌唱永恒，而是叙说我们周遭发生的事物，如同德尔斐的神谕在播报晚间新闻。 他的作品之美就是最高的等级，改变了我们关于“诗歌是什么”以及“诗歌何为”的观念。 ——诺贝尔文学奖授奖词 ▼战后铁器时代的游吟诗人，首次将歌词带入经典文学殿堂 鲍勃·迪伦在伟大的美国歌曲传统范畴内，创造了全新的诗意表达方式。 ——诺贝尔文学奖颁奖词 尽管这个结果可能让人惊奇，但要是你回望过去的5000年，你会发现诗人荷马和萨福。 他们写下的充满诗意的篇章，其本意就是用来表演的，鲍勃·迪伦就是如此。 ——瑞典学院常务秘书长 莎拉·丹纽尔 ▼百万张脸孔唱过的歌，撼动一个时代的灵魂之声 那些在风中飘荡的，随暴雨而至的，在红色天空下的 致纽约的，致青春的，致战争的，致爱人的 ...(展开全部)', '鲍勃?迪伦（Bob Dylan） 美国音乐家、诗人。半个世纪来，鲍勃?迪伦发行了超过四十张专辑，创作出传唱数代人的经典歌曲，影响了遍布全球的音乐人，并首次将歌词带入经典文学殿堂。曾获格莱美终身成就奖（1991）、奥斯卡最佳原创歌曲奖（2000）、金球奖最佳原创歌曲奖（2001）、普利策奖特别荣誉奖（2008）。2016年获颁诺贝尔文学奖，以表彰其“在伟大的美国歌曲传统中开创了新的诗性表达”。', 2040, 198.00, '充气', '新民说 鲍勃·迪伦诗歌集', '9787549597048 ', 17, 17);
INSERT INTO `books_list` VALUES (112, '万物静默如谜 : 辛波斯卡诗选', '[波兰] 维斯瓦娃·辛波斯卡', '陈黎 / 张芬龄', NULL, '浦睿文化·湖南文艺出版社', '[波] 维斯拉瓦?辛波斯卡 / 陈黎、张芬龄 / 浦睿文化·湖南文艺出版社 / 2012-8 / 26.00元', '《辛波斯卡诗选：万物静默如谜》收录辛波斯卡各阶段名作75首，包括激发知名绘本作家幾米创作出《向左走，向右走》的《一见钟情》，收录高中语文教材的《底片》，网上广为流传的《在一颗小星星下》《种种可能》等，曾获得《洛杉矶时报》年度最佳图书。 辛波斯卡的诗取材于日常生活的事物和经验，甲虫、海参、石头、沙粒、天空；安眠药、履历表、衣服；电影、画作、剧场、梦境等等，在她的笔下无不焕发出新的诗意，让人们重新认识生活中常见的事物。', '维斯拉瓦·辛波斯卡（Wislawa Szymborska），1923-2012，一九二三年生于波兰小镇布宁。她擅长以幽默、诗意的口吻描述严肃主题和日常事物，以诗歌回答生活。是波兰最受欢迎的诗人，也是公认为当代最为迷人的诗人之一，享有“诗界莫扎特”的美誉。一九九六年获得诺贝尔文学奖，是文学史上第三位获奖女诗人。二O一二年二月的一个晚上，辛波斯卡在住宅里安然去世。在她的葬礼上，波兰各地的人们纷纷赶来，向诗人作最后的告别。 译者简介： 陈黎，台湾著名诗人、翻译家。著有诗集、散文集、音乐评论集二十余种，曾获时报文学奖、联合报文学奖、梁实秋文学奖翻译奖，金鼎奖等众多大奖。 张芬龄，台湾师大英语系毕业。著有评论集《现代诗启示录》，与陈黎合译书十余种。曾多次获得梁实秋文学奖翻译奖。', 201, 26.00, '平装', '辛波斯卡诗选', '9787540457228 ', 11, 11);
INSERT INTO `books_list` VALUES (113, '我的孤独是一座花园 : 阿多尼斯诗选', '[叙利亚] 阿多尼斯', '薛庆国', NULL, '译林出版社', '[叙利亚] 阿多尼斯 / 薛庆国 / 译林出版社 / 2009-3 / 28.00', '《我的孤独是一座花园》是阿多尼斯作品的首个中译版，收录了诗人从上世纪50年代“最初的诗”到2008年的最新作品，时间跨越半个世纪。', '阿多尼斯，原名阿里·艾哈迈德·赛义德·伊斯伯尔，1930年生于叙利亚拉塔基亚省，1956年移居黎巴嫩，开始文学生涯。1980年代起长期在欧美讲学、写作，现定居巴黎。 阿多尼斯是作品等身的诗人、思想家、文学理论家，是当代最杰出的阿拉伯诗人，在世界诗坛享有盛誉。他对诗歌现代化的积极倡导、对阿拉伯文化的深刻反思，都在阿拉伯文化界引发争议，并产生广泛影响。迄今共出版22部诗集，并著有文化、文学论著十余部，还有一些译著。他曾荣获布鲁塞尔文学奖、土耳其希克梅特文学奖、马其顿金冠诗歌奖、法国的让·马里奥外国文学奖和马克斯·雅各布外国图书奖、意大利的诺尼诺诗歌奖和格林扎纳·卡佛文学奖等国际大奖。近年来，阿多尼斯一直是诺贝尔文学奖的热门人选。', 282, 28.00, '精装', '20世纪经典', '9787544708531 ', 2, 2);
INSERT INTO `books_list` VALUES (114, '海子诗全集', '海子 / 编者 西川', NULL, NULL, '作家出版社', '海子 著、西川 编 / 作家出版社 / 2009-3 / 88.00元', '《海子诗全集》收录迄今为止所有发现的海子文学作品。全书以上海三联书店1997年出版的《海子诗全编》为底本，依照时间与类别结合的方式，收入海子的抒情短诗、长诗诗剧、诗学提纲、日记小说等各种体裁作品。以“补遗”的方式增加了当时没有收入的海子早期油印诗集《小站》、《麦地之瓮》里面的作品，以及近年陆续发现的散佚作品。 其中最大的发现，在于颂体诗《生日颂》与散文小说《村庄》。《生日颂》是海子给友人孙理波及同时代人的献诗，长达一百二十余行，近两千字。围绕“生日”这一主题，海子阐述了其人生理念和诗歌理想。《村庄》则是四个诗体散文小说组成的主题小说，其中的《谷仓》一篇以近乎注解的方式，清晰化了海子后期诗作，尤其是长诗中经常出现的一些意象的意思，堪称理解海子诗歌理想中“道家思想”的钥匙。 全集第一次披露了一些海子文字以外的作品，这就是海子自己为“太阳”系列诗作所做的插...(展开全部)', '海子，原名查海生，1964年生于安徽省怀宁县高河查湾，在农村长大。1979年考入北京大学法律系，1983年毕业后被分配至北京中国政法大学哲学教研室工作，1989年3月26日在河北省山海关附近卧轨自杀。在不到7年的时间里，海子创作了大量的文学作品。他说：“我的诗歌理想是在中国成就一种伟大的集体的诗。我不想成为一名抒情诗人，或一位戏剧诗人，甚至不想成为一名史诗诗人，我只想融合中国的行动，成就一种民族和人类的结合，诗和真理合一的大诗。”', 1171, 88.00, '精装', NULL, '9787506346207 ', 17, 17);
INSERT INTO `books_list` VALUES (115, '事物的味道，我尝得太早了 : 石川啄木诗歌集', '[日] 石川啄木', NULL, NULL, '上海人民出版社', '[日] 石川啄木 / 周作人 / 上海人民出版社 / 2016-5-1 / 45.00元', '全书收录了石川啄木的四 部小集子。《一握砂》包含551首短歌，《可悲的玩具》包含194首短歌，这两部歌集诚恳地记录了诗人在贫病生活中的哀思和叹息。《叫子和口哨》为石川啄木创作的6首现代诗，着重在对现实世界的思考。《可以吃的诗》则是诗人针对诗歌创作而写的一篇自白文章，简述了自己的创作之路，剖析自己的心灵。', '石川啄木（1886—1912），本名石川一，日本明治时代诗人、小说家与评论家。他打破了日本短歌三十一音一行的定例，开创了短歌新形式，为日本诗歌的发展作出了重要贡献。石川啄木一生贫病，死时年二十六岁。 周作人（1885—1967），浙江绍兴人，中国现代著名散文家、翻译家、思想家。一生著译传世约1100万字，其中翻译作品居一半有余。', 250, 45.00, '精装', '周作人经典日文译作系列', '9787208136991 ', 18, 18);
INSERT INTO `books_list` VALUES (116, '奥克诺斯', '[西] 路易斯·塞尔努达', NULL, 'Ocnos', '人民文学出版社', '[西班牙] 路易斯·塞尔努达 / 汪天艾 / 人民文学出版社 / 2015-1 / 29.00元', '《奥克诺斯》是西班牙诗人塞尔努达的散文诗集。流亡国外的诗人追忆童年、秋日和故乡小城，在文字中重构一个透明的世界，达成自己对生命的理解、对永恒的渴望。 在西班牙诗坛，塞尔努达的影响足以媲美安东尼奥·马查多和希梅内斯；而本书在《理想藏书》的西班牙文学书单中名列第二位。 “ 西班牙语诗人中最伟大的一个，也是最神秘、最不为人知的一个。” ——《理想藏书》 “诗歌艺术的圣人。” ——哈罗德? 布鲁姆 如果你的母语是西班牙语，那么我向你推荐安东尼奥?马查多、洛尔迦、塞尔努达…… ——布罗茨基 很少有这样的现代诗人，无论何种语言，能给我们带来这样不寒而栗的体验，当我们知道自己面对的是一个说出真理的人。 ——奥克塔维奥?帕斯', '作者 路易斯·塞尔努达 Luis Cernuda，1902—1963 西班牙诗人，出生于塞维利亚。他是“二七一代”的代表诗人，1938 年因西班牙内战开始流亡，此后二十五年辗转英、美、墨西哥直至去世，终其一生未再回国。 他的创作生涯是对欧洲诗歌财富的缓慢继承，风格先后受到法国 超现实主义、荷尔德林以及十九世纪英国诗歌的浸染，堪称西班牙诗坛的“欧洲诗人”。 译者 汪天艾，西班牙马德里自治大学文哲系博士候选人，研究方向二十世纪西班牙诗歌，尤以塞尔努达和战后“五零年代”诗人为专攻。此前先后毕业于北京大学西葡语系和英国伦敦大学国王学院比较文学系。译诗撰文散见报章刊物，兼有《文艺报》月专栏“伊比利亚诗笺”译介评论二十世纪西班牙诗坛。', 162, 29.00, 'Hardcover', NULL, '9787020107018 ', 20, 20);
INSERT INTO `books_list` VALUES (117, '唯有孤独恒常如新 : 伊丽莎白·毕肖普诗选', '[美] 伊丽莎白·毕晓普', NULL, NULL, '湖南文艺出版社', '(美) 伊丽莎白·毕肖普 / 包慧怡 / 湖南文艺出版社 / 2015-3 / 39.00', '毕肖普的写作技艺精湛、形式完美，从专业角度看炉火纯青，让人叹为观止。 ——谢默斯·希尼（1995年诺贝尔文学奖得主） 伊丽莎白·毕肖普， 这位一生都在流浪和漫游中度过的诗人，诗作很少，却被称之为“诗人中的诗人”“狄金森之后美国最伟大的女诗人”，囊括了美国国家图书奖、普利 策奖、纽斯塔国际文学奖等众多大奖，深受布罗茨基、谢默斯·希尼、帕斯等诺贝尔文学奖获得者推崇。 《唯有孤独恒常如新》收录了毕肖普最为经典的诗歌，包括《失眠》《一种艺术》《旅行的问题》《致纽约》等脍炙人口的名篇。选译于Farrar，Straus and Giroux出版社为纪念毕肖普诞辰100周年出版的两卷本《毕肖普诗歌散文全集》之《诗歌卷》,被《波士顿邮报》评为年度最佳诗集，也是迄今最权威的毕肖普诗歌选本。 特别收录仅以手稿形式存世的少作集《埃德加·艾伦·坡与自动点唱机》，并附录15篇...(展开全部)', '伊丽莎白·毕肖普（Elizabeth Bishop） 继艾米莉·狄金森之后美国20世纪最重要、最有影响力的女诗人之一。 1911年生于美国马萨诸塞州。童年时，父亲去世，母亲进了精神病院，辗转被外祖父及叔伯抚养长大。奇特而悲伤的成长经历，让她的一生都在流浪和漫游中度过。不 仅曾在美国各地定居，还去过法国、墨西哥、巴西等地，直至晚年才应哈佛大学之邀，回美国任教。 1946年发表诗集《北与南》，一举成名。生前就被誉为“诗人中的诗人”。发表的诗作不多，但几乎囊括了美国所有重要的奖项，包括普利策奖、美国国家图书奖、古根海姆奖、美国国家书评奖等。1969年出版《诗全集》，奠定其杰出诗人的地位。1979年逝世，享年68岁。 【译者简介】 包慧怡，诗人，译者。复旦大学英语系学士、硕士，都柏林大学中古英语文学博士，1985年生于上海，现居爱尔兰。出版译著《好骨头》《隐...(展开全部)', 312, 39.00, '平装', NULL, '9787540470265 ', 5, 5);
INSERT INTO `books_list` VALUES (118, '博尔赫斯诗选', '[阿根廷] 豪·路·博尔赫斯', '陈东飚 / 陈子弘等', NULL, '河北教育出版社', '（阿根廷）博尔赫斯 / 陈东飚、陈子弘等 / 河北教育出版社 / 2003-1 / 19.0', '突然间黄昏变得明亮 因为此刻正有细雨在落下。 或曾经落下。下雨 无疑是在过去发生的一件事。 谁听见雨落下，谁就回想起 那个时候，幸福的命运向他呈现了 一朵叫做玫瑰的花 和它奇妙的，鲜红的色彩。 这蒙住了窗玻璃的细雨 必将在被遗弃的郊外 在某个不复存在的庭院里洗亮 架上的黑葡萄。潮湿的暮色 带给我一个声音，我渴望的声音， 我的父亲回来了，他没有死去。', '博尔赫斯,阿根廷诗人、小说家兼翻译家。生于布宜诺斯艾利斯一个有英国血统的律师家庭。在日内瓦上中学，在剑桥读大学。精通英、法、德等多国文字。中学时代开始写诗。1923年出版第一部诗集，1935年出版第一本短篇小说集，从此奠定了在阿要廷文坛上的地位。1950年至1953年间任阿根廷作家协会主席。1955年任国立图书馆馆长、布宜诺斯艾利斯大学哲学文学系教授。1986年4月24日与玛丽亚·儿玉结婚，6月14日在日内瓦去世。 他喜欢的主题有：追求不可能实现的事物，讽刺性地实现人类的梦想，理想主义哲学的各种含义，存在之混乱和无益，时间的周而复始，以及理性的失败。据他本人所言，博尔赫斯的成败将取决于他的诗歌。他一生所获得的最重要的奖项可能是福门托奖，这业已并将永远成为人们批评甚至或抨击瑞典文学院的一个有力证据。', 306, 19.00, '平装', '20世纪世界诗歌译丛', '9787543448377 ', 4, 4);
INSERT INTO `books_list` VALUES (119, '现实与欲望 : 塞尔努达流亡前诗全集（1924—1938）', '[西] 路易斯·塞尔努达', NULL, 'LA REALIDAD Y EL DESEO', '四川文艺出版社', '[西班牙] 路易斯·塞尔努达 / 汪天艾 / 四川文艺出版社 / 2016-1 / 58.00', '塞尔努达的作品是一条通向我们自己的路。……很少有这样的现代诗人，无论何种语言，能给我们带来这样不寒而栗的体验，当我们知道自己面对的是一个说出真理的人。他击中了我们每个个体的内心，那是属于我们自己的真理。—— 奥克塔维奥·帕斯 ※ 自上世纪20年代起，塞尔努达的诗歌作品漫步涉足二十世纪欧洲和西班牙诗歌几乎所有的风格、音调和抒情领域：一方面，是纯诗、超现实主义、新浪漫主义和反抒情主义（或可称为无韵主义）；另一方面，是歌诗传统、哀歌、颂歌、牧歌、戏剧诗歌、叙事诗歌、冥思短诗和亚历山大体律诗（或可称为文化主义诗歌）。这种创作风格上的多元化与诗人的生命轨迹完全相应，使塞尔努达在“二七年代”众多伟大诗人中独树一帜、被西班牙尤其是1960年后涌现的诗人尊崇为典范的根源。他屹立于传记记录与形而上冥思的交汇点上，每个个体的体验与一个至高的同一体验在他的诗里相遇。——何...(展开全部)', '路易斯·塞尔努达（Luis Cernuda），西班牙著名诗人，“二七年代”代表之一。1902年出生于塞维利亚，1938 年因西班牙内战开始流亡，此后二十五年辗转英、美、墨西哥直至去世，终其一生未再回国。 他的创作生涯是对欧洲诗歌财富的缓慢继承，风格先后受到法国超现实主义、荷尔德林以及十九世纪以降英国诗歌的浸染，堪称西班牙诗坛的“欧洲诗人”，被西语世界二十世纪下半叶的数代诗人奉为经典和偶像。 ※ 关于译者 汪天艾，西班牙马德里自治大学文哲系博士在读，研究方向二十世纪西班牙诗歌，尤以塞尔努达和战后“五〇年代”诗人为专攻。此前先后毕业于北京大学西葡语系和英国伦敦大学国王学院比较文学系。译著有塞尔努达散文诗集《奥克诺斯》（人民文学出版社，2015）等。', 365, 58.00, '精装', '副本译丛', '9787541142062 ', 3, 3);

